    color: #e1dee3;
    font-family: Open Sans,sans-serif;
    font-size: 95%;
    font-weight: 400!important;
    line-height: normal;
}
.courses .coursebox {
    background-color: #868484!important;
    padding: 4px;
    border: 10px #7a5b7a;
}
.page-link:not(:disabled):not(.disabled) {
    cursor: pointer;
    background: #2a2a2a;
    border: 2px;
}
.form-inline input.form-control[type=text] {
    min-width: 120px;
    height: 32px;
    background: #101010;
    border: 2px;
}
.btn:not(:disabled):not(.disabled), #page-grade-grading-manage .actions .action:not(:disabled):not(.disabled), #rubric-rubric.gradingform_rubric #rubric-criteria .criterion .addlevel input:not(:disabled):not(.disabled), #rubric-rubric.gradingform_rubric .addcriterion:not(:disabled):not(.disabled) {
    cursor: pointer;
    background: #4e6369;
    border: 1px;
}
.custom-select {
    word-wrap: normal;
    background: #101010;
    border:1px;
}
.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #51666c;
    -webkit-background-clip: border-box;
    background-clip: border-box;
    border: 1px solid rgba(0,0,0,.125);
}
.bg-white {
    background-color: #51666c!important;
}
.pb-3, .py-3 {
    padding-bottom: 1rem!important;
    background: #5992af;
}
.message-drawer {
    -webkit-box-shadow: -2px 2px 4px rgba(0,0,0,.08);
    box-shadow: -2px 2px 4px rgba(0,0,0,.08);
}
.message-drawer .body-container>* {
    position: absolute;
    right: 0;
    left: 0;
    top: 0;
    bottom: 0;
    background: #2a2a2a;
}
a, a:visited, .tabtree .tabrow0 li a, #adaptable-page-header-wrapper .dropdown-menu.usermen>li>a, #adaptable-page-header-wrapper .dropdown-menu>li>a.dropdown-item, #adaptable-page-header-wrapper .dropdown-item a {
    color: #ffffff;
    text-decoration: none;
}
.message-drawer .list-group .list-group-item:last-child {
    border-bottom: 0;
    background: #2a2a2a;
}
h1, h2, h3, h4, h5, h6 {
    margin: 0 0 10px;
    color: #fff;
    font-family: Roboto,sans-serif;
    font-weight: 400!important;
}
.bg-light {
    background-color: #212121!important;
}
.input-group>.form-control, .input-group>.custom-select, .input-group>.custom-file {
    position: relative;
    flex: 1 1 auto;
    width: 1%;
    margin-bottom: 0;
    background: #51666c;
    border: 2px #51666c;
}
.bg-white {
    background-color: #51666c!important;
    border: 2px #51666c;
}
#page-footer, #page-footer p {
    background-color: #2a2a2a;
    margin: 10px 0 0;
    text-align: left;
    color: #fff!important;
    border-top: 0;
    font-size: 110%;
    word-wrap: break-word;
}
pre {
    display: block;
    font-size: 87.5%;
    color: #ededed;
}
}

