css:
@-moz-document domain("admin.ch") {

body{
    background-image:url("https://images.unsplash.com/photo-1502741509793-1bf00d85aeff?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2100&q=80");
    font-family: 'Nanum Pen Script', cursive;  
  
}



/*header*/ 
header{
    background:none;
    background-color:pink;
}


.mod-layout .mod-logo h2{
    color:#d63e69;
}


.mod-layout .mod-logo h2{
    font-size: 20px;
}

.treecrumb .dropdown:first-child .dropdown-toggle{
    background-color:#ffffba;
    border:none;
}

.treecrumb .dropdown-toggle{
    color:#d63e69;
}

.treecrumb .dropdown-toggle:hover{
    color:#660f70;
}

.nav-lang a{
    font-size:18px;
    color:#d63e69;
}

.nav-lang a:hover{
    color:#660f70;
}


.nav-lang a.active{
    background-color:#ffffba;
}

.nav-lang{
    border-left: dotted 4px #ffffba;
}

.nav-service a{
    font-size: 20px;
    color:#d63e69;
}

.nav-service a:hover{
    color:#660f70;
}

.brand img{
    border-right:none;
}

.yamm--select .dropdown-toggle{
    
    border-radius: 10px;
    border:none;
    color:#d63e69;
}

.yamm--select .dropdown-toggle:hover{
    color:#660f70;
}



/* Liens */
a:hover{
    color:#d63e69;
}



/* Nav */
.nav-main.yamm .nav>li>a:hover{
    color:black;
    background:none;
    background-color:#d63e69;
}


.nav-main.yamm .nav>li>a:after{
    color:black;
}


.nav-main {
    background:none;
    background-color:#d63e69;
    border-top: 10px solid #ffffba;
}



.mod-imagesbundesrat .img-wrapper li figcaption{
    color:#d63e69;
    font-size: 26px;
}

.form-control, .has-error .form-control, .has-success .form-control{
    background-color:#d63e69;
    border-radius: 10px;
    border:none;
}

button, input, select, textarea {
    color:white;
}




span.icon.icon--bottom:after {
    color:white;
}

.nav-main.yamm .nav>li>a{
    background:none;
    background-color:#ffffba;
}



/*presentation*/
.mod-teaserfocus .nav.nav-tabs li.active a{
    background-color:#d63e69;
}

.nav-tabs>li.active>a, .nav-tabs>li:active>a:focus, .nav-tabs>li:active>a:hover, .nav-tabs>li>a, .nav-tabs>li>a:focus, .nav-tabs>li>a:hover{
    background-color:#ffffba;
}



.col-md-12>.mod-teaserfocus .nav-tabs li.active>a, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li.active>a, .col-md-12>.mod-teaserfocus .nav-tabs li.active>a:focus, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li.active>a:focus, .col-md-12>.mod-teaserfocus .nav-tabs li.active>a:hover, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li.active>a:hover, .col-md-12>.mod-teaserfocus .nav-tabs li>a:focus, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li>a:focus, .col-md-12>.mod-teaserfocus .nav-tabs li>a:hover, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li>a:hover{
    border-right:3px solid pink !important;
}

.mod-teaserfocus .nav.nav-tabs li a, .mod-teaserfocus .nav.nav-tabs li.active a{
    color:#660f70;
}

.mod-teaserfocus .nav.nav-tabs li a, .mod-teaserfocus .nav.nav-tabs li.active a:hover{
    color:#d63e69;
}


a{
    color:#d63e69;
    font-size:20px;
}


p{
    font-size: 18px;
}

hr{
    height: 0px;
   
}


h3{
    font-size: 30px;
    color: #660f70;
}


.mod-teaserfocus .nav.nav-tabs li{
    background-color:#ffffba;
}

.nav-main.yamm .nav>li>a{
    font-size: 20px;
    text-align:center;
    padding-top:2px;
    color:#d63e69;
}

.nav-main.yamm .nav>li>a:hover{
    color: #ffffba;
}

.nav-main.yamm .nav>li{
    border-right: solid 1px #d63e69;
}

.mod-teaserfocus .nav.nav-tabs li a, .mod-teaserfocus .nav.nav-tabs li.active a{
    font-size:30px;
}






/* News */
.col-md-12>.mod-teaserfocus, .col-md-12>.par>.teaserfocus>.mod-teaserfocus{
    border:none;
}

.col-md-12>.mod-teaserfocus .tab-content.tab-border.tab-focus, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .tab-content.tab-border.tab-focus{
    border-bottom: dotted 4px pink;
}

.list-group h2{
    font-size: 24px;
    color: #d63e69;
}

.list-group-header{
    border-bottom: dotted 4px pink;
   
}




#site-map{
    background:none;
    background-color:pink;
}

footer{
    border-top:none;
}




/* Footer */
.icon--before.icon--twitter:before{
    font-size: 80px;
    color:#d63e69;
}


.icon--before.icon--message:before{
    font-size: 80px;
    color:#d63e69;
}


.icon--before.icon--rss:before{
    font-size: 80px;
    color:#d63e69;
}


.fa-youtube:before{
    color:#d63e69;
}


.fa.icon.icon--after:after, .fa.icon.icon--before:before, .fa:before{
    font-size:80px;
}



.site-map+div{
    background-color:pink;
    border-radius:40px;
}


#site-map{
    background-color:#fffedc;
    border-radius:40px;
}

.site-map{
    padding-bottom:0px;
    border-bottom:0px;
    background:none;
    background-color:#fffedc;
}

li{
    color:#660f70;
    list-style-type: circle;
}

.mod-twitterstream .twitter-timeline .tweet .footer .tweet-actions li a, .mod-twitterstream .twitter-timeline .tweet .footer .tweet-actions li button{
    color:#d63e69;
}

.mod-twitterstream .twitter-timeline .tweet .e-entry-content .e-entry-title a{
    color:#d63e69;
    text-decoration:underline;
}

.list-group-item, .list-group-item:last-child{
    border-bottom: dotted 4px pink;
}


.col-md-12>.mod-teaserfocus .nav-tabs li, .col-md-12>.par>.teaserfocus>.mod-teaserfocus .nav-tabs li{
    border-bottom: dotted 4px pink !important;
}


.mod-teaserfocus .nav.nav-tabs.nav-justified>li.active>a, .mod-teaserfocus .nav.nav-tabs.nav-justified>li.active>a:focus, .mod-teaserfocus .nav.nav-tabs.nav-justified>li.active>a:hover, .mod-teaserfocus .nav.nav-tabs.nav-justified>li>a, .mod-teaserfocus .nav.nav-tabs.nav-justified>li>a:focus, .mod-teaserfocus .nav.nav-tabs.nav-justified>li>a:hover{
    border-radius:6px;
}

img{
    border-radius: 20px;
    border: solid 4px pink;
}

.mod-imagesbundesrat .img-wrapper li img{
    border: solid 4px pink;
}


footer{
    background:none;
    background-color:#fffedc;
}

/*animation*/




.mod-layout .mod-logo h2 {
  -webkit-animation-name: tada;
  animation-name: tada;
  -webkit-animation-duration: 1s;
  animation-duration: 1s;
  -webkit-animation-fill-mode: both;
  animation-fill-mode: both;
  }
  @-webkit-keyframes tada {
  0% {
  -webkit-transform: scale3d(1, 1, 1);
  transform: scale3d(1, 1, 1);
  }
  10%, 20% {
  -webkit-transform: scale3d(.9, .9, .9) rotate3d(0, 0, 1, -3deg);
  transform: scale3d(.9, .9, .9) rotate3d(0, 0, 1, -3deg);
  }
  30%, 50%, 70%, 90% {
  -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  }
  40%, 60%, 80% {
  -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  }
  100% {
  -webkit-transform: scale3d(1, 1, 1);
  transform: scale3d(1, 1, 1);
  }
  }
  @keyframes tada {
  0% {
  -webkit-transform: scale3d(1, 1, 1);
  transform: scale3d(1, 1, 1);
  }
  10%, 20% {
  -webkit-transform: scale3d(.9, .9, .9) rotate3d(0, 0, 1, -3deg);
  transform: scale3d(.9, .9, .9) rotate3d(0, 0, 1, -3deg);
  }
  30%, 50%, 70%, 90% {
  -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  }
  40%, 60%, 80% {
  -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  }
  100% {
  -webkit-transform: scale3d(1, 1, 1);
  transform: scale3d(1, 1, 1);
  }
  } 



}
