/* ==UserStyle==
@name           Twitch Confortable Style
@namespace      USO Archive
@author         jorge padron
@description    `confortable colors for see streams on dark mode`
@version        20191002.0.53
@license        CC-BY-NC-SA-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("twitch.tv") {
.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: #121212;
}

.tw-root--theme-dark body {
  	font-family: Helvetica Neue,Helvetica,Arial,sans-serif!important;
    background-color: #252525
}

.tw-root--theme-dark .channel-header {
    background-color: #18181b
}
.tw-root--theme-dark .side-nav-section{
background-color: #181818
}

.tw-root--theme-dark .top-nav__menu{
background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );        
}
    
.tw-root--theme-dark .stream-chat-header{
background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );   
}
    
.tw-root--theme-dark .iNtICC{
background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );       
}
    

.tw-root--theme-dark .tw-c-background-base {
     background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
  background-attachment: fixed;
   
}

.tw-c-background-body {
    background-color: #0f0e11!important;
}

.tw-root--theme-dark .chat-room {
    background: #2c2a2f;
}
.tw-root--theme-dark .tw-interactable {
    background: #121212 !important;
}
.tw-root--theme-dark .chat-list--default {
    background: #181818;
}
.tw-root--theme-dark .channel-info-content  {
background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% ); 
}
.tw-root--theme-dark .InjectLayout-sc-588ddc-0 .KNLSb  {
 background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );       
}
.tw-root--theme-dark .social-media-space--content {
    background: #121212 !important;
}

.tw-root--theme-dark #sideNav .simplebar-scrollbar{
 background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );        
}
   
.tw-root--theme-dark .channel-header{
   background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}
.tw-root--theme-dark  .side-nav-header  {
    text-align: center;
    border: double 4px transparent;
    border-radius: 80px;
    background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
    
}
 .tw-root--theme-dark  .side-nav-show-more-toggle__button  {
   background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}  
    
.tw-root--theme-dark .tw-border-b {
    border-bottom:4px solid #d9d9de!important;
    border-bottom: var(--border-width-default) solid var(--color-border-base)!important;
    background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(51,51,81,1) 0%, rgba(34,72,86,1) 90% );
}
}
