css:
@-moz-document domain("steamcommunity.com")
{
  .no_header.profile_page.has_profile_background
  {
    background-size: 100%;
    background-attachment: fixed;
  }

  .playerAvatar.profile_header_size .playerAvatarAutoSizeInner img
  {
    border-radius: 50%!important;
  }

  .profile_header .playerAvatar.profile_header_size
  {
    border-radius: 50%!important;
  }

  .profile_background_overlay_content
  {
    visibility: hidden!important;
  }

  .profile_background_image_content
  {
    visibility: hidden!important;
  }

  .profile_background_holder_content
  {
    background-color: rgba(0, 0, 0, 0)!important;
  }

  .profile_header_bg
  {
    background: none!important;
  }

  .profile_header_bg_texture
  {
    background-color: rgba(39,39,39,0.75)!important;
    box-shadow: 0px -2px 35px rgba(0,0,0,0.8)!important;
    background-image: none!important;
  }

  .profile_header_badge
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    border-radius: 3px!important;
    border: 0px!important;
    box-shadow: 0px 0px 0px #000!important;
  }

  .profile_content.has_profile_background
  {
    width: 952px!important;
    background-color: rgba(39,39,39,0.75)!important;
    box-shadow: 0px -2px 35px rgba(0,0,0,0.8)!important;
  } /* custom background */
  .profile_content
  {
    background-color: rgba(39,39,39,0.75)!important;
    box-shadow: 0px -2px 35px rgba(0,0,0,0.8)!important;
    background-image: none!important;
    padding: 12px;
    width: 952px!important;
  } /* standart background */
  .profile_header_bg
  {
    padding-bottom: 13px!important;
  }

  .commentthread_area
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    border-radius: 3px!important;
  }

  .profile_customization
  {
    background-image: none!important;
    background-color: transparent!important;
    padding: 0px!important;
  }

  .profile_customization_header
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    padding-left: 10px!important;
    font-size: 14px!important;
    height: 23px!important;
  }

  .showcase_content_bg
  {
    border-top-left-radius: 0px!important;
    border-top-right-radius: 0px!important;
    margin-bottom: 0px!important;
  }

  .screenshot_showcase
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    padding: 7px!important;
  }

  .showcase_badges_icons
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    margin-bottom: 0px!important;
  }

  .item_showcase
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
  }

  .showcase_bodylabel
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    padding-left: 7px;
    display: none!important;
    margin-bottom: 0px!important;
  }

  .showcase_gamecollector_games
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    padding: 7px;
    padding-left: 12px;
    padding-bottom: 12px!important;
  }

  .showcase_content_bg.showcase_stats_row
  {
    border-radius: 0px;
  }

  .favoritegroup_avatar img
  {
    border-radius: 50%;
  }

  .playerAvatar img
  {
    border-radius: 50%;
  }

  .playerAvatar
  {
    border-radius: 50%;
  }

  .workshop_showcase_details_row, .workshop_showcase_details_content
  {
    background-image: none;
    background-color: transparent;
  }

  .trade_showcase_items
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    margin-bottom: 0px!important;
  }

  .showcase_slot.item_showcase_item.tradable_item
  {
    margin-left: 25px;
  }

  #footer, #footer_spacer, #footer_responsive_optin_spacer
  {
    display: none;
  }

  .commentthread_header
  {
    background-color: transparent!important;
  }

  .commentthread_header_label
  {
    font-size: 14px;
  }

  .commentthread_subscribe_ctn, .commentthread_subscribe_hint
  {
    display: none!important;
  }

  .profile_customization_editlink
  {
    display: none!important;
  }

  .myworkshop_showcase_header
  {
    background-image: none;
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    width: 100%!important;
    margin-bottom: 0px!important;
  }

  .myworkshop_showcase
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
  }

  .recent_game
  {
    border-radius: 0px!important;
    margin-bottom: 0px!important;
    background-color: rgba( 0, 0, 0, 0.3 )!important;
  }

  .profile_recentgame_header.profile_leftcol_header
  {
    border-bottom-left-radius: 0px!important;
    border-bottom-right-radius: 0px!important;
    margin-bottom: 0px!important;
    background-color: rgba( 0, 0, 0, 0.3 )!important;
  }

  .game_info_stats
  {
    display: none!important;
  }

  .recent_game:nth-child(3)
  {
    border-bottom-left-radius: 3px!important;
    border-bottom-right-radius: 3px!important;
  }

  .recentgame_quicklinks
  {
    display: none!important;
  }

  .recentgame_quicklinks.recentgame_recentplaytime
  {
    display: block!important;
  }

  .profile_comment_area
  {
    margin-top: 12px!important;
  }

  .commentthread_allcommentslink
  {
    font-size: 10px!important;
  }

  .pagebtn.disabled, .pagebtn.disabled:hover
  {
    border: 0px!important;
    padding: 0px 5px!important;
  }

  .pagebtn
  {
    background-color: transparent!important;
    padding: 0px 5px!important;
  }

  .commentthread_footer
  {
    padding-top: 0px!important;
    border-top: 0px!important;
  }

  .commentthread_comment_container
  {
    border-bottom: 0px!important;
  }

  .profile_topfriends .friendBlock:hover
  {
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    border-radius: 3px!important;
  }

  .miniprofile_hover
  {
    display: none!important;
  }

  .community_tooltip
  {
    background: #fcfcfc!important;
    font: 11px Tahoma!important;
    color: #212121!important;
    padding: 2px 4px!important;
    position: absolute!important;
    border: 1px solid #b1b1b1!important;
    border-radius: 0px!important;
  }

  .item_showcase .showcase_slot_row, .trade_showcase .showcase_slot_row
  {
    margin-left: 0px;
    padding: 6px;
  }

  .myworkshop_showcase_header.guides
  {
    border: 0px!important;
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    background: none;
  }

  .myguide_showcase_item_ctn
  {
    width: 50%!important;
    background-color: rgba( 0, 0, 0, 0.3 )!important;
    border: 0px!important;
    border-radius: 0px!important;
    margin-bottom: 0px!important;
  }

  .commentthread_header_label
  {
    quotes: "" " Theme Steam Edit: NoviinhOJP";
  }

  .commentthread_header_label:before
  {
    content: open-quote;
  }

  .commentthread_header_label:after
  {
    content: close-quote;
  }
}
