 font ('Raleway', sans-serif)

css:
@-moz-document url-prefix("https://www.blick.ch/") {
/*@import url('https://fonts.googleapis.com/css?family=Raleway&display=swap');*/



* {
    font-family: 'Raleway', sans-serif;
}


body {
    background-image: none !important;
    background-color: black;
}
.vertical-container--type-main-container {
    background-image: none !important;
    background-color: black;
}

.navigation-bar {

    background-color: #FF5B00;
}

.main-menu__container {

    background-color: #FF5B00;
}

.basic-image {
    border: 0px solid #FF5B00;
    background-color: black;
}

.basic-image picture {
    opacity: 0.1;
}

.basic-image:hover picture {
    opacity: 1;
}

.content-placeholder {
    display: none
}
.carousel-nav-btn > .icon {
    background: #FF5B00;
    color: #fff;
}

.section-navigation__subsection {

    background-color: #FF5B00;
}

.section-navigation .section__name__before {

    background: #FF5B00;
}
.section-navigation .section > .section__bullet {

    background-color: #FF5B00;
}


.section__bullet--blick_news {
    background-color: #FF5B00;
}

.icon--video-play:before {
    background-color: #FF5B00;
    border-radius: 7px
}



.teaser-standard--desktop-small .teaser-standard__info:after {
    content: '';
    z-index: 10;
    background: transparent;
    position: absolute;
    top: 0;
    left: 0;
    box-shadow: 0.1875rem 0.1875rem #FFFF inset;
}

.main-menu-item {
    display: flex;
    flex-shrink: 0;
    align-items: center;
    padding: 1.125rem .4375rem .75rem .4375rem;
    line-height: 1.94;
    font-family: "HeronSans", sans-serif;
    font-size: 1rem;
    font-weight: 600;
    word-wrap: break-word;
}

.title strong {
    color: #FF5B00;
}

.teaser-standard--desktop-small .title strong {
    color: #FF5B00;
}


.teaser-mega .title--blick_news strong {
    color: #FF5B00;
}

.main-menu {
    background-color: #FF5B00;
}



.main-menu-item--activeLeaf .main-menu-item__title {
    color: #ffff;
}

.main-menu-item--activeLeaf .main-menu-item__container {
    border-right: .3125rem solid #FF5B00;
}


.teaser-mega .title strong {
    color: #FF5B00;
}

.layout-mega-module--blick_wirtschaft {
    background-color: #ffff;
}

.teaser-standard--desktop-small .title--inverted {
    color: black;
}

header {

    background-color: #FF5B00;
}
}
