css:
@-moz-document domain("stackoverflow.com") {
html, body {
    
    font-size: 13px;
    background-color: #d40038;
        color: #171e23;
    
}


.top-bar.-secondary.-ctas.btn-topbar-clear
{color : green;}

.s-btn__primary, .s-btn__primary:focus {
    background-color: #008c00;
    border: 1px solid #008c00;
}

.top-bar .-secondary .-ctas .btn-topbar-primary {
    font-family: American Typewriter, serif;
    background-color: #008c00;
    border: 1px solid #008c00;
    
    box-shadow: inset 0 1px 0 #008c00;
}

.top-bar .-container {
    background-color: #008c00;
}

.top-bar .-main {
    background-color: transparent;
}

.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice {
    color: #242729;
    background-color: #5fba7d38;
    
}


#question-header .question-hyperlink {
    FONT-WEIGHT: 600;
    background-color: white;
    padding: 10px;
    font-family: American Typewriter, serif;
    color: #5fba7d;
}

.top-bar .-secondary .-ctas .btn-topbar-clear {
    
    color: #fafafb;
   
}


.s-sidebarwidget__yellow .s-sidebarwidget--header, .s-sidebarwidget__yellow .s-sidebarwidget--content {
    border-color: #2cbb37;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header {
    background-color: #484843;
    color: #737063;
}

.top-bar .-marketing-link {
   
    padding: 6px 10px;
    
    color: #eff0f1;
   
}

.s-sidebarwidget__yellow .s-sidebarwidget--header {
    
    color: #2cbb37;
}


.s-sidebarwidget__yellow .s-sidebarwidget--header {
    background-color: #d40038;
    color: #2cbb37;
}


.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice {
    font-size: 20px;
}

.s-sidebarwidget__yellow .s-sidebarwidget--header, .s-sidebarwidget__yellow .s-sidebarwidget--content {
    border-color: #2cbb37;
}

.container {
  background-color: #f7f519;
    text-align: center;
}


.question-status {
   
    background-color: #38c7ed;
   
}



.s-anchors.s-anchors__default a:not(.s-link), .s-anchors .s-anchors.s-anchors__default a:not(.s-link) {
    color: #38c7ed;
}


.post-text h3, .wmd-preview h3, .post-text h3 code, .wmd-preview h3 code {
    font-size: 20px;
    font-family: American Typewriter, serif;
}


.page-numbers.current {
   
    background-color: #008c00;
   
}


/* boutons liens à doroite de la page */
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes.answered-accepted, .show-votes .sidebar-related .spacer>a:first-child .answer-votes.answered-accepted {
    color: #d40038;
    background-color: #f7f519;
}





.show-votes .sidebar-linked .spacer>a:first-child .answer-votes, .show-votes .sidebar-related .spacer>a:first-child .answer-votes {
    padding: 15px 0;
    white-space: nowrap;
    width: 45px;
    
    border-radius: 5px;
    font-size: 90%;
    background-color: #f7f519;
    color: #129e3e;
   
}

.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice {
    color: #d40038;
    background-color: #5fba7dc9;
}



.clc-btn-secondary {
    color: #45a163;
    background-color: #efeb8a;
    border-color: #efeb8a;
    box-shadow: inset 0 1px 0 #efeb8a;
}

a {
    color: #45a163;
   
}
a, .s-link {
    color: #45a163;
   
}

#sidebar .related, #sidebar .linked {
    font-size: 13px;
}


.s-anchors.s-anchors__default a:not(.s-link), .s-anchors .s-anchors.s-anchors__default a:not(.s-link) {
    color: #38c7ed;
}

/*couleurs lien a gauche*/
.nav-links .nav-links--link, .nav-links .nav-links--link:visited {
    color:#f7f519;
}

/*couleurs footer*/
.site-footer {
    background-color: #38c7ed;
    color: #45a163;
    
}

.site-footer .-title a {
    color: #45a163;
    
}
/*liens jaunes à droites */
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes.extra-large, .show-votes .sidebar-related .spacer>a:first-child .answer-votes.extra-large, .show-votes .sidebar-linked .spacer>a:first-child .answer-votes.large, .show-votes .sidebar-related .spacer>a:first-child .answer-votes.large {
    padding: 10px 0;
    min-width: 20px;
}


.s-sidebarwidget__yellow {
    border-color: #f7f51991;
    background-color: #f7f519;
}
}
