css:
@-moz-document domain("app.hubspot.com") {
#hs-nav-v4 #navbar {
    background-color: #D31D8C;
    border-top: 3px solid #EE88CD;
}
#hs-nav-v4 .secondary-nav, #hs-nav-v4 .expansion a.navSecondaryLink {
    background-color: #00C7F6;
}
#hs-nav-v4 .primary-links>li>a:hover {
    background-color: #D31D8C;
}
#hs-nav-v4 .secondary-nav>ul>li.active>a, #hs-nav-v4 .secondary-nav>ul>li:not(.expandable)>a:hover {
    background-color: #00C7F6;
    background-color: #D31D8C;
}
#hs-nav-v4 .primary-links>li.active>a, #hs-nav-v4 .primary-links>li.currentPage>a {
    background-color: #00C7F6;
}
#hs-nav-v4 .nav-dropdown-separator {
    border: 1px solid #fff;
}
#hs-nav-v4 .navSearch-v2 .navSearch-inputWrapper .navSearch-input {
    background-color: #D31D8C;
}
#hs-nav-v4 #navbar .navSearch-v2.open .navSearch-inputWrapper .navSearch-input, #hs-nav-v4 .navSearch-v2 .navSearch-inputWrapper .navSearch-input:hover {
    background-color: #00C7F6;
}
.private-button--primary {
    background-color: #00cc7f;
    background-color: #00C7F6;
    border-color: #00cc7f;
    border-color: #00C7F6;
    color: #fff;
}
.private-button--secondary {
    background-color: #fff;
    border-color: #00cc7f;
    border-color: #00C7F6;
    color: #00cc7f;
    color: #00C7F6;
}
}
