css:
@-moz-document domain("rts.ch") {
:root {
    --rts-color: #db2e33;
    --highlight-color: var(--rts-color);
}


.main,
.bevel-top,
.bg-universe,
.main,
.rts-editorial-zone .bevel-background,
.border,
.bg-default,
.bg-header,
.bg-automatic-zone,
.rts-layout,
div[class^=rts-module-infosport],
.rts-module-timeline-event{
    background: #191919 !important;
    color: white;
}

.highlight1 .bevel-content,
.bizot,
.article-related-elements,
.rts-module-infosport-related
{
    background: var(--highlight-color) !important;
}

.rts-footer .rts-footer-wrapper
{
      border-color: var(--highlight-color);  
}

.bg-mid,
.bg-mid .rts-container {
    background: #454545 !important;
}

.rts-cookie-warning-container,
.rts-cookie-warning-container .rts-container
{
    background: #555555 !important;
    color: #dddddd;
}

.rts-module-subnav,
.rts-module-subnav u,
.rts-menu,
.rts-module-infosport-footer,
a.home-link
{
    background: #191919 !important;
    color: #dddddd !important;
}

.rts-module-timeline-event .inner
{
    background: #191919 !important;
    color: #dddddd !important;    
}

.rts-card,
.first-cell,
.container-item,
.box-bizot,
.rts-module-infosport-footer {
    background: #191919 !important;
    color: #dddddd;
}

.container-item {
    margin: 0px;
}



.sport-epg .rts-container,
.sport-epg .rts-container a.epg-card .card-caption
{
    background: #191919 !important;    
}
.rts-navbar,
.rts-navbar-nav,
.navbar-actions {
    background: #191919
}

.article-boxes,
.article-part .box-callout
.article-body
{
    background: #191919;
    color: #dddddd;
}

.rts-module-timeline-min time,
.rts-module-timeline-key-event *
{
    background: #191919 !important; 
    color: var(--highlight-color)  !important;
}

.rts-module-timeline-min h5,
.rts-module-timeline-key-event h5
{
    background: #191919 !important; 
    color: #dddddd  !important;
}

.card-caption,
.card-header .time,
.card-header .timer,    
.content-item h4,
.stripchars,
.right-info,
.timeline-Widget,
.card-container {
    background: #191919 !important;
    color: white !important;
}


.card-caption {
    background: #19191988 !important;
}

.rts-module-framed .border-spacer
{
    background: #444444 !important;
    color: #dddddd !important;
    border-color: #444444
}

.rts-card * {
    color: #dddddd;
}

.rts-box
{
    background: #444444 !important;
    color: #dddddd !important;
    border-left-color: var(--highlight-color) !important;
}

a.rts-btn {
    background-color: #191919 !important;
    border: #666666 !important;
    color: #dddddd !important;

}

.rts-module-pagination .text {
    background-color: #191919 !important;
    border: #666666 solid !important;
    color: #dddddd !important;
}

.article-time,
figure .legend{
    color: #999999 !important;
}

.li.count {
    background-color: #191919 !important;
}

.rts-module-hp-item.highlight .container-item,
.rts-module-hp-item.highlight .rts-module-title *
{
    background-color: var(--rts-color) !important;
}

.le-court-du-jour-aujourdhui-cest-de-la-dynamite * {
    background: #333399 !important;
    color: #dddddd !important;
}

.ranking .rank,
.swisstxt-table .cell.top
{
    background-color: #333333 !important;
    color: #dddddd;
    border: #dddddd solid 1px;  
}
.ranking .competitor-name,
.ranking .competitor-result,
.swisstxt-table .cell,
.swisstxt-event,
.swisstxt-event .player
{
    background-color: #333333 !important;
    color: #dddddd !important;
}

.swisstxt-event .player.won *
{
    color: white !important;
    
}

}
