body {
  display: flex;
  justify-content: center;
  background: #263238;
  color: #eceff1;
}

xmp {
  font-size: 1rem;
  white-space: pre-wrap;
}

/* sample files */
pre {
  display: inline-block;
  padding: 1.5em;
  border-radius: 6px;
  border: 1px solid #00000055;
  background: #455A64;
  max-width: 100%;
  box-sizing: border-box;
  overflow-x: scroll;
}

/* other code examples */
#content .code {
  border-radius: 6px;
  border: 1px solid #00000055;
  background: #455A64;
}

tt {
  font-size: 1rem;
  overflow-wrap: anywhere;
}

hr {
  margin: 2rem 0;
}

h1,
h2,
h3,
#content h1,
#content h2,
#content h3 {
  margin: 2rem 0;
  color: #eceff1;
}

p {
  margin: 1em 0;
}

ul {
  list-style-type: square;
  font-size: 1rem;
}

a:link, a:visited {
  color: #81D4FA;
  text-decoration: none;
}
a:hover {
	text-decoration: underline;
}
a:active {
  color: #E1F5FE;
  text-decoration: underline;
}

#header,
#footer {
  padding: 0;
}

#header h1 {
  display: block;
  margin: 1rem;
  font-family: 'Roboto', sans-serif;
  text-align: center;
}

#headerimg {
  filter: invert(100%) hue-rotate(120deg) grayscale(50%);
  border-top: 8px solid white;
}

figure {
  text-align: center;
}

figure img {
  max-width: 100%;
}

#footer {
  border-top: 1px solid #eceff1;
  color: #81D4FA;
}

#footer a {
  display: inline-block;
  margin: 1rem;
  color: #81D4FA;
}

#nav {
  font-family: 'Roboto', sans-serif;
  height: auto;
  padding-left: 1rem;
}

#nav ul li {
  float: none;
  display: inline-block;
  font-size: 1.1rem;
  padding: 0;
}

#nav ul li a {
	color: #546E7A;
}

#nav ul li a:hover {
	background: #37474f;
	color: #cfd8dc;
}

#wrapper {
  width: auto;
  min-width: 0;
  max-width: 940px;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  border: none;
  box-shadow: 0 0 3px #00000055;
  background: #37474F;
}

#content {
  padding: 1rem 5%;
  box-sizing: border-box;
  font-family: 'Roboto', sans-serif;
  font-size: 1rem;
  color: #CFD8DC;
}

#content .code {
  color: #eceff1;
}

/* Special info boxes on some pages */
.info {
  background-color: #546E7A;
  color: #ECEFF1;
  border-color: #CFD8DC;
}

.warn {
  background-color: #01579B;
  border-color: #29B6F6;
  color: #29B6F6;
}

/* Google Calendar */
iframe {
  filter: invert(100%) hue-rotate(145deg) contrast(75%);
}

table {
  width: 100%;
  table-layout: fixed;
  color: #CFD8DC;
}

td#t01 {
  border: 1px solid #CFD8DC;
}

th, th#t01 {
  background-color: #455A64;
  border-color: #CFD8DC;
}

}
