css:
@-moz-document domain("dec-industrie.com") {
#page-wrapper {
	background-color: #202b30;
	padding-top: 0 !important;
}
#main.container {
	background: #29363d;
}
body {
	color: white;
}
.box {
	background: none;
}
strong, b {
	color: white;
	font-weight: bold;
}
h1, h2, h3, h4, h5, h6 {
	color: white;
}
#header {
	background: #202b30 !important;
	box-shadow: 0px 1px 1px rgb(40, 50, 60);
}
.nav_logo {
	background: #202b30 url(http://dec-groupe.com/imgs/logo.png) no-repeat center center;
	background-size: contain;
}
.nav_logo:focus {
	background: #202b30 url(http://dec-groupe.com/imgs/logo.png) no-repeat center center !important;
	background-size: cover !important;
}
#header nav > ul > li a {
	background: none;
	color: white !important;
}
#header nav > ul > li.active a:not(.button) {
	background: none;
}
.dropotron {
	background: #202b30;
	box-shadow: none;
}
.dropotron li:hover > a {
	background-color: #202b30;
	color: white !important;
}
.dropotron li:hover {
	background-color: #202b30;
}
.dropotron.level-0:before {
	background: #202b30;
}
#footer {
	background: #202b30;
}
a.rub_element {
	color: rgb(180, 180, 180);
}
.box.specialrub h3 {
	color: white;
}
.blogi {
	background: #29363d;
	border: none;
}
.blogi p {
	color: rgb(210, 210, 210);
}
.most_popular_articles {
	background: #29363d;
	border: none;
}
.most_popular_articles ul li.popular_article h3{
	color: rgb(180,180,180);
}
#twitter-widget-0 {
	border-left: 1px solid rgb(60, 60, 60) !important;
}
.formulaire_recherche input.text {
	background: #29363d url(https://www.pngkit.com/png/full/14-140989_free-icons-png-instagram-search-icon-white.png) no-repeat right 5px center;
	background-size: 16px;
	border: solid 1px rgba(255, 255, 255, 0.1);
}
.box.specialrub .image.featured {
	border: solid 1px rgba(255, 255, 255, 0.1);
}
.menu_rubriques {
	background: #29363d;
	border: none;
}
.menu_rubriques a {	
	background: #29363d;
	border: solid 1px rgba(255,255,255,0.1);
}
.menu_rubriques a:last-child {
	border: solid 1px rgba(255,255,255,0.1);
}
.liste_sous_rubrique .box.special_sous_rubrique .box_content h3{
	color: rgb(180,180,180);
}
.liste_sous_rubrique {
	background-color: #29363d !important;
}
.timeline-Widget {
	background: #29363d;
}
.timeline-Header-title {
	color: white;
}
}
