@-moz-document url("https://juejin.im/"){

/*直接隐藏不需要的 */
.main-nav-list[data-v-b2256e10] , .nav-item.submit[data-v-b2256e10],.welcome-context[data-v-5d054180], .view-nav, .view-nav:before{
    display: none !important;
}
    
/**背景*/
 body {
    background: #F1F4ED url(https://i.loli.net/2019/10/04/on2ZXYK9tNLyGEa.png) no-repeat fixed top right !important;
    background-size:cover !important;
    height:750px;
	overflow: hidden;
              
}
/* 页面头部样式设置 */

/* .main-header[data-v-b2256e10] {
	display:none;
} */

.main-header[data-v-b2256e10] {
    background: rgba(255, 255, 255, 0) !important;
    color: #909090;
    height: 5rem;
    z-index: 250;
    border-bottom:none !important;
}
/* 掘金logo样式设置 */
.logo[data-v-b2256e10] {
    position: absolute;
    top: 180px;
    left: 250px;
    height: 100px;
    width: 100px;
}

.logo[data-v-b2256e10] img{
    height:100px;
}
/* 搜索框简改 */
.search-form[data-v-b2256e10] {
    width: 500px;
    height: 50px;
    border-radius: 10px;
    background: rgba(255, 255, 255, 0.1);
}
/* 搜索框定位居中 */
.nav-item.search[data-v-b2256e10] {
    position: absolute;
    top: 300px;
    left: 230px;
   
}
/* 搜索框细节化 */
.search-form .search-input[data-v-b2256e10] {
    border: none;
    width: 30rem;
    padding: .6rem 1rem;
    box-shadow: none;
    outline: none;
    font-size: 1.1rem;
    color: #666;
    background-color: transparent;
}

/* 登录注册样式改动 */
.nav-item.auth[data-v-b2256e10] {
    position: absolute;
    top: 300px;
    right: 100px;
    color: #007fff;
    font-size: 1.8rem;
}
/* LOGO */
 .tbh-logo {
    position: absolute;
    top: -160px;
    left: 50%;
    transform: translateX(-50%);
}
/* 顶部nav */
 .J_Cover {
    position: fixed;
    top: 55%;
    left: 50%;
    transform: translateX(-50%);
}
.site-nav {
    background-color: white;
    border: none;
}
/* 掘金首页导航栏信息隐藏 */
 .site-nav .site-nav-bd {
    display: none !important;
}
/* 信息页背景萌化 */
 #content, #page #content {
    background: #F1F4ED url(https://s2.ax1x.com/2019/04/29/E37N5t.jpg) no-repeat fixed top right !important;
}
}


@-moz-document url-prefix("https://juejin.im/post"){
/* post文章页 */
/*目录 */
nav.article-catalog {
    min-width: 208.813px;
    overflow: auto;
    padding: 15.6px;
    background-color: #fff;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .04);
}
div.catalog-title {
    margin-bottom: 20px;
    padding: 4px;
    color: #444;
    text-align: center;
    font-size: 1.5em;
    box-shadow: 0 0 2px 2px #e7ede7;
}
/* 作者信息 */
div.author-block {
    z-index: 249;
}
div.block-body {
    display: none;
}
div.block-title:hover + div.block-body {
    display: block;
}
div.block-body:hover {
    display: block;
}

/* 文章页 */
/**背景*/
body{
  background: #F1F4ED url(https://i.loli.net/2019/10/06/NGhIwiYAfgjK7H1.png) no-repeat fixed top right !important; 
  background-size:cover !important;
}
/* 导航栏样式改动 */
.main-header[data-v-b2256e10] {
    background: rgba(255,255,255,0.5)  !important;
    border-bottom: 1px solid #f1f1f1;
    color: #909090;
    height: 5rem;
    z-index: 250;
}
/* 内容区分享区三个隐藏 */
/* 分享隐藏 */
.share-title[data-v-91445d5a] {
    display:none;
}
/* 新浪按钮隐藏 */
.panel-btn.weibo-btn[data-v-91445d5a] {
  display:none;
}
/* QQ分享隐藏 */
.panel-btn.qq-btn[data-v-91445d5a] {
    display: none;
}
.panel-btn.wechat-btn[data-v-91445d5a] {
    display: none;
}

/* main内容区背景样式设置 */
.main-area[data-v-f23bec18] {
    position: relative;
    width: 700px;
    max-width: 100%;
    border-radius: 2px;
    box-sizing: border-box;
    background: rgba(255,255,255,0.5)  !important;
}

.main-area[data-v-f23bec18] {
    position: relative;
    width: 700px;
    max-width: 100%;
    border-radius: 20px;
    background: rgba(255,255,255,0.5) !important;
}

/* 右侧干扰元素隐藏 */

div.index-book-collect,
div.app-download-sidebar-block,
div.wechat-sidebar-block {
    display: none!important;
}

/* 右侧模块样式设计 */
.sidebar[data-v-f23bec18] {
    position: absolute;
    top: 0;
    right: 0;
    width: 19rem;
}

div.block-title:hover + div.block-body {
    transition: all 2s;
    display: block;
}

/* post区右侧栏样式设置 */
.sidebar-block[data-v-a357a130]:not(.pure) {
    background: rgba(255,255,255,0.5)  !important;
    border-radius: 10px;
}

/*  post区右侧栏目录中目录字体设计 */
div.catalog-title {
    margin-bottom: 20px;
    padding: 4px;
    color: #444;
    text-align: center;
    font-size: 1.8em;
    box-shadow: none;
}

/*  post区右侧栏目录样式设计 */
	nav.article-catalog {
    min-width: 208.813px;
    overflow: auto;
    padding: 15.6px;
 	 background: rgba(255,255,255,0.5)  !important;
    border-radius: 7px;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .04);
}

/*  post区右侧栏目录中原点样式设计 */
.catalog-list[data-v-3ba6bf54] .item.d1>a:before {
    left: 5px;
    margin-top: -3px;
    width: 10px;
    height: 10px;
}

.catalog-list[data-v-3ba6bf54] .item.d2>a:before {
    left: 24px;
    height: 5px;
    width: 5px;
}


/* 折叠底部相关推荐 */
ul.recommended-entry-list {
    display: none;
}
.recommended-entry-list-title:hover + .recommended-entry-list {
    display: block;
}
.recommended-entry-list:hover {
    display: block;
}
}
