css:
@-moz-document domain(stallman.org)
{
body {
    color: #aaa !important;
    background: #111 !important;
    /*font-family: Arial, Helvetica, sans-serif !important;*/
}

a:visited{
    color: #bd7ad7 !important;
    text-decoration: underline !important;
}

a:link{
    color: #186ec3 !important;
    /*text-decoration: none !important;*/
}

a:hover, a:hover span {
    color: #ccc !important;
    text-decoration: underline !important;
}

img {
    background-color: #111 !important;
    filter: grayscale(30%) brightness(70%) !important;
}

form input {
    background:rgba(22, 75, 120, 0.1) !important;
    border: 1px solid rgba(28, 92, 146, 0.3) !important;
    color: #bbb !important;
    border-radius: 2px !important;
}

form input[type=submit] {
    min-height: 22px !important;
}
}
