css:
@-moz-document domain(4chan.org)
{

body[bgcolor] 
  { background: url("http://i44.tinypic.com/2cru639.png") 23% 5% no-repeat fixed #C1F0F6 !important;

    font: 80%/1.4 Helvetica, Arial, sans-serif !important; }

.filesize { font: 100%/1.4 Helvetica, Arial, sans-serif !important; }

a 
  { color: #000000 !important;  }

.doubledash { display: none; }
.postblock 
  { background-color: #CCFFFF!important;
    border-color: #000000!important;
    color: #000000 !important;  }

.inputtext, input[name="upfile"]
  { -moz-border-radius: 0 10px 10px 0 !important; }

textarea.inputtext
  { height: 100px !important; }

img[md5] {
height: auto !important;
width: auto !important;
border: 1px solid #000 !important;
}

DIV[class="4chan_ext_thread"], .reply, .pages, th {
-moz-border-radius: 1em !important;
}

.deletebuttons .inputtext { width: 100px !important; -moz-border-radius: 10px !important; }

.pages { position: relative !important; }
.pages a, .pages b { padding: 0 0.25em !important; }
.pages a:visited { font-style: normal !important; }

.pages b { font-size: 150% !important; margin-top: 0.25em !important;  }

.commentpostername a.linkmail[href="mailto:sage"]:after
  { content: " (ä¸‹ã’ã‚‹)"; }

.reply, .replyhl, .pages
  { border: 0px solid !important;}

.reply, .pages
  { background-color: #ffffff !important;
    border: 1px solid #BFB783 !important;
    padding-bottom: 10px !important;
    padding-right: 10px !important;}

.postertrip 
  { color: #000000 !important; 
    opacity: 0.5 !important; }

.replyhl .postertrip { color: #000000 !important; }

.replyhl .commentpostername { color: #000000 !important; }

.replyhl a { color: #000000 !important; }

hr 
  { border-color: #f2cc5d !important; 
    width: 100% !important;
    clear: both !important; }

tbody, .filesize, .postblock, .reply, .replyhl, .unkfunc, .filetitle, .omittedposts, .commentpostername, .postertrip, .pages, .postername, .replytitle, #navbot, #navtop, th, DIV[class="4chan_ext_thread"], blockquote, a {
font-family: Verdana, Arial, sans-serif !important;
font-size: 12px !important;}

/* 4chan extension */

DIV[class="4chan_ext_hiddenthreadinfo"] .reply
  { padding: 0 !important; 
    border: 0 none !important;
    -moz-border-radius: 0 !important;
    background-color: transparent !important; }

.reply .postblock { text-align: center !important; }

SPAN[class="4chan_win_header_buttons"]
  { right: 34px !important; }

SPAN[style="border: thin solid black; padding: 3px; z-index: 100; position: absolute; right: 10px; top: 0px;"][class="reply"],
SPAN[style="border: thin solid black; padding: 3px; position: absolute; right: 10px;"][class="reply"] 
{ padding: 0 !important;
  -moz-border-radius: 0 5px !important; }

#twi_table_div 
  { border: 0 none !important; 
    opacity: 0.90 !important; }

#twi_table_div .postername,
#twi_table_div .postertrip,
#twi_table_div .filetitle
  { font-size: 100% !important; }

#twi_table_div > .postblock 
  { border-color: #000000!important;
    border-width: 1px 1px 0 1px !important;
    -moz-border-radius: 10px 10px 0 0 !important;
    text-align: center !important; }

#twi_table_div table .postblock 
  { -moz-border-radius: 0 !important;
    background-color: #f2cc5d!important; 
    margin: 0 !important;
    color: #000000 !important;
    text-align: center !important; }

#twi_table_div > #twi_table
  { border-width: 0 1px 1px 1px !important;
    -moz-border-radius: 0 0 10px 10px !important; 
    padding: 0 0 5px 0 !important;
    opacity: 1.00 !important; }

center {
-moz-opacity: 0 !important;
max-height: 1px !important
}

hr {
-moz-opacity: 0 !important
}

.doubledash, th[bgcolor], td.rules, #navbotr, #navbot, #footer, #navtop, #twi_table_div, span[id*="4chan_ext_right"], span[id*="4chan_ext_nav_top"], td[align="right"] {
display: none !important}

}
