css:
@-moz-document url-prefix("https://lms.codefactory.live/") {
:root {
    --main-bg-color: #263238;
    --sec-bg-color: #4f5b62;
    --dark-bg-color: #000a12;
    --blue-bg-color: #459bfffa;
}

#page-my-index {
    background-color: var(--dark-bg-color) !important;
}

.card-body {
    color: white;
    background-color: var(--main-bg-color) !important;
}

.list-group-item {
    color: white;
    background-color: var(--sec-bg-color) !important;
}

.list-group-item:hover {
    color:black;
    background-color: white !important;
}

#nav-drawer {
    background-color: var(--main-bg-color) !important;
}

.navbar-light {
    background-color: var(--main-bg-color) !important;
}

.site-name {
    color:white;
}

button {
    background-color: var(--dark-bg-color) !important;
    color: white !important;
}

::-webkit-scrollbar {
    width: 5px;
}
::-webkit-scrollbar-track {
    background: var(--dark-bg-color);;
    border-radius: 10px;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px var(--blue-bg-color); 
}

.container-fluid {
    background-color: var(--dark-bg-color);
}

.d-md-block{
    color: white;
}

.coursebox {
    background-color: var(--sec-bg-color);
}

.courses .coursebox.even {
    background-color: #5d6b73;
    
}

.coursebox>.info>.coursename a, h2 {
    color: white;
}

#region-main {
    background-color: var(--main-bg-color)
}

.fswd60-devenv>p, .fswd60-devenv>p>span, li>span {
 color: #e1d8d8 !important;   
}

h2>span, h1>span, h3>span, h4>span, .modified {
    color: var(--blue-bg-color) !important;;
}

.no-overflow>table, .no-overflow>p {
    color: white !important;
}

.no-overflow>table>caption {
    color: var(--blue-bg-color)
}

.btn-link {
    background-color: var(--main-bg-color) !important
}

.no-overflow>ul, .no-overflow>ol, span>ol, span>p , .no-overflow>div>p>span {
    color:white !important;
}

#region-main {
    color:white;
}

table>tbody>tr>td, table>tbody>tr>td>p, table>tbody>tr>td>p>span {
    background-color: var(--main-bg-color) !important;
    color: white !important;
}

.m-t-1 {
    background-color: var(--main-bg-color);
}

#page-footer {
    background-color: var(--sec-bg-color) !important; 
}
}
