css:
@-moz-document domain(forums.xamarin.com) {

#Frame,
body,
.Item,
.CommentFormWrap,
.DataList.Activities > .Item,
.MessageFormWrap,
.MessageList.Comments > .Item,
.MessageList.Discussion .ItemDiscussion,
.Discussion .CommentForm,
.MessageForm {
    background-color: #202020;
}

.local-bar {
    background-color: #363636;
}

.local-bar .navigation ul li a,
.local-bar nav ul li a {
    color: #ccc;
}

.local-bar .navigation ul li a:hover,
.local-bar nav ul li a:hover {
    color: #a3a3a3;
}

#Head .ForumMenu li a:hover,
#Head .WhoIs .Username:hover {
    color: #3498db;
}

.Comments .Author > a,
.Discussion .Author > a {
    margin-left: 5px !important;
    color: white;
}

.DataList .Message,
.ItemDiscussion .Message,
.MessageList .Message,
.userContent > :not(.emoji):not(:last-child):not(br),
.editor .icon,
.editor-help-text,
.DataTable td.BigCount {
    color: #ccc;
}

.DataList .Message blockquote,
.ItemDiscussion .Message blockquote,
.MessageList .Message blockquote,
code {
    background-color: #333;
    border-radius: 5px;
}

.MessageList .QnA-Item-Accepted {
    background-color: #223322;
}

ul.AcceptedAnswers li .Comment {
    border: none;
}

.MessageList .Comment,
.MessageList .CommentFormWrap,
.MessageList .Discussion,
.MessageList .MessageFormWrap {
    border-top: 1px solid #303030;
}

.Comment .Reactions a .Count,
.Discussion .Reactions a .Count,
.MiniPager {
    color: #ccc !important;
    background-color: #333 !important;
}

.EditCommentForm textarea.TextBox,
.CommentForm textarea.TextBox {
    background: #ccc;
}

.DataTable .Item td {
    background-color: #202020;
}

.DataTable .Item:nth-child(2n+1) td {
    background-color: #363636 !important;
}

.DataTable thead td {
    background-color: #4c4c4c;
    color: #ccc;
}

.DataTable thead td {
    border-bottom: 1px solid #303030;
}

.DataTable .QnA-Tag-Accepted {
    background-color: #77d065;
}

.BoxNewDiscussion {
    padding-bottom: 10px;
}

.BoxNewDiscussion ul {
    background-color: #202020;
}

.Pager a,
.Pager span,
.NumberedPager .Next,
.NumberedPager .Previous {
    background: #333;
    color: #ccc;
}

.Pager a.Highlight,
.Pager a:hover,
.NumberedPager .Next:hover,
.NumberedPager .Previous:hover {
    background: #707070;
    color: white;
}

/* CODE */
pre {
    background-color: #4a4a4a;
}

.kwd {
    color: #569cd6;
}

.typ {
    color: #c3d9dc;
}

.pln {
    color: #64c2fe;
}

.com {
    color: #46a649;
}

.lit {
    color: #b5ce97;
}

.pun {
    color: #b4b4b4;
}

.tag {
    color: #e0e6d8;
}

.atn {
    color: #83caea;
}

.atv {
    color: #cfba72;
}

}
