css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/**************
  * General CSS *
  **************/
  
body
{
  background: url("https://cdn.dribbble.com/users/44136/screenshots/670871/attachments/59345/pokemon_wallpaper_2560x1440.jpg") !important;
  background-size: cover !important;
}
  
  /*******************
  * General CSS ~end *
  *******************/
  
  /******************
  * Mainmenuwrapper *
  ******************/
  
.menugroup
{
  background: rgba(255, 255, 255, 0.5);
  border: 1px solid #000;
  color: #FFF;
  text-shadow: 0px 1px 0px #000;
}

.mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:focus, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:focus
{
  border: 1px solid #4C5081;
  background: transparent -moz-linear-gradient(center top , #7CAAC3, #526180);
  background: linear-gradient(to bottom,hsl(201, 37%, 63%),hsl(220, 22%, 41%));
  text-shadow: 0px -1px 0px #4C5081;
}

.mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover
