  text-shadow: 0px -1px 0px #F26330;
}

.menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active
{
  border: 1px solid #4C5081;
  background: #526180;
  text-shadow: 0px -1px 0px #4C5081;
  color: #FFF;
}

.select
{
  border-color: #000;
  color: #252525;
  background: rgba(255, 255, 255, 0.9);
  text-shadow: none;
}

.select:hover
{
  border-color: #000;
  color: #252525;
  background: rgba(255, 255, 255, 1);
}

.select:disabled, .select:disabled:hover
{
  border-color: #000;
  background: rgba(255, 255, 255, 0.5);
}

.mainmenufooter
{
  padding: 10px;
  border: 1px solid #000;
  background: rgba(255, 255, 255, 0.5);
  border-radius: 20px;
  color: #F6AB52;
}

.mainmenufooter a
{
  color: #F6AB52;
}

.mainmenufooter a:hover
{
  color: #EECC49;
}

.pm-window h3
{
  background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
  background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  border-color: #000;
  color: #000;
}

.pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover
{
  background: #E3E3E3;
  border-color: #000;
  color: #000;
}

.pm-window h3.pm-notifying
{
  border: 1px solid #F26330;
  background: transparent -moz-linear-gradient(center top , #EECC49, #F6AB52);
  background: linear-gradient(to bottom,hsl(48, 83%, 61%),hsl(33, 90%, 64%));
  text-shadow: 0px -1px 0px #F26330;
  color: #FFF;
}

.pm-window h3.pm-notifying:hover
{
  background: #F6AB52;
  border-color: #F26330;
  color: #FFF;
  text-shadow: 0px -1px 0px #F26330;
}

.pm-log, .pm-window.focused .pm-log
{
  border-color: #000;
}

.pm-log-add, .pm-window.focused .pm-log-add
{
  border-color: #000;
