  border-color: #000;
}

.utilichart b
{
  color: #5F769D;
}

.teambar button
{
  background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
  background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  border-color: #000;
}

.teambar button:hover
{
  background: #E3E3E3;
  border-color: #000;
}

.teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active
{
  background: #E3E3E3;
  border-color: #000;
}
  
  /* #999 */
  
.utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em
{
  color: #666;
}
  
  /* #777 */
  
.utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol
{
  color: #444;
}
  
  /* #444 */
  
.utilichart .col
{
  color: #222;
}

.utilichart .illegalcol em
{
  border: 1px solid #CD2B2B;
  color: #CD2B2B;
}

.teambuilder-clipboard-data, .teambuilder-clipboard-data:hover
{
  background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
  background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  border-color: #000;
}

.teambuilder-clipboard-data .section
{
  border-color: #000;
}

.teambuilder-clipboard-title
{
  text-shadow: none;
  text-decoration: underline;
}

.teambuilder-clipboard-container
{
  border-color: #F26130;
}
  
  /***********************
  * Teambuilder CSS ~end *
  ***********************/ 
  
  /*************
  * Battle CSS *
  *************/

.controls
{
  background: rgba(255, 255, 255, 0.5);
  color: #000000;
  text-shadow: 0px 0px 1px #FFF;
}

.battle-log
{
  background: transparent;
  border-left: none;
  color: #000;
  text-shadow: 0px 0px 1px #FFF;
}

.battle-log .chat strong
{
  color: #000;
}

.battle-log .chat em
{
  color: #000;
  text-shadow: none;
  border: 1px solid #000;
  background: #EEE;
}

.battle-log .chat small
{
  color: #444;
  text-shadow: none;
}

em[style="color:#445566;display:block;"]
{
  color: #000 !important;
  text-shadow: 0px 0px 3px #000;
  font-style: italic !important;
}

.battle-log h2
{
  border-color: #000;
  border-left: 1px solid #000;
  background: rgba(255, 255, 255, 0.3);
}

.battle-log-add
{
  background: #000;
}

.battle-log-add .textbox
{
  border-color: #000;
}

.battle-log-add
{
  border-top-left-radius: 5px;
  border: none;
  text-shadow: none;
}

.battle-log .rated strong
{
  background: #000;
  text-shadow: none;
}

.battle-controls .whatdo
{
  color: #000000;
}

.message-error
{
  color: #000;
  border: 1px solid #000;
  background: #EEE;
  margin-top: 1px;
  padding-left: 5px !important;
}

.moveselect button, .switchselect button
{
  color: #212121;
}

.chat small
{
  color: #454545;
}

.ps-room.ps-room-opaque
{
  background: rgba(255, 255, 255, 0.5);
  color: #000;
  text-shadow: none;
  border-right: none;
}
  
  /* Battle Background */


.backdrop:after
{
  display: block;
  content: "";
  background: url("https://i.imgur.com/uHaHmEJ.jpg");
  background-size: cover;
  width: 700px;
  height: 455px;
}
  
  /* Battle Background ~end */
  
  /*******************
  * Leftbar/Rigthbar *
  *******************/

.rightbar
{
  border-color: rgba(215,215,215,0.5);
}

.leftbar
{
  border-color: rgba(215,215,215,0.5);
}

  /************************
  * Leftbar/Rigthbar ~end *
  ************************/
  
  /*****************
  * Battle Weather *
  *****************/

.weather
{
  text-shadow: none;
  color: rgba(0,0,0,0);
}

.weather em small
{
  font-size: 7pt;
  color: white;
  text-shadow: 1px 1px black;
  position: absolute;
  left: 0;
  top: 85px;
  width: 95px;
  text-align: center;
}

.sunweather, .sunnydayweather, .desolatelandweather
{
  background: url(http://i.imgur.com/DNMtOWR.gif) scroll left top;
}

.rainweather, .raindanceweather, .primordialseaweather
{
  background: url(http://i.imgur.com/NQqkU6j.gif) scroll left top;
}

.deltastreamweather
{
  background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
}

.hailweather
{
  background: url(http://i.imgur.com/PweinWm.gif) scroll left top;
}

.sandstormweather
{
  background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
}

.pseudoweather
{
  background: url(http://play.pokemonshowdown.com/fx/weather-trickroom.png) scroll left top;
}
  
  /**********************
  * Battle Weather ~end *
  **********************/
  
.messagebar
{
  z-index: 10;
}

.foehint div
{
  z-index: 11;
}

.battle-controls
{
  background: rgba(51,51,51,0.7);
}

.whatdo .healthy
{
  position: absolute;
  right: 8px;
}

.movecontrols
{
  float: left;
}

.movemenu
{
  width: 302px;
}

.movemenu button
{
  width: 150px;
  height: 50px;
  border-radius: 25px;
  border-width: 0;
  box-shadow: none;
