}

.switchmenu button .pokemonicon
{
  margin: -4px 10px;
}

.switchmenu button .hpbar, .switchmenu button .status
{
  display: none;
}

.switchselect ~ .switchmenu button, .switchselect ~ .switchmenu button:hover, .switchselect ~ .switchmenu button:disabled
{
