css:
@-moz-document domain("papajohns.com") {
section.nav-main nav {
    background-color: #077954;
}

.slider-overflow {
    background-color: black;
}

div.page-wrapper main {
    background-color: #077954;
}

div.card-details p, .fit-grid>*, .points-info p, .anchor-new, div#redeemables p, .highlight+p, section:nth-child(5) > a, .account p, .account label, .account legend, .account strong, p.nutritional-info, a.default-link {
    color: #00ff17;
}

.hero-card, div.banner-card.spacing-bottom-med, div.card-details, .offer-card, rwd-deal-builder .deal-builder, .panel--add-to-cart {
    background-color: #077954;
}

.banner.banner--grey {
    background-color: #ab131b;
    color: #ffffff;
}

.site-footer {
    border-top: 2px solid #077954 !important;
    padding: 2vh 0 4vh;
    font-size: 14px;
    background-color: #ababab;
}

.container.spacing-top-sm.spacing-bottom-med, div.page-wrapper, .container.ng-trigger.ng-trigger-collapseVertical {
    background-color: black;
}

header.panel-header, header.deal-builder-header, .menu-card .card-details, p.legal.nutritional-info {
    background-color: #077954;
    color: #31ff03;
}

.highlight[data-type="success"] {
    background-color: #ab131b;
}

th.h4-new {
    background-color: #b10b14;
}

span.icon-warning.left {
    color: #cc040e;
}

.option-picker input[type="radio"]:checked+label {
    border-color: #ab131b;
}

.next-step--wrapper {
    margin-bottom: 0;
    padding: 0.75em;
    background: #ab131b;
    border-top: 2px solid #E6E6E6;
    text-align: center;
}
}
