css:
@-moz-document domain("intra.42.fr")
{
body {background: none; background-color: #1d1d1d}
.login-container {background-color: #171717;padding: 20px;}
.scale-question-answers {background: none;}
.marked-title {color: #D9D9D9;}
.container-fullsize, body, .changelogs-list .changelog-item .changelog-left span, .broadcasts-filters-btn, .fc-event.closed, .scale-div{background-color: #121212}
.broadcasts-filters-btn:hover {color: #FFFFFF}
h1.nav-title {color: #00BABC;}
.list-group-item, .broad-content {background-color: #242424;border:none;}
h1, h2, h3, h4, h5, .profile-title, .content-style, .broadcasts-filters-btn, .fc-center h2, .quests-list .quests-header h4, .quest-wrapper.main .quest-item-header, .quest-child-item span.ongoing, .team-item .team-content .team-title,.team-item .team-content .team-notice, .main-title, .page-header, .team-item .team-content .team-lock-and-close, .changelogs-list .changelog-item .changelog-left span, .video-item-footer .video-item-views,.current-article, .main-navbar, .app-sidebar-right, .main-navbar .main-navbar-search .searches-title, .light-title
{color: #D9D9D9;}
text > tspan
{color: #D9D9D9!important;fill:#D9D9D9!important; text-shadow: none;font-size: 1.2em}
.search-item.search-user-item {border:none; border-radius:0;}
.search-item.search-video-item .search-item-main, .search-item.search-user-item .search-item-main {background-color: #333;border-radius:0;}
.search-item.search-video-item, .search-item.search-pdf-item {border-radius: 0; border: 1px solid #333;border-radius:0;}
.tt-menu {background-color: #171717;}

.main-left-navbar,.main-navbar .main-navbar-logo, #broadcasts-container, .container-breadcrumb .breadcrumb, .articles-routes, .profile-item .profile-item-top.home-banner .profile-right-box {background-color: #1D1D1D;}
.container-item-nav-tab .tab-content, .container-item-nav-tab .nav-tabs {background-color: #1D1D1D; border: none;}
.container-item-nav-tab .nav-tabs > li.active > a, .container-item-nav-tab .nav-tabs > li.active > a:hover, .container-item-nav-tab .nav-tabs > li.active > a:focus {background-color: transparent;border: none;}
.app-sidebar-left {background-color: #171717;}
.main-navbar, .app-sidebar-right {background-color: #1D1D1D;color: #D9D9D9;}
label {color: #D9D9D9}

.pagination > .active > a, .pagination > .active > a:hover, .pagination > .active > a:focus, .pagination > .active > span, .pagination > .active > span:hover, .pagination > .active > span:focus {background-color: #1d1d1d; border:1px solid rgba(255, 255, 255, 0.6);}
.pagination > li > a, .pagination > li > span {border: 1px solid rgba(255, 255, 255, 0.6); color: #d9d9d9;background-color: #333}
#sidebarCollapse {border: none;margin-top: 10px;background-color: #1d1d1d;border: 1px solid rgba(255, 255, 255, 0.6);}
#iconbroad {background: none;}


#calendar {
	min-width: 80vw;
	min-height: 85vh
}
.fc-time-grid-event .fc-bg {
    background: none;
}
.fc-today {
	background-color: #242728;
}
.fc-time-grid-event,
.fc-bg,
.fc-time-grid-event {
	background: none;
	opacity: 0.9!important;
	color: #afafaf;
}
.fc-body tr,
.fc-body tr td {
	border-bottom: 1px solid #2d2d2d !important;
}
.closed > .fc-bg,
.fc-event.closed {
	background: none;
	background-color: #C62828 !important;
	opacity: 0.1!important;
}
.reserved > .fc-bg,
.fc-event.reserved {
	background: none;
	background-color: #EF6C00 !important;
}
.fc-event.fc-resizable {
	background: none;
	background-color: #00babc !important;
}
.fc-prev-button,
.fc-next-button {
	background: #2d2d2d;
	box-shadow: none;
}
.fc-scroller[style] {
	overflow-x: hidden !important;
	overflow-y: hidden !important;
	min-height: 1447px !important;
	height: 120% !important;
}
.fc-today {
	background-color: #242728;
}

.fc-time > span {color: #D9D9D9!important;}
.fc-unthemed, .fc-event, .fc-event:hover, .ui-widget .fc-event {color: #D9D9D9;}
.fc-event, .fc-event:hover, .ui-widget .fc-event {background-color: black;border: 1px solid black;border-radius: 0;}
.fc-slats td {border-color: #333;}

.container-fullsize .container-inner-item, .container-item { background: #1D1D1D;;border: none; border-radius: 0; color: #D9D9D9}

.notion-grid > .notion-item, .notion-grid > .subnotion-item, .subnotion-grid > .notion-item, .subnotion-grid > .subnotion-item {background-color: #1D1D1D; border: 1px solid rgba(255, 255, 255, 0.6);; border-radius: 0; color: #D9D9D9}
.notion-grid > .notion-item .notion-title, .notion-grid > .notion-item .subnotion-title, .notion-grid > .subnotion-item .notion-title, .notion-grid > .subnotion-item .subnotion-title, .subnotion-grid > .notion-item .notion-title, .subnotion-grid > .notion-item .subnotion-title, .subnotion-grid > .subnotion-item .notion-title, .subnotion-grid > .subnotion-item .subnotion-title {background-color: #1d1d1d;}
.notion-grid > .notion-item .subnotions-list .subnotion-item:not(:first-child), .notion-grid > .subnotion-item .subnotions-list .subnotion-item:not(:first-child), .subnotion-grid > .notion-item .subnotions-list .subnotion-item:not(:first-child), .subnotion-grid > .subnotion-item .subnotions-list .subnotion-item:not(:first-child) {border-top: none;}
.notion-grid > .notion-item .subnotions-list, .notion-grid > .subnotion-item .subnotions-list, .subnotion-grid > .notion-item .subnotions-list, .subnotion-grid > .subnotion-item .subnotions-list {border-top: none;}
/*
Merci GitHub
*/
.profile-banner {background-image:none!important;background-color: #121212}
.profile-item .user-header-box {background-color: #1D1D1D;border-radius: 0;}
.profile-item .user-primary .user-infos .user-infos-sub {background-color: #1d1d1d;}
.profile-item .user-header-box.infos {background: none;}

g > text[transform] {
    fill: #D9D9D9!important;
}
.quest-status {
      border: solid 3px #e5e2e2!important;
}
.quest-child-item span.quest-child-status {
      border: solid 2px #e5e2e2!important;
}
g[data-original-title] > text {
    fill: white!important;
}
.container-fullsize .container-inner-item .overflowable-item {
    overflow-anchor: none;
	overflow-x: hidden;
}

g[data-original-title] > rect {
  stroke-width: 0.6!important;
  stroke-linecap: butt!important;
  stroke-dasharray: 0!important;
}
g[data-original-title='0h00'] > rect, g[data-original-title] > rect[fill='#fafafa'] {
    fill: #2f2c2ecc!important;
	stroke: transparent!important;
}
g[data-original-title='Wrong data sent. Contact your pedagogic team.'] > rect {
	fill: #d92222f0;
    stroke: transparent;
}
g[data-original-title] > rect {
	stroke: transparent;
}
g > g > rect {
    fill: transparent!important;
    stroke: transparent;
}
/*
End Merci Github
*/

.achievement-item .achievement-item--container {border: 1px solid #333;border-radius: 0;}
.achievement-item.stack .achievement-item--container {border-left: 1px solid #333; border-bottom: 1px solid #333;border-right: double 4px #ddd;}
.achievement-item.stacked .achievement-item--container {background-color: #131313;}
.tab-pane .achievement-item--container[data-done="true"], .container-inner-item.boxed .achievement-item--container {background-color: #2D2D2D;color: #D9D9D9}
.tab-pane .achievement-item--container[data-done="true"], .container-inner-item.boxed .achievement-item--container {border: none;border-radius: 0;}
.achievement-item .achievement-item--container .achievement-item--content h1 {color: #D9D9D9}
.achievement-item .achievement-item--container .achievement-item--image {background: none;}

.school-record-button, .broadcast-footer {background-color: #333;}
.correction-item .corrected-item .correction-comment-item {background-color: #171717;}
.table-list li.table-item:hover, .projects-list-container .projects-list--list-header .project-item:hover, .projects-list-container .projects-list--list .project-item:hover,
.changelogs-list .changelog-item .changelog-main:hover, .expertise-list .expertise-list-item:hover, .expertises-user-list .expertises-user-list-header:hover, .expertises-user-list .expertises-user-list-item:hover, .quest-wrapper.main .quest-item-header:hover, .quest-wrapper.validated .quest-item-header:hover, .quest-wrapper.ongoing .quest-item-header:hover
{background-color: #333;}
.quest-child-item {background:none;}

.modal-open .modal {background-color: rgba(51, 51, 51, 0.8)}
.modal-footer, .modal-body {background-color: #242424;color: #D9D9D9;}
}
