@-moz-document domain('timetable.fit.cvut.cz') {
    :root {
        --dark-bg-1: #121212;
        --dark-bg-2: #212121;
        --dark-bg-3: #303030;
        --text-fullwhite: #ffffff;
        --text-1: rgba(255, 255, 255, .87);
        --text-2: rgba(255, 255, 255, .60);
        --text-3: rgba(255, 255, 255, .38);
    }
    body {
        background: var(--dark-bg-1);
    }
    p,
    .day-name,
    .footer a,
    .footer,
    .switch-label {
        color: var(--text-1);
    }
    #fittable .table .day:nth-child(odd),
    input {
        background: var(--dark-bg-2);
    }
    #fittable .table .events .event .detail,
    .switch label {
        background: var(--dark-bg-3);
    }
    #fittable .table .events .event .detail span,
    #fittable .table .events .event .detail i,
    #fittable .table .events .event .detail .value {
        color: var(--text-1);
    }
    #fittable .table .day:nth-child(even),
    #fittable .settings-toggle-btn {
        background: var(--dark-bg-3);
        color: var(--text-1);
    }
}

@-moz-document domain('courses.fit.cvut.cz') {
    :root {
        --dark-bg-1: rgb(18, 18, 18);
        --dark-bg-2: rgb(33, 33, 33);
        --dark-bg-3: rgb(48, 48, 48);
        --text-fullwhite: #ffffff;
        --text-1: rgba(255, 255, 255, .87);
        --text-2: rgba(255, 255, 255, .60);
        --text-3: rgba(255, 255, 255, .38);
        --text-orange: rgba(255, 165, 0, .87);
        --panel-orange: rgb(255, 200, 100);
        --panel-blue: rgb(0, 200, 255);
        --panel-green: rgb(167, 255, 127);
    }
    body,
    .App,
    header,
    .Footer,
    .page-footer,
    .navbar,
    .navbar .navbar-header .navbar-brand,
    .navbar .collapse ul.nav.navbar-nav.navbar-right a {
        background: var(--dark-bg-1);
        color: var(--text-1);
    }
    body {
        border-top: 3px solid var(--text-orange);
    }
    .header {
        background: none;
    }
    img:hover {
        background: white;
    }
    .heading-group .course-heading,
    .toc h2 {
        color: var(--text-1);
    }
    a,
    .menubar ul > li > ul a {
        color: var(--text-orange)
    }
    .button.button-light {
        color: var(--text-1);
        border-color: var(--text-1);
    }
    .button:not(.button-light):hover,
    .Settings dt {
        color: var(--text-2)
    }
    .UserMenu,
    .UserMenu .user-menu ul,
    .UserMenu .user-menu ul button,
    .Upcoming .event,
    .App.in-search,
    .app-menu li.user,
    .app-menu .username,
    .app-menu li.user.dropdown:hover,
    .app-menu li.user.dropdown:active,
    .panel,
    .list-group .list-group-item,
    .Settings {
        background: var(--dark-bg-2);
        color: var(--text-1);
    }
    .panel .panel-heading,
    .alert {
        color: var(--dark-bg-2)
    }
    .panel-default,
    .panel-default .panel-heading,
    .alert-default {
        border-color: var(--text-2)
    }
    .panel-default .panel-heading,
    .alert-default {
        background-color: var(--text-2)
    }
    .panel-warning,
    .panel-warning .panel-heading,
    .alert-warning {
        border-color: var(--panel-orange);
    }
    .panel-warning .panel-heading,
    .alert-warning {
        background-color: var(--panel-orange);
    }
    .panel-info,
    .panel-info .panel-heading,
    .alert-info {
        border-color: var(--panel-blue);
    }
    .panel-info .panel-heading,
    .alert-info {
        background-color: var(--panel-blue);
    }
    .panel-success,
    .panel-success .panel-heading,
    .alert-success {
        border-color: var(--panel-green);
    }
    .panel-success .panel-heading,
    .alert-success {
        background-color: var(--panel-green);
    }
    .CoursesItem .tags li,
    .CoursesItem .code,
    .menubar ul > li > ul,
    .toc,
    .proof .well {
        background: var(--dark-bg-3);
        color: var(--text-2)
    }
    .course-semester ul {
        border-top: 1em solid var(--dark-bg-3);
    }
    .App.in-search .CoursesItem .code,
    .App.in-search .CoursesItem:hover .code {
        background: none;
    }
    .admonition-block,
    pre,
    pre code {
        background-color: var(--dark-bg-2);
    }
    .Heading,
    .Heading h1,
    .list .CoursesItem .column-info a,
    .callout-list li {
        color: var(--text-1)
    }
    .list .CoursesItem .column-info a:hover,
    .list .CoursesItem:hover,
    .search-results.search-field-focused .CoursesItem.link:first-child .column-info > a {
        background: var(--dark-bg-3)
    }
    .Upcoming,
    .Upcoming .event .time,
    .Upcoming .event .location,
    .CoursesItem .url,
    .CoursesItemDetailed .column-grades li .description {
        color: var(--text-2);
    }
    .CoursesItem:hover .code,
    .CoursesItem:hover .tags li {
        background: var(--dark-bg-2)
    }
    .list.shortened .CoursesItem .code {
        background: none;
    }
    .UserMenu .user-menu ul li:not(:last-child)::after,
    .CoursesItem {
        border-bottom: 1px solid var(--text-2);
    }
    .UserMenu .user-menu .user-card,
    .popover {
        background: var(--dark-bg-3);
        color: var(--text-2);
    }
    .Courses .list.shortened::before {
        background: linear-gradient(0deg, rgba(18, 18, 18, 1) 0%, rgba(18, 18, 18, 0) 100%)
    }
    .table tr {
        background-color: var(--dark-bg-2);
    }
    .table tr.odd {
        background-color: var(--dark-bg-3);
    }
    .table tr.header,
    .table-hover > tbody > tr:hover {
        background-color: var(--dark-bg-1);
    }
    code,
    .admonition-block code {
        background: var(--dark-bg-3);
        color: var(--text-1);
    }
    .slides {
        background: var(--dark-bg-1);
        color: var(--text-2)
    }
    .slides h1,
    .slides h2,
    .slides h3,
    .slides h4 {
        color: var(--text-1);
    }
}
