css:
@-moz-document domain("cw.fel.cvut.cz") {
body {
	background-color: #3c3f41;
}

body,
.content h1,
.content h2,
.content h3,
.content h4,
.content h5,
.content h6,
strong {
	color: #f7f7f7
}

a,
.breadcrumb a,
#aside-sidebar a,
.breadcrumb li.is-active a{
	color: #a0d3ff;
}

a strong {
	color: currentColor;
}

a:hover,
.breadcrumb a:hover {
	color: #40aabd;
}

pre {
	background-color: #2b2b2b;
	color: #a9b7c6
}

code {
	background-color: #2b2b2b;
	color: #12bbbb
}

.code .co3,
.code .br0,
.code .me1,
.code .me2,
.code .nu0,
.code .kw3,
.code .kw7,
.code .sy2,
.code .sy3,
.code .sy4 {
	color: #a9b7c6;
}

.code .st0 {
	color: #71a553;
}

.code .kw1,
.code .kw2,
.code .kw4,
.code .es0,
.code .es1,
.code .esHARD {
	color: #CC7832;
}

.code .co2,
.code .sy1 {
	color: #008080;
}

.code .re5 {
	color: #ff399c;
}

table {
	background-color: #2b2b2b;
}

.table {
	background-color: #3C3F41;
}

table,
.table,
.content table thead td,
.content table thead th {
	color: #f9f9f9;
}

.content table th,
.content table thead td,
.content table thead th {
	color: #f9f9f9;
}

.table.is-hoverable.is-striped tbody tr:not(.is-selected):hover {
	background-color: #8c9398;
}

.table.is-striped tbody tr:not(.is-selected):nth-child(even) {
	background-color: #3c3f41;
}

.table.is-striped tbody tr:not(.is-selected):nth-child(odd) {
	background-color: #2b2b2b;
}

#dw__toc {
	background-color: #2b2b2b;
}

.dokuwiki form.changes li .sizechange {
	color: black;
}

.menu-label {
	color: #dedede;
}

.navbar.is-link {
	background-color: #2a3138;
}

.navbar-dropdown {
	background-color: #2a3138;
}

.navbar-item,
.navbar-link,
.navbar-dropdown > a {
	color: #ffffff !important;
}

.menu-list a:hover,
.navbar.is-link .navbar-brand > a.navbar-item:hover,
.navbar.is-link .navbar-end .navbar-link:hover,
.navbar.is-link .navbar-end .navbar-link.is-active,
.navbar.is-link .navbar-item.has-dropdown:hover .navbar-link,
.navbar-dropdown a.navbar-item:hover {
	background-color: #8c9398;
}

.navbar-item.is-active:hover,
.navbar-item.is-active,
.menu-list a.is-active:hover,
.menu-list a.is-active {
	background-color: #80a5c5 !important;
	color: #ffffff !important;
}

.navbar.is-link .navbar-dropdown a.navbar-item.is-active {
	background-color: #80a5c5;
}

.navbar-link {
	background-color: #8c9398;
	background-color: #2a3138;
}

.navbar-link:hover {
	background-color: #8c9398;
}

.message {
	background-color: #2b2b2b !important;
}

.message-body {
	color: #dfdfdf !important;
}

.message,
.noteimportant,
.noteclassic,
.notetip,
.notewarning {
	color: #2b2b2b !important;
}

.noteclassic > a,
.notetip > a,
.noteimportant > a,
.notewarning > a {
	color: #1f39fd !important;
}
}
