.btn-secondary:active, .btn-secondary.active, .show > .btn-secondary.dropdown-toggle {
    color: white;
    background-color: #191829;
    background-image: none;
    border-color: #191829;
}

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: 0.5rem 0;
    margin: 0.125rem 0 0;
    font-size: 1rem;
    color: white;
    text-align: left;
    list-style: none;
    background-color: #1a1927;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #191829;
    border-radius: 0.25rem;
}

.dropdown-item {
    display: block;
    width: 100%;
    padding: 3px 1.5rem;
    clear: both;
    font-weight: normal;
    color: white;
    text-align: inherit;
    white-space: nowrap;
    background: #1a1927;
    border: 0;
    
}

.btn-success {
	background: #191829 !important;
	padding:10px 10px 10px 10px;
