@-moz-document domain("websitetips.com") {

 /* Scrollbar Width _____________________________________________________________________*/

::-webkit-scrollbar {
 	width: 1em!important;
 }

 /* Scrollbar Face Color _____________________________________________________________________*/

::-webkit-scrollbar-thumb
{
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, .95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

::-webkit-scrollbar-thumb:hover
{
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Scrollbar Track Color _____________________________________________________________________*/

::-webkit-scrollbar-track
{
 	/* Color of track */
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(53, 2, 2, .93), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Scrollbar Button Color _____________________________________________________________________*/

::-webkit-scrollbar-button
{
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-button:hover
{
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgb(15, 249, 3), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

