css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("electro-torrent.pl") {
  
body
{
    font-family: "Open Sans",sans-serif !important;
    background-color: #212529;
}

#baner-background, #menu_horizontal
{
    background-image: none !important;
    border: none !important;
}


#main 
{
    background-image: none !important;
    background-color: none !important;
    border: none !important;
}

#right-button-top-right, #center-side #right-button-top-2, #left-button-top, #kategoria-gatunek-1, #button_najczesciej_pobierane, #button_popularne, #button_przydatne, #button_pomocne, td.stats-title, #center-right-side #right-button-top-2, td.colhead-2, td.komentarz-torrenta-top
{
    background-image: none !important;
    background-color: #293139 !important;
}

#menu li a.menu_vertical{background-image: none !important}

#baner-shadow{margin-top: -208px}
#baner{margin-top: 28px;}


/* ..............................................Podmenu.................................................. */

.gatunki_menu
{
    background-color: #232930 !important;
    background-image: none !important;
    border-top: 1px solid #929292 !important;
    border-left: 1px solid #929292 !important;
    border-right: 1px solid #929292 !important;
    border-bottom: 1px solid #929292 !important;
    border-radius: 3px;
    -webkit-box-shadow: none !important;
}


/* ..............................................Zmiana Koloru.................................................. */

#premiery-all, #center-side td.main-2, #right-content-right-statystyki, #right-content-right, #left-panel-kanal, #left-panel, #premiery-all, div#premiery-all-2, #sms_info, td.logowanie, #left-menu, #premiery-seed-leech, #center-side td.main-2, #staty1, #najczesciej_pobierane, #popularne, #przydatne, #pomocne, #center-right-side td.main-2, #forum-main-2, #center-side table.main-2, #podobne_torrenty, .komentarze_box_all, .komentarz-torrenta, #staff_all, #wybrane_gatunki, #wszystkie-torrenty-warezy-1-button, #wszystkie-torrenty-warezy-2-button, #wszystkie-torrenty-warezy-3-button, #news, td.statybackground, #userdetails_main, #forum-main, #forum-nicki, ul.pagination li a.not-pager
{
    background-color: #232930 !important;
    background-image: none !important;
    border-top: 0px !important;
    border-left: 1px solid #293139 !important;
    border-right: 1px solid #293139 !important;
    border-bottom: 1px solid #293139 !important;
}

.arrow_coments_reply, .reply_coment, .komentarz-torrenta
{
    background-color: #313942 !important;
    background-image: none !important;
    border-top: 0px !important;
    border-left: 1px solid #293139 !important;
    border-right: 1px solid #293139 !important;
    border-bottom: 1px solid #293139 !important;
 
}



 /*usuwanie dolnych ramek */

#left-button-bottom-menu, #center-side #right-button-bottom, #right-button-bottom-right, #left-button-bottom
{
    background-image: none !important;
}


/* ..............................................MENU.................................................. */

.menu_horizontal
{
  width:1190px;
  margin-left:16px !important;
  list-style: none;
  margin: 0;
  padding: 0;
  width: 100%;
  margin: auto;
  position: relative;
  background-color: #2c3e50;
}

.menu_horizontal ul
{
background-image: none !important;
border: solid #ffffff 0px;
background: #293139;
}

.menu_horizontal a {
  color:#bbb;
  text-transform: uppercase;
  transition: all 0.2s linear;
}

.menu_horizontal li a:hover, .menu_horizontal ul li:hover a
{
background-image: none !important;
background: #293139; 
}

/* ..............................................Forum.................................................. */


.infobox{
    background: #293139 !important;
    background-image: none !important;
}
}
