css:
@-moz-document domain("translate.google.es"), domain("translate.google.com.pe") {
/* scrollbar */
::-webkit-scrollbar{
    width: 14px;
    border-radius: 1px;
    border: 1px solid hsl(0, 0%, 0%);
    background: #101010;
}
::-webkit-scrollbar-thumb{
    border-radius: 1px;
    border: 1px solid hsl(0, 0%, 0%);
    background-color: #161515;
}
::-webkit-scrollbar-thumb:active {
    background: #191818;
    border: 1px solid #000
}
/*-----------------------------*/

   /* menu desplegable */

.gb_Nc {
    background: #0d0d0d;
    box-shadow: 0 0 19px #000000 !important;
    border-bottom: 10px solid rgb(11, 11, 11) !important;
}
.gb_Mc:not(:last-child) {
    background: #101010;
    border-bottom: 1px solid #1a73e8;
}    
ul.gb_Mc {
    background: #131313;
}
.gb_Kc {
    background: #131313;
}

.gb_Pc, .gb_Qc {
    display: block;
    min-height: 40px;
    padding-bottom: 0px;
    padding-top: 2px;
    font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif;
    color: rgba(0,0,0,0.87);
}

span.gb_Tc {
    color: #5f6368;
}

/*---------------------------------*/

/* menu aplicaiones */


ul.gb_H.gb_y {
    background: #0d0d0d;
}
a.gb_I.gb_cg {
    background: #101010;
    border-top: 1px solid black;
}
ul.gb_H.gb_z {
    background: #0d0d0d;
}
.gb_E.gb_D.gb_U.gb_F.gb_Ec {
    border: 1px solid black;
}
.gb_i:not(.gb_j):hover .gb_r {
    background: #101010;
    border-top: 1px solid #222;
}
.gb_i:hover a {
    border: 1px solid #222;
    border-radius: 2px;
}
.gb_J {
    border-bottom: 1px solid #3c4043;
    left: 28px;
    width: 264px;
}
.gb_r {
    color: #5f6368;
}
span.gb_J {
    border-bottom: 1px solid #3c4043;
    left: 28px;
    width: 264px;
}
.gb_J {
    left: 0px;
    width: 305px;
}
a.gb_4f.gb_J {
    border-top: 1px solid #000;
}
#gb a.gb_I, #gb a.gb_I:visited, #gb a.gb_I:active, #gb a.gb_J, #gb a.gb_J:visited {
    color: #1a73e8;
    text-decoration: none;
}
.gb_D {
    background: #0d0d0d;
}

/*---------------------------*/

/* barra de titulo */

.gb_ja .gb_Nd.gb_Ud.gb_Vd {
    background: #0d0d0d;
    min-width: 0;
}
.gb_ja .gb_Qd.gb_Xd.gb_Zd {
    background: #0d0d0d;
    min-width: 0;
}
#gb {
    box-shadow: 0 0 15px #000000 !important;
    border-bottom: 1px solid rgb(11, 11, 11) !important;
}
.gb_na svg, .gb_Dc svg, .gb_6c .gb_ce, .gb_Qc .gb_ce {
    color: #9da09b;
    opacity: 1;
}

/*-----------------------------*/

/*lista lenguaje */


#sl_list-search-box, #tl_list-search-box {
    border-bottom: 1px solid rgba(255,255,255,.18) !important;
    border-radius: 9px !important;
    box-shadow: inset 0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: #070707 !important;
    width: 100%;
}
.language-list-search-box {
    background: #0d0d0d;
}
.language-list {
    background-color: #0d0d0d;
    padding-top: 0;
    padding-left: 24px;
}
.language_list_sl_list, .language_list_tl_list {
    background-color: #0d0d0d;
    border: 1px solid rgba(3, 3, 3, .82);
    box-shadow: 0 0 15px #000 !important;
}
.language_list_section {
    width: 100%;
    background: #1a73e8;
}
.language_list_item_wrapper {
    box-sizing: border-box;
    break-inside: avoid-column;
    font-size: 15px;
    page-break-inside: avoid;
    white-space: nowrap;
    width: 100%;
    background: #0d0d0d;
}
.language_list_item_language_name {
    color: #9E9E9E;
}
.language_list_item_wrapper:hover, .language_list_item_wrapper:focus {
    background-color: #121212;
}
.language_list_item_wrapper.item-selected {
    background-color: #121212;
}
/*---------------------------*/

.frame {
    height: calc(100vh - 65px);
    background: #101010 !important;
}

.homepage.translate-text .input-button.text-icon, .homepage.translate-docs .input-button.documents-icon {
    background-color: #101010;
     border: 1px solid #000;
    box-shadow: 0 0 15px #000000 !important;
}
.header-button {
    border: 1px solid #000;
    box-shadow: 0 0 15px #000000 !important;
    border-radius: 4px;
    box-sizing: border-box;
    color: #1a73e8;
    cursor: pointer;
    display: inline-block;
    font-family: 'Google Sans',sans-serif;
    font-size: 15px;
    font-weight: 500;
    height: 36px;
    padding-left: 40px;
    padding-right: 16px;
    position: relative;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-user-select: none;
}
.main-header {
    border: 1px solid rgba(3, 3, 3, .82);
    box-shadow: 0 0 15px #000 !important;
}

.orig, .input-area.less-padding .text-dummy {
    padding-right: 54px;
    border-top: 3px solid rgba(0, 0, 0, .62);
}

body:not(.with-lang-list) .results-container.empty {
    background-color: #0d0d0d;
    border-top: 3px solid rgba(0, 0, 0, .42);
    border-left: 1px solid rgba(0, 0, 0, .47);
    box-shadow: none;
    display: block;
}
.ls-wrap {
    border-top: none;
    border-bottom: none;
    background-color: #f000;
    box-sizing: border-box;
    height: 48px;
}
.sl-wrap, .tl-wrap {
    background: #10101000;
}
.ls-left-arrow, .ls-right-arrow {
    background: transparent;
    height: 45px;
    width: 0px;
    z-index: 2;
    position: absolute;
    top: 0;
}
.ls-wrap .sugg-fade{
    background:#101010 !important;
}
.swap-wrap {
    background-color: #101010 !important;
    display: inline-block;
    margin: 0 -25px;
    vertical-align: top;
}
.swap.jfk-button-disabled {
    background-color: #101010 !important;
} 
.sl-sugg .goog-inline-block.jfk-button, .tl-sugg .goog-inline-block.jfk-button {
    background-color: #101010 !important;
    background-image: none;
    border: none;
    box-shadow: none;
    box-sizing: border-box;
    color: rgb(175, 177, 176);
    font-size: 14px;
    font-weight: 500;
    height: 46px;
    line-height: 48px;
    margin-left: 0;
    padding: 0 24px;
    text-transform: uppercase;
    z-index: 0;
    position: relative;
    overflow: hidden;
}
.swap.jfk-button {
    background-image: none!important;
    border: none!important;
    background-color: #757575 !important;
    background-size: 0 0;
    height: 46px;
    margin-right: 0;
    line-height: 48px;
    outline: none!important;
    box-shadow: none!important;
    cursor: pointer;
    width: 50px;
    border-radius: 25px;
    transition: all .4s;
}
.orig {
    background-color: #0d0d0d;
}
.source-wrap {
    background-color: #0d0d0d;
    display: block;
    flex-grow: 1;
    z-index: 1;
    padding-bottom: 56px;
}
.source-input {
    background-color: rgba(0, 0, 0, .46);
    
}
.input-area.less-padding .orig, .input-area.less-padding .gt-hl-layer {
    color: rgba(144, 144, 144, 0.87);;
    padding-right: 44px;
}
.source-or-target-footer-button {
    display: inline-block;
    height: 42px;
    width: 45px;
    background: #757575;
    border-radius: 100%;
    margin-top: 5px;
}
.speech-button {
    margin-top: 5px;
    margin-left: 4px;
}
.src-tts .jfk-button-img {
    margin-top: 10px;
    margin-left: 10px;
}
.result-footer .jfk-button-img {
    margin-top: 9px;
    margin-left: 9px;
}

.results-container, .result, .gt-edit, .contribute-target {
    background-color: #0d0d0d;
    border-top: 2px solid rgba(0, 0, 0, .62);
} 
.results-container:not(.empty) .placeholder, .result-dict-wrapper .translation, .contribute-target, #st-buttons .jfk-button {
    color: rgba(144, 144, 144, 0.87);
}
-lc.gt-lc-mobile .gt-cd {
    background-color: #0d0d0d;
    border: 1px solid #000;
    box-shadow: 0 0 15px #000000 !important;
    margin-bottom: 8px;
    padding-top: 16px;
    padding-bottom: 8px;
}
.gt-def-row {
    color: darkred;
    unicode-bidi: inherit;
}

.source-or-target-footer {
    border-top: none;
    left: 14px;
    width: calc(100% - 22px);
}
.jfk-button-flat {
    font-size: 13px;
    font-weight: normal;
    line-height: 21px;
    margin-right: 1px;
    min-width: 0;
    margin-top: 5px;
    padding: 0;
}

.vk-box {
    height: auto;
    padding: 10px;
    background-color: #0d0d0d;
    border: 1px solid #000;
    box-shadow: 0 0 15px #000000 !important;
    z-index: 2147483644;
    font-family: arial,sans-serif;
    font-size: 14px;
    position: fixed;
}
.vk-btn {
    border: 1px solid rgba(0,0,0,0.1);
    width: 29px;
    height: 29px;
    margin: 2px;
    text-align: center;
    vertical-align: middle;
    position: relative;
    padding: 1px;
    min-width: 0px;
    max-width: 500px;
    min-height: 0px;
    max-height: 50px;
    color: #000;
    background-image: -webkit-linear-gradient(top, #dfdfdf,#373737);
    -webkit-border-radius: 2px;
}
.vk-btn.vk-sf-h {
    border-color: #c6c6c6;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#d1d1d1),to(#292929));
    box-shadow: 0px 1px 1px rgba(0,0,0,0.1);
}
.vk-btn.vk-sf-a {
    border-color: #c6c6c6;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#f00),to(#f00));
    box-shadow: inset 0px 1px 1px rgba(0,0,0);
}

.gt-spell-correct-message {
    background-color: #101010;
    box-shadow:none;
    border: 1px solid #000;
    color: rgba(229, 229, 229, 0.54);
    font-size: 16px;
    margin-bottom: 8px;
    margin-top: 0;
    padding: 11px 16px;
}

.gt-is-mobile {
    background-color: #0d0d0d;
    border: 2px #000;
    border-style: solid;
    border-width: 1px 0 0;
    border-radius: 4px;
    box-shadow: 0 0 9px #000;
    display: block;
    position: relative;
    margin-bottom: 0;
    margin-top: 6px;
    }
.gt-is-mobile .gt-is-ld {
    border-top: 1px solid #0d0d0d;
}
.gt-is-tr {
    font-size: 14px;
    color: #959595;
    display: block;
    padding-top: 4px;
}
.gt-lc.gt-lc-mobile .gt-cd {
    background-color: #101010;
    border: .6px solid #0d0d0d;
    border-radius: 8px;
    margin-bottom: 8px;
    padding-top: 16px;
    padding-bottom: 8px;
}
.gt-lc.gt-lc-mobile .gt-cc-r-i {
    background-color: #fd000000 !important;
}
.gt-lc.gt-lc-mobile .gt-def-num {
    position: absolute;
    left: 22px;
    border: 1px solid rgb(167, 167, 167);
    border-radius: 100%;
    width: 18px;
    height: 18px;
    line-height: 18px;
    font-size: 14px;
    text-align: center;
    color: rgb(198, 193, 193);
}
.gt-lc.gt-lc-mobile .gt-def-example {
    color: #5f6368;
    margin-top: 4px;
    font-size: 14px;
}
.gt-lc.gt-lc-mobile .gt-ex-text {
    color: #5f6368;
}
.gt-lc.gt-lc-mobile .gt-ex-quote {
    position: absolute;
    left: 22px;
    height: 24px;
    width: 24px;
    opacity: .54;
    background: #5f6368;
    border-radius: 100%;
}
.gt-card-ttl-txt {
    color: #5f6368;
    unicode-bidi: embed;
}
.gt-is-mobile .gt-is-sg {
    font-size: 16px;
    color: #5f6368;
    margin: 0 !important;
    position: absolute;
    right: 48px;
}
.gt-lc.gt-lc-mobile .gt-cd-cl, .gt-lc.gt-lc-mobile .gt-cd-ncl {
    color: #a9a8a8;
    background: #3c4043;
    border: 1px solid #3c4043;
    border-radius: 32px;
    margin-bottom: 6px;
}
.gt-lc.gt-lc-mobile .cd-expand-label {
    color: #8e8d8d;
}
.gt-baf-word-selected {
    background-color: #5f6368;
    color: #5f6368;
}
.gt-baf-previous-word-mobile {
    color: rgba(203, 202, 202, 0.54);
}
.gt-lc-mobile .gt-baf-word-selected .gt-baf-word-clickable {
    color: #a9a9a9;
}
.gt-baf-word-clickable {
    color: #73767b;
    cursor: pointer;
    vertical-align: top;
    white-space: nowrap;
    unicode-bidi: embed;
}
.gt-lc-mobile div.gt-baf-translations {
    color: #5f6368;
    font-size: 14px;
    line-height: 24px;
    margin: 6px 8px 6px 12px;
}
.gt-cd-pos-pop {
    color: #3c4043;
    display: inline-block;
    font-size: 12px;
    font-style: italic;
    letter-spacing: -0.05px;
    margin-right: 12px;
    margin-top: -2px;
}

/*iconos circulares */

.ft-icon-oval {
    background: #101010;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: 32px;
    border: 1px solid #757575;
    border-radius: 50%;
    height: 60px;
    width: 60px;
}

.ft-icon-txt {
    color: #5f6368;
    font-family: 'Google Sans',sans-serif;
    font-weight: 500;
    line-height: 18px;
    margin-top: 8px;
    opacity: .54;
    text-decoration: none!important;
    text-transform: none;
}
/*---------------------------------------*/
}
