css:
@-moz-document url-prefix("https://juejin.im/post/") {
  .global-component-box {
    display: none;
    overflow: hidden;
  }

  .activity-recommend, .main-header-box, .sidebar, .recommended-area, .article-suspended-panel {
    display: none !important;
  }

  .main-container > div:first-of-type {
    display: flex;
    justify-content: center;
  }
  .main-container > div:first-of-type > div {
    display: none;
  }
  .main-container > div:first-of-type > div:first-of-type {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
}
@-moz-document url-prefix("https://juejin.im/entry/") {
  .main-header-box, .global-component-box {
    display: none;
    overflow: hidden;
  }

  .main-container > div {
    display: none;
  }
  .main-container > div:first-of-type {
    display: initial;
  }

  .entry-public-view {
    display: flex;
    justify-content: center;
  }
  .entry-public-view > div {
    display: none;
  }
  .entry-public-view > div:first-of-type {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
}
@-moz-document url-prefix("https://blog.csdn.net/") {
  body > aside {
    display: none;
  }

  #csdn-toolbar {
    display: none;
  }

  #mainBox {
    display: flex;
    justify-content: center;
  }
  #mainBox > div {
    display: none;
  }
  #mainBox > aside {
    display: none;
  }
  #mainBox #article_content {
    height: auto !important;
  }
  #mainBox #article_content .article-copyright {
    display: none;
  }
  #mainBox .hide-article-box {
    display: none;
  }
  #mainBox > main {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  #mainBox > main > .recommend-box {
    display: none;
  }
  #mainBox > main div.t0 {
    display: none;
  }
  #mainBox > .recommend-right {
    display: none;
  }
  #mainBox .recommend-item-box, #mainBox .recommend-loading-box {
    display: none;
  }

  .tool-box, .csdn-side-toolbar {
    display: none !important;
  }
}
@-moz-document regexp("https://[\\w-]+\\.iteye\\.com/blog/.*") {
  #csdn-toolbar, #header {
    display: none;
  }

  #content {
    display: flex;
    justify-content: center;
  }
  #content > div {
    display: none;
  }
  #content #local, #content > .blog-sidebar {
    display: none;
  }
  #content #main {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  #content #main > div.blog_main > div.blog_comment {
    display: none;
  }

  #blog_content {
    height: auto !important;
  }
  #blog_content .hide-article-box {
    display: none !important;
  }
}
@-moz-document url-prefix("https://www.jianshu.com/p/") {
  body > nav {
    display: none;
  }
  body .note-bottom {
    display: none;
  }

  #note-fixed-ad-container, #web-note-ad-1 {
    display: none !important;
  }

  .note {
    display: flex;
    justify-content: center;
  }
  .note > div {
    display: none;
  }
  .note > .post {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  .note > .post .meta-bottom, .note > .post .follow-detail {
    display: none;
  }
}
@-moz-document url-prefix("https://www.zhihu.com/question/") {
  .AdblockBanner {
    display: none;
  }

  header[role=banner] {
    display: none;
  }

  main[role=main] .Question-main > .Question-sideColumn {
    display: none;
  }
  main[role=main] .Question-main .Question-mainColumn {
    width: auto;
  }
}
@-moz-document url-prefix("https://www.cnblogs.com/") {
  #header, #footer {
    display: none;
  }

  #leftmenu {
    display: none;
  }

  #main_content {
    display: flex;
    justify-content: center;
  }
  #main_content > div {
    display: none;
  }
  #main_content #content {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    margin: 0;
  }

  #navigator {
    display: none;
  }

  #main {
    display: flex;
    justify-content: center;
    background: transparent;
  }
  #main > div {
    display: none;
  }
  #main #post_detail, #main #mainContent, #main > div:first-of-type {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    margin: 0;
  }
  #main #post_detail .forFlow, #main #mainContent .forFlow, #main > div:first-of-type .forFlow {
    margin: 0;
  }

  #comment_form {
    display: none;
  }

  #green_channel {
    display: none;
  }
}
@-moz-document url-prefix("https://www.infoq.cn/article/") {
  #layout .layout-header {
    display: none;
  }
  #layout .inner-content {
    padding-top: 0;
  }
  #layout .inner-content > div:last-of-type {
    display: none;
  }
  #layout .inner-content .topic-nav {
    display: none;
  }
  #layout .inner-content .article-fixed-operation {
    display: none;
  }
  #layout .inner-content .layout-content {
    display: flex;
    justify-content: center;
  }
  #layout .inner-content .layout-content > div {
    display: none;
  }
  #layout .inner-content .layout-content .main-content {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  #layout .inner-content .layout-content .main-content .like-section, #layout .inner-content .layout-content .main-content .operation-btn-wrapper, #layout .inner-content .layout-content .main-content .com-topic-tag, #layout .inner-content .layout-content .main-content .article-advertising {
    display: none;
  }
}
@-moz-document url-prefix("https://sspai.com/post/") {
  #header, #footer {
    display: none;
  }

  #app .article-wrapper > div {
    display: none;
  }
  #app .article-wrapper .article-detail {
    display: flex;
    justify-content: center;
  }
  #app .article-wrapper .article-detail > div {
    display: none;
  }
  #app .article-wrapper .article-detail > article {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  #app .article-wrapper .article-detail > article .clock {
    max-width: none;
  }
  #app .article-wrapper .article-detail > article .recommend {
    display: none;
  }
}
@-moz-document url-prefix("https://www.imooc.com/article/") {
  body > div {
    display: none;
  }

  #main {
    display: block;
  }
  #main .main_con {
    display: flex;
    justify-content: center;
  }
  #main .main_con > div {
    display: none;
  }
  #main .main_con .left_essay {
    display: block;
    width: 960px;
    max-width: 100% !important;
    float: none;
    box-sizing: border-box;
    margin: 0;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  }
  #main .main_con .left_essay .article_wrap, #main .main_con .left_essay .praise-box {
    display: none;
  }

  .long-content {
    max-width: none !important;
  }

  .showMore {
    display: none !important;
  }
}
@-moz-document url-prefix("https://my.oschina.net/") {
  #headerNavMenu, #footer, #copyright, #floatMenu {
    display: none;
  }

  .float-menu-content > .author-card, .float-menu-content > .action, .float-menu-content > .breadcrumb, .float-menu-content > .around-articles-wrap {
    display: none;
  }

  .article-list {
    display: none;
  }
}
