css:
@-moz-document url("https://www.bilibili.com/") {
/* 板块屏蔽 */
.first-screen.b-wrap,
#bili_live {
    display: none !important;
}
/*--------*/
/* 广告屏蔽 */
.banner-card.b-wrap {
    display: none !important;
}
/*--------*/
/* 其它屏蔽 */
.mascot,
.try-feedback {
    display: none !important;
}
/*--------*/
/* Header修改 */
.bili-banner {
    display: none !important;
}
.mini-header.m-header {
    position: static !important;
    background: rgba(251, 114, 153, 0.8) !important;
    box-shadow: rgba(0, 0, 0, 0.1) 0 1px 2px;
}
.nav-link .nav-link-ul .nav-link-item .link, 
.nav-user-center .user-con .item .name {
    color: white !important;
    text-shadow: none !important;
}
.international-header .nav-search #nav_searchform {
    background-color: #f4f4f4 !important;
    border: 1px solid #e7e7e7;
}
.international-header .nav-search .nav-search-btn {
    background-color: transparent !important;
}
/*--------*/
/* 导航修改 */
#elevator {
    top: 150px !important;
}
.item.sortable:nth-child(1) {
    display: none !important;
}
.page-tab .con li {
    border-color: transparent !important;
}
/*--------*/
/* 其它修改 */
.rank-wrap .pvc,
.nav-item .i-frame {
    z-index: 2000;
}
.time-line-card .txt .update .published {
    width: auto;
    color: white !important;
    background: #00a1d6 !important;
    border-radius: 10px !important;
    padding: 0 8px !important;
}
/*--------*/
}
