#afterdance .star
{
    background-image: url(https://www.dropbox.com/s/2f3nlxicbcp4f3n/star_glow.png?raw=1) !important;
    transform: rotate(5deg);
}
#afterdance .score.superstar .stars .star, .score__stars.star
{
    background-image: url(https://www.dropbox.com/s/sbi91fyc0ocodpk/star_glow_plus.png?raw=1) !important;
    background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center
}
#afterdance .score.megastar .stars .star, .score__stars.star
{
    background: url(https://www.dropbox.com/s/navd8kyg4apr319/star_glow_plus_more.png?raw=1) !important;
    background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center;
}
#afterdance .flag
{
    display: none;
}
#afterdance .purple .color::before, .player-solo #afterdance .purple .color::before {
    background: linear-gradient(to right, #8607c7 20%, #6d4cd0 100%)!important;
    box-shadow:none;
    transform:none;
}
#afterdance .blue .color::before, .player-solo #afterdance .blue .color::before {
    background: linear-gradient(to right, #4292ff 20%, #6426e7 100%)!important;
    box-shadow:none;
    transform:none;
}
#afterdance .red .color::before, .player-solo #afterdance .red .color::before {
    background: linear-gradient(to right, #ff821d 20%, #f01611 100%)!important;
    box-shadow:none;
    transform:none;
}
#afterdance .green .color::before, .player-solo #afterdance .green .color::before {
    background: linear-gradient(to right, #87e449 20%, #5cb327 100%)!important;
    transform:none;
}
#afterdance .aqua .color::before, .player-solo #afterdance .aqua .color::before {
    background: linear-gradient(to right, #48dd98 20%, #48b5ab 100%)!important;
    box-shadow:none;
    transform:none;
}
#afterdance .pink .color::before, .player-solo #afterdance .pink .color::before {
    background: linear-gradient(to right, #f2149c 20%, #ce1eeb 100%)!important;
    transform:none;
}
#afterdance .players.player-solo .color::before
{
    transform: rotate(0deg) !important;
    width: 100% !important;
}
#afterdance .player-solo .player .score .stars {
    overflow: visible;
    left: 45%;
    top: 8%;
    animation: supel 1.0s infinite!important;
    z-index: 1000;
    transform: scale(0.7)
}
#afterdance .player-solo .score.superstar::after
{
    margin-left: 39.5%;
    margin-top: 1%;
    animation: nopel 1.2s infinite!important;
    z-index: 999999999999;
}
#afterdance .player-solo .score.megastar::after
{
    margin-left: 39.5%;
    margin-top: 1%;
    animation: nopel 1.2s infinite!important;
    z-index: 999999999999;
}
