css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(leo.org) {
  #mainContent >  .fl-left {
    visibility: hidden;
    display: none;
  }
  .card-content, .p-large, .card::after, .card-footer, .card {
    background-color: #3b3b3b;
    color: #ffffff;
  }
  .l-dict-adv, .l-www-adv {
    display: none;
  }
  .l-dict-header, .l-www-header {
    width: 100%;
  }
  html {
    background-color: #222;
    color: white;
  }
	table, td, tr, .section {
  	border: none !important;
  } 
  .ui-widget-content { background-color: #1c1c1c;}
  .bg-lightblue { background-color: #000055;}
  .bg-white { background-color: #333333;}
	article:nth-child(1n).m-bottom-large {
    background-color: #222222 !important;
    color: white;
  }
  .bg-lightyellow {
    color: unset;  
  }
  
  
	.section > footer.more {
    background: #111111;
  }
  
  .bg-darkyellow, .bg-yellow {
    background-color: #111111;
    color: white;
  }
  
  .bg-darkblue {
    background-color: black;
  }
  
  .dropdown > span {
    color: white;
  }
  
  #switchSearchMode{
    color: white;
  }
  
  .tblf-alternate > tbody > tr:nth-child(2n) {
    background-color: #333;
  }
  
  .search {
    background-color: #222;
  }
  
  .isRelinked, .isRelinked b, .isRelinked b a {
    color: #aaa;
  }
  
  .button-raised {
    background-color: #111111;
  }
  
  .section.forumResults table tbody tr:nth-child(4n+2) td, 
  .section.forumResults table tbody tr:nth-child(4n+1) td {
    background-color: #222;
}
  
  .section.forumResults table tbody tr:nth-child(4n+3) td, 
  .section.forumResults table tbody tr:nth-child(4n+4) td {
    background-color: #333;
}
  
  .search input[type="text"], .search input[type="search"], #search-field:active, #search-field:focus {
    background-color: #222;
    color: white;
  }
  
  .tblf-alternate > tbody > tr {
    background-color: #222;
  }
  
  #centerColumn {
    margin-left: 100px !important;
    margin-right: 100px !important;
  }
  
  #searchBar {
    background-color: #111111;

  }
  
  #right {
    display: none !important;
  }
  #mainContent {
    width: 100%;
  }
  
  body {
    max-width: unset !important;
  }
  
  .banner-big {
    display: none;
  }
  
   #topBranding {
    padding-bottom: 2px !important;
     padding-top: 2px !important;
  }
  
  a:link, a:visited, a {
    color: #aaa;
  }
}
