css:
@-moz-document url-prefix("https://news.ycombinator.com") {
.title {
    font-size: 11pt;
}

td.title {
    vertical-align: text-bottom;
}

.subtext {
    font-size: 9pt;
}

/* Large vote arrows. */
.votearrow {
    position: relative;
    top: 0pt;
    width: 18pt;
    height: 16pt;
    background-size: 18pt;
    opacity: 0.5;
    background-position-y: -6pt;
    margin-bottom: -4pt;
}

td.subtext {
    font-size: 10pt;
}
}

@-moz-document url-prefix("https://news.ycombinator.com/item"), url-prefix("https://news.ycombinator.com/submit"), url-prefix("https://news.ycombinator.com/threads"), url-prefix("https://news.ycombinator.com/edit"), url-prefix("https://news.ycombinator.com/reply") {
/* Larger point, narrow font. */
span.c00 {
    font-size: 12pt;
    font-family: Roboto Condensed, DejaVu Sans Condensed, Arial Narrow, sans-serif;
}

div.reply p font u {
    font-size: 10pt;
}

/* Large vote arrows. */
.votearrow {
    position: relative;
    top: -4pt;
    width: 18pt;
    height: 18pt;
    background-size: 18pt;
    opacity: 0.5;
}

/* No width limitation for pre fragments. */
div.comment span pre {
    right: 100% !important;
    font-size: 10pt;
    margin-left: -2em;
    max-width: none;
}

/* Indent own comment like others. */
td.votelinks center img[src="s.gif"] {
    width: 18pt;
}

/* Mark own comment more obviously. */
td.votelinks center font {
    font-size: 16pt;
    font-weight: bold;
}
}
