/* this is cute because i did it */
body {
    background:#313131;
    margin:0 !important;
    padding: 0 !important;
    line-height:21px;
}
a, a:hover, a:focus, a:active {
    color:#727777 !important;
}
#imagecontainer {
    background:url("https://i.imgur.com/EWEv4um.jpg") bottom fixed repeat !important;
    z-index:1;
    height:143px !important;
    filter: sepia(90) hue-rotate(120deg) grayscale(.8);
    margin-bottom:-20px !important;
}
#left, #right {
    width:0px !important;
    display:none !important;
}
.clearfix {
    font-family: 'Roboto Slab','Times New Roman',serif;
}
.cpp-section {
    font-family: 'Roboto Slab','Times New Roman',serif;
    font-size:22px !important;
}
#middle {
    border: solid 4px #3a3e3e !important;
    background:rgb(48,50,49) !important;
    color:#acacac !important;
    font-family: 'Roboto Slab','Times New Roman',serif;
    width:100% !important;
}
#layout {
    table-layout: auto !important;
}
p[style*="display:block;color:#000000;background:#FFFF66"], p[style*="background:#66FFFF"], div.useCode, div.post table th, div.blueBubble, div.greenBubble, span.current, .page-numbers {
    background:#3a3e3e !important;
    color:#edfdffa3 !important;
    border-radius:0px !important;
    border:none !important;
}
.dots {
    background:none !important;
}
#footer {
    background:url("https://i.imgur.com/EWEv4um.jpg") top fixed repeat !important;
    z-index:1;
    filter: sepia(90) hue-rotate(120deg) grayscale(.8);
    color:#acacac !important;
    text-shadow: 0px 1px 2px #212121;
    height:60px !important;
    padding: 5px;
    border:none !important;
    border-top: solid 4px #3a3e3e !important;
    line-height: 15px;
    text-align:right !important;
    width:100% !important;
    margin:0 !important;
}
.clearfix {
    font-family: 'Roboto Slab',serif;
}
div.post-headline h1, div.post-headline h2 {
    font-size:24px !important;
    font-weight:bold !important;
}
.cpp-section {
    font-family: 'Roboto Slab', serif;
    font-size:22px !important;
}
.cpp-topline {
    border-top: solid 2px #3a3e3e;
    padding-top:20px !important;
}
.cpp-bottomline {
    border-bottom: solid 2px #3a3e3e;
}
hr {
    background: #3a3e3e;
}
.cpp-note {
    border: solid 2px #343737 !important;
    background:#2d2f2f !important;
    color:#acacac !important;
    font-family: 'Roboto Slab','Times New Roman',serif;
    border-radius: 0px !important;
    padding:0 20px !important;    
}
img[src*="https://www.learncpp.com/images/CppTutorial/next.png"],img[src*="https://www.learncpp.com/images/CppTutorial/prev.png"],img[src*="https://www.learncpp.com/images/CppTutorial/up.png"] {
    filter: saturate(5) sepia(90) grayscale(.8) invert(0.83) hue-rotate(240deg) ;
    width:32px;
    height:32px;
    background:none !important;
    border:none !important;
    line-height:305px !important;
}
td {
    border: solid 2px #343737 !important;
    background:#2d2f2f !important;
    color:#acacac !important;
    font-family: 'Roboto Slab','Times New Roman',serif;
    border-radius: 0px !important;
    padding-right:20px !important;
}
.commentlist, li.thread-even, .bypostauthor, div.comment-container, .children {
    border: solid 2px #343737 !important;
    background-color:#2d2f2f !important;
    color:#acacac !important;
    font-family: 'Roboto Slab','Times New Roman',serif;
    padding-bottom:15px !important;
    border-radius:0 !important;
}
ul.commentlist li.thread-odd {
    border-bottom:none !important;
}
ul.children div.comment-container {
    border:none !important;
    padding-bottom:0 !important;
}
img.avatar {
    border:none !important;
    border-radius:0; 
    background-color:#3a3e3e !important;
}
#respond {
    border: solid 2px #343737 !important;
    background-color:#2d2f2f !important;
    color:#acacac !important;
    font-family: 'Roboto Slab','Times New Roman',serif;
    padding-bottom:15px !important;
    border-radius:0 !important;
}
input.text, input.textbox, input.password, input.file, input.TextField, textarea,  .contact-form input[type="email"], .contact-form input[type="text"], .contact-form input[type="url"] {
    background:#343737 !important;
    border:solid 2px #3a3e3e;
    color:#acacac !important;
}
.crayon-theme-familiar-learncppcom .crayon-striped-line {
    background: none !important;
}
.crayon-theme-familiar-learncppcom .crayon-table .crayon-nums {
    border-right-color:#3a3e3e !important;
}
.button, .Button, input[type="submit"] {
    padding: 0px 10px;
    background:#3a3e3e !important;
    color:#edfdffa3 !important;
    border-radius:0px !important;
    border:none !important;
}
}
