css:
@-moz-document domain("tutorialrepublic.com") {
body,
.content,
h1,
code,
h2,
h3,
h4,
.syntax,
.shadow,
.codebox-title,
.example,
.menu,
code[class*="language-"],
pre[class*="language-"],
table,
table.data th,
table.data td,
table td:first-child code,
.content code.plain,
.content table.data code.plain,
.inner-area,
.codelab-footer,
.outer-area,
.outer-wrapper,
.carbon-wrap,
.CodeMirror-gutters {
    color: #bfbfbf;
    background: #222;
    background-color: #222;
}

.CodeMirror {
    color: white;
}

.header-wrapper {
    background: none;
}

[class^="icon"] {
    background: none;
    color: white;
}

.info-tab i::after {
    border-bottom-color: #222;
    border-left-color: #222;
}

input.search {
    background: #8d8d8d;
    color: #000000;
}

input.search::placeholder {
    color: #2c2c2c;
}

.search-btn {
    background: #212121;
}

a.try-btn,
a.download-btn {
    color: #bfbfbf;
    background-color: #0b4c42
}

.carbon-poweredby,
a.try-btn span,
.token-operator,
.token.operator {
    background: none;
}

.tip-box {
    color: #f2ffe8;
}

.cm-s-default .cm-attribute {
    color: #6666f7;
}


p code,
code,
code.mark,
p code,
table td:last-child code,
.content ul li code,
code.mark {
    color: #c7c7c7;
    background-color: #000000;
}

.bottom-link a {
    color: #6fdeff;
}

.note-box {
    background: #063652;
}

.note-box * {
    color: #e6e6e6;
    background: none;
}

.note-icon {
    background: #006394;
}

.tip-box {
    background: #434d3b;
}

.leftcolumn a:visited {
    color: #137263;
}

.CodeMirror-activeline-background,
.CodeMirror-linebackground {
    background: #000 !important;
    background-color: #000 !important;
}

.CodeMirror-cursor{
    border-left-color: white;
}

.leftcolumn a,
.leftcolumn a:hover {
    color: #1db79f;
}

.appeal {
    background: #07433a;
}

.codebox pre.syntax-highlighter > code {
    background-image: linear-gradient(transparent 50%, #222 50%);
}

pre[class*="language-"] > code {
    background-color: #292929;
}

.CodeMirror-focused .CodeMirror-selected, .CodeMirror-selected {
    background: #000;
}
}
