css:
@-moz-document domain('catalogue.chartes.psl.eu'){

html {
margin: 0;
padding: 0;
background-color: #f0ede7 !important;
height: auto;
}

a {
font-size: normal;
}

body {
background-color: transparent !important;
}

#wrap {
margin-top: 50px !important;
padding: 0 !important;
background-color: transparent !important;
}

#header-region {
background-image: url('https://upload.wikimedia.org/wikipedia/commons/2/2e/Biblioth%C3%A8que_de_l%27Ecole_nationale_des_chartes.jpg');
height: 400px;
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
margin: 0;
padding: 0;
}


#header-region .navbar {
position: fixed;
top: 0;
z-index: 999;
width: 100% !important;
height: auto !important;
background-color: #ffffff !important;
border: none !important;
padding: 0;
margin: 0;
box-shadow: 0px 1px 3px gray !important;
}

#header-region .navbar-inner {
padding: 5px 0 !important;
background-color: #ffffff !important;
}

#opacheader .row {
display: none;
}

.navbar .divider-vertical {
border: none !important;
}

.main, #opacheader, #opac-main-search {
margin: 20px 0 0 0;
padding: 0 10%;
}

.main {
background-color: #fbf8f4 !important;
}

#news {
border: none;
margin-left: 10px;
}

.newsitem {
margin-bottom: 15px !important;
border: 1px solid #bd052d !important;
}

.newsbody, .newsheader {
padding: 5px 15px !important;
}
.newsfooter {
padding: 0 15px 5px !important;
}

.newsheader {
background-color: #bd052d !important;
color: #ffffff !important;
}

.mastheadsearch, .librarypulldown {
padding: 5px;
margin: 0;
background-color: #f0ede7;
text-align: center;
border-radius: 30px;
}

.masthead_search {
color: #ffffff !important;
}

#rssnews-container {
display: block !important;
background-color: #bd052d !important;
color: #ffffff !important;
padding: 2px !important;
text-align: center !important;
}

.flipster__container{
margin: 15px 0;
}

.textlogin {
display: block;
margin: 0 0 20px;
padding: 15px;
background-color: #bd052d;
color: #ffffff;
}

.flotte {
border: 1px solid #bd052d;
}

.textlogin a {
color: #ffffff;
}

#auth {
display: block;
border-radius: 10px;
background-color: #bd052d !important;
margin: 0 0 20px !important;
padding: 0 !important;
}

.brief {
color: #ffffff !important;
padding: 15px !important;
height: auto !important;
margin-top: 10px;
text-align: center;
}

legend {
color: #ffffff !important;
margin: 0 !important;
padding: 0 !important;
border: none;
}

#opacnavright{
margin-top: 15px;
padding: 0;
}

#navigation {
margin: 15px 25px 0 0;
padding: 0;
width: auto;
height: auto;
background-color: transparent;
}

#opacnav {
margin-bottom: 20px;
}

#opacnav .opac-menu {
width: 100%;
padding: 0;
}

#opacnav .dropnobtn, #opacnav .dropdown {
display: block;
width: 100% !important;;
background-color: transparent !important;;
font-weight: bold;
border-bottom: 1px solid #bd052d !important;;
}

#opacnav .dropbtn {
background-color: transparent !important;
width: 100% !important;
}

#opacnav .dropbtn, #opacnav .dropnobtn, #opacnav .dropdown {
text-decoration: none !important;
font-weight: bold !important;
padding: 0;
text-align: center;
}

#opacnav .dropnobtn:hover, #opacnav .dropdown:hover, #opacnav .dropbtn:hover {
display: block;
color: #ffffff !important;
background-color: #bd052d !important;
}

#opacnav hr {
display: none;
}

#opacnavbottom .opac-menu {
margin-top: 20px;
border: 1px solid #bd052d !important;
padding: 5px;
 width: -webkit-calc(100% - 15px);
    width:    -moz-calc(100% - 15px);
    width:         calc(100% - 15px);
}

.breadcrumb {
display: none;
}

#rssnews-container, #newacqs, .textlogin {
margin-left: 10px;
}

.span3 {
width: auto;
text-align: center;
}

.flotte {
margin: 10px !important;
padding: 10px !important;
border: none;
}

table {
border: 1px solid #fbf8f4 !important;
}

td {
border: 1px solid #fbf8f4 !important;
}

thead {
color: #bd052d !important;
}

}
