css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("23hq.com") {

#addCommentForm .h3
{
padding:4px !Important;
border:0 !important;
}

#imgBuddyIcon{
padding:2px !important;
background-color:#fff !important;
border:5px solid #999 !important;
}

#contextlinks a
{
font-size:12px !important;
}

.more-menu-popup
{
border:0 !important;
}

.column .image img {
padding-bottom:17px;
padding-right:17px;
}

.onecomment .head
{
padding:2px 2px 4px 4px !important;
margin-bottom:10px !important;
}

.onecomment .content
{
font-size:14px !important;
}


#bottomcontext h3,
#content h2,
#about div b
{
display:block !important;
margin-bottom:10px !important;
font-size:22px !important;
font-weight:normal !important;
padding: 4px 8px 2px 0 !important;
}

#content h3,
#foo {
display:block !important;
padding: 6px 4px 4px 8px !important;
font-size:12px !important;
font-weight:bold !important;
margin-bottom:10px !important;
border:0 !important;
}

.sidebar-item h3,
.h3
{
padding:0 0 0 4px !important;
display:block !important;
font-size:12px !important;
font-weight:bold !important;
margin-bottom:10px !important;
border:0 !important;
border-bottom:1px solid #333 !important;
}

/* ********* Color #000 */
.bottom-table-left a,
#globalnav .hover,
#globalnav .hover a,
#photoWords,.onecomment .content, h2, #about div b, #albumOneTitle,
.sidebar-item h3, .h3,
#foo {
  color:#000 !Important;
}

/* ********* Color #333 */
.album-name a {
  color:#333 !important;
}

/* ********* Color #999 */
#globalnav a {
  color:#999 !important;
}

/* ********* Color #ccc */
#foo {
  color:#ccc !important;
}

/* ********* Color #fff */
#globalsearch .globalsearch-input input.text,
#personalnav a,#titlebar a,#tabs a,
#titlebar .title a,
#titlebar .title .plustext,
#content h3,
#globalnav ul li a:hover,
#content h3 a,
#foo {
color:#fff !Important;
}

/* ********* Backgroundcolor #333 */
#personalnav, li.logo img, #globalnav, #titlebar, #tabs,body, #maincontainer {
  background-color:#333 !important;
}

/* ********* Backgroundcolor #666 */
#globalnav .hover,
#globalnav .hover a,
#content h3,
#personalnav .highlight, #globalsearch *,  #contextlinks a {
  background-color:#666 !important;
}

/* ********* Backgroundcolor #999 */
#addCommentForm .h3,
.more-menu-popup,
.album-list .album-one, .album-cover {
  background-color:#999 !important;
}

/* ********* Backgroundcolor #eee */
#bottomcontext
{
  background-color:#eee !important;
}

/* ********* Backgroundcolor #fff */
.sidebar-item h3, .h3,
#main, 
#foo {
  background-color:#fff !important;
}

/* ********* Background NONE */
#bottomcontext h3, #content h2, #about div b
{
  background:none !important;
}


#main,
#bottomcontext
{
border-color: #999 !important;
border-width: 0 !important;
}


}
