css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("23hq.com") {

/*
 * 23hq.com: Round 1 Light
 * A light theme for Round 1
 */

/* Background #333 */
body,
#frame,
#maincontainer,
#titlebar,
#globalnav,
#tabs,
#content h2,
.album-list .album-one,
.album-cover
{
background: none !important;
}

#tabs ul li,
#tabs #contextlinks
{
font-size:24px !important;
background:none !important;
border:0 !important;
}

#titlebar
{
margin:0 !important;
}

#about div b,
#albumOneTitle
{
display:block !important;
padding: 8px !important;
font-size:22px !important;
font-weight:normal !important;
margin-bottom:10px !important;
}

.sidebar-item h3,
.h3
{
display:block !important;
background-color:#ddd  !important;
padding: 6px 4px 4px 4px !important;
font-size:12px !important;
font-weight:bold !important;
margin-bottom:10px !important;
border:0 !important;
}


#content h2
{
margin-bottom:10px !important;
font-size:22px !important;
font-weight:normal !important;
background-color:#ccc !important;
padding: 4px 8px 2px 8px !important;
}

#globalnav a,
#personalnav a
{
color:#aaa !important;
}

#titlebar .title a,
#titlebar .title .plustext
{
color:#000 !important;
}

.album-list .album-one,
.album-cover,
#personalnav,
li.logo img,
#globalnav,
#titlebar,
#tabs,
body,
#maincontainer
{
background-color:#ddd !important;
}

#titlebar a,
#tabs a
{
color:#999 !important;
}

#personalnav .highlight,
#globalsearch *
{
background-color:#eee !important;
}

#main,
#bottomcontext
{
background-color:white !important;
border-color:#aaa !important;
border-width: 0 !important;

}


#bottomcontext h3,
#content h2,
#about div b
{
display:block !important;
margin-bottom:10px !important;
font-size:22px !important;
font-weight:normal !important;
}

}
