css:
@-moz-document url-prefix("https://wheeldecide.com"), domain("wheeldecide.com") {
#header, #footer, #content > div :nth-child(2n), #content > .row.row-centered, #content > center:nth-child(3n), form > div > div > div > div > a > input.btn.btn-block {
    display: none !important;
}
canvas#wheelcanvas {
    width: 700px;
    height: 700px;
	margin-left: -100px;
}
canvas#wheelcanvastop {
    width: 704px;
    height: 704px;
	margin-left: -102px;
    margin-top: -2px;
}
form {
	transition: 0.5s;
	transition-delay: 5s;
    margin-top: 200px;
	opacity: 0;
	transform:scaleX(1.5);
}
form:hover {
	transition: 0.5s;
	opacity: 1;
	transform:scaleX(1);
}
div#modifywheeldiv, img#mutebutton {
    margin-top: 160px;
}
button.btn {
    margin-bottom: 13px;
}
body.home.page.page-id-4.page-template.page-template-wheel-template.page-template-wheel-template-php.custom-background {
    background-image: url(http://i.piccy.info/i9/e74b0ed0c685b5deb3871adf1b944935/1570815664/182880/1342063/111.jpg);
	-moz-background-size: cover;
    -webkit-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
	background-attachment: fixed
}
}
