css:
@-moz-document domain("animeindo.to") {
:root {
--primary: #36c7ff;
--primary-text: #2f9dc8;
--btn-server-play: #38bb54;

}
/* Home */

.episode-card:hover .episode-number::before, .series-card:hover .episode-number::before {
    width: 55px;
    -webkit-transition: .2s;
    transition: .2s;
    -webkit-transition-delay: .3s;
    transition-delay: 0s;
}

/* Home End */

/* View */

.single-anime .series-details .block.ongoing, .single-anime .series-details .block.green {
    background-color: var(--primary);
    color: #000;
}

.single-anime .series-details .block {
    width: 100%;
    padding: 11px 0;
    font-size: 16px;
    line-height: 18px;
    background-color: #3a3d42;
    color: #fff;
    text-align: center;
    border-radius: 10px;
}

h5, .text-h5 {
    font-size: 20px;
}

.single-anime .anime-pic .episode-ratio {
    border-radius: 10px;
}

.btn-success:hover {
    color: #000;
    background-color: var(--primary);
    border-color: var(--primary);
}

.mb30 {
    margin-bottom: 20px;
}

/* View */

/* Play */

.single .col-main .mirror-buttons .the-button.active, .single .col-main .mirror-buttons .the-button:hover {
    background: var(--btn-server-play);
    color: #2e3035;
}

.breadcrumb span {
    margin-left: 10px;
    margin-right: 10px;
    color: var(--primary-text);
}


/* Play End */

/* Semua */


.sidebar-col {
    max-width: 0px;
}

/* Semua End */
}
