css:
@-moz-document domain("cryptotalk.org")
{
   :root {
	--bg-color: #2F3238;
	--bg-color-lv2: #26292E;
	--bg-color-lv3: #141518b3;
	--bg-color-lv4: #141518e3;
	--text-color: #ffffff;
	--text-color-lv2: #d1d1d1;
	--text-color-lv3: #cf74b5;
	--link-color: #79a6ff;
	--border-line: solid 0.1em #37557680;
	--border-line-lv2: solid 0.3em #37557680;
	--bg-link-active: #fd7008;
	--bg-link: #4a4b4b;
	--transparent: #fff0;
}

body {
    color: var(--text-color-lv2);
}

a {
	color: var(--text-color);
	background: var(--transparent) !important;
}

a.cke_button_on {
    background: #484848 !important;
}

strong {
	background-color: var(--transparent) !important;
	color: var(--text-color-lv2) !important;
}

span {
	background-color: var(--transparent) !important;
	color: var(--text-color-lv2) !important;
}

.ipsfocus_bg1 body, .ipsfocus_bg1 .ipsItemStatus.ipsItemStatus_large, .ipsfocus_bg1 .ipsItemStatus.ipsItemStatus_large.ipsItemStatus_read, .ipsfocus_bg1 .ipsType_sectionTitle, .ipsfocus_bg1 .cPost::before, .ipsfocus_bg1 #ipsLayout_body .cAuthorPane_mobile, .ipsfocus_bg1 .ipsComment:not(.ipsModerated) .ipsComment_header, .ipsfocus_bg1 .ipsButtonBar {
    background-color: var(--bg-color);
    background-image: none;
}

.content-wrap {
    background: var(--bg-color-lv3);
}

.ipsUserPhoto::after {
    border: none;
}

.ipsNavBar_primary > ul > li[data-active]::after {
    background-image: none;
}

.ipsNavBar_primary > ul > li[data-active]::before {
    background-image: none;
}

.ipsApp .ipsNavBar_primary > ul > li[data-active] > a {
    background: var(--bg-color-lv3);
    color: var(--text-color-lv2);
}

.ipsApp .ipsBreadcrumb {
    color: var(--text-color-lv2);
    background: var(--bg-color-lv3);
}

ipsBox:not(.ipsBox_transparent):not(.ipsModerated), .ipsBox, .ipsPageHeader, .focus-topic-compact .focus-topic, .ipsPager, .ipsWidget, .ipsBox_alt, #comments {
    background-color: var(--bg-color-lv2);
}

.ipsApp .ipsButton_link.ipsFollow, .ipsApp .ipsButton_link.ipsPromote {
    background: var(--bg-color-lv2);
    color: var(--text-color-lv2);
}

.ipsBox:not(.ipsBox_transparent):not(.ipsModerated), .ipsBox, .ipsPageHeader, .focus-topic-compact .focus-topic, .ipsPager, .ipsWidget, .ipsBox_alt, #comments {
    background-color: var(--bg-color-lv2);
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    color: var(--text-color-lv2);
    background: var(--bg-color-lv3);
    background-image: none;
}

.ipsItemStatus:not(.ipsItemStatus_large), .ipsItemStatus.ipsItemStatus_read:not(.ipsItemStatus_large) {
    color: var(--text-color-lv2);
}

.ipsType_light {
    color: var(--text-color-lv2);
}

.ipsTruncate_line {
	color: var(--text-color-lv2);
}

.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a, .ipsButtonBar .ipsButtonRow li > a.ipsButtonRow_active, .ipsButtonBar .ipsButtonRow li > button.ipsButtonRow_active {
    background: var(--bg-color);
    color: var(--text-color-lv2);
}

.ipsfocusBox-inner {
    background: var(--bg-color-lv2);
}

#elSearch {
    color: var(--text-color-lv2);
    background: var(--bg-color-lv3);
}

.ipsWidget .ipsTabs_small {
    color: var(--text-color-lv2);
}

.ipsWidget .ipsTabs_small .ipsTabs_activeItem {
    color: var(--text-color);
}

.ipsWidget.ipsWidget_horizontal .ipsTabs_panel {
    background: var(--bg-color-lv2);
}

.ipsAreaBackground_light {
    background: var(--bg-color-lv3);
}

.focus-post-bar .cTopic .ipsItemControls, .focus-post-bar #elMessageViewer .ipsItemControls {
    background: var(--bg-color-lv3);
    color: var(--text-color-lv2);
}

.ipsReact_reactCount {
    background: var(--bg-color-lv2);
}

#ipsfocus [data-role="replyArea"] {
    background: var(--bg-color);
}

.ipsComposeArea_editor, .ipsComposeArea_unavailable .ipsComposeArea_editor {
    background: var(--bg-color-lv3);
    border-color: var(--bg-color-lv3);
    color: var(--text-color-lv2);
}

.ipsComposeArea_dummy {
    background-color: var(--bg-color);
}

.focus-post-row .cTopic .ipsComment_controls, .focus-post-row #elMessageViewer .ipsComment_controls {
    color: var(--text-color-lv2);
}

.ipsQuote, .ipsCode, .ipsSpoiler, .ipsStyle_spoiler {
    background: var(--bg-color);
    color: var(--text-color-lv2);
}

.ipsQuote_citation, .ipsSpoiler_header, .ipsEmbedded_headerArea {
    background: var(--bg-color-lv3);
	color: var(--text-color-lv2);
}

.ipsApp .cke_contents, .ipsComposeArea_dummy {
    color: var(--text-color-lv2);
}

.ipsMenu_headerBar, .ipsMenu_footerBar {
    background: var(--bg-color-lv2);
}

.ipsApp .ipsDataItem_status, .ipsDataList_readStatus .ipsDataItem:not(.ipsDataItem_unread):not(.ipsDataItem_selected):not(.ipsModerated) {
    background: var(--bg-color-lv3);
}

.ipsDataItem_main > a:hover {
    color: var(--bg-link-active);
}

.ipsMenu_innerContent {
	background: var(--bg-color-lv3);
}

.ipsMenu, .ipsHovercard, .ipsHovercard_loading {
    background: var(--bg-color-lv4);
}

blockquote {
	background: var(--bg-color-lv3);
}

.ipsDialog > div {
    background: var(--bg-color-lv3);
}

div[data-role="streamBody"] .ipsBox_alt {
    background: var(--bg-color-lv3) !important;
}

.ipsStream .ipsStreamItem_contentType {
    background: var(--bg-color-lv2);
    color: var(--text-color-lv2);
}

html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem::after {
    border-right-color: var(--bg-color-lv2);
}

.ipsAreaBackground_reset {
    background: var(--bg-color-lv2);
}

.ipsType_sectionHead, #cAnnouncementSidebar h3, .ipsPager_type, .ipsPager .ipsPager_next a::after, .ipsPager .ipsPager_prev a::after, .ipsStreamItem_snippet .ipsType_richText {
    color: var(--text-color-lv2);
}

.cStreamFilter a {
    color: var(--text-color-lv2);
}

.cStreamFilter h3 {
    color: var(--text-color);
}

.ipsTabs_panel {
	background: var(--bg-color-lv2);
    background-color: var(--bg-color-lv2);
}

.ipsReact_types ul {
    background: var(--bg-color-lv2);
}

.ipsApp textarea.cke_source, html .cke_wysiwyg_frame, html .cke_wysiwyg_div {
    background-color:  var(--bg-color-lv2);
}

.cke .cke_top, .ipsComposeArea div[data-ipseditor-toollist] .ipsAreaBackground_light {
    background: var(--bg-color-lv1);
}

.fa-paperclip::before {
    color: var(--text-color-lv2);
}

.ipsNotificationCount {
    background-color: rgb(182, 63, 63) !important;
}

.ipsSelectTree {
    background-color: var(--bg-color-lv2);
    color: var(--text-color);
}

.ipsSelectTree_nodes [data-role="nodeList"] {
    background: var(--bg-color-lv1);
}

.ipsSelectTree_nodes {
    background-color: var(--bg-color-lv2);
}

.ipsSelectTree_placeholder {
    color: var(--text-color);
}

.ipsSelectTree_nodes [data-role="nodeList"] li .ipsSelectTree_item:hover:not(.ipsSelectTree_itemDisabled):not(.ipsSelectTree_selected) {
    background: var(--bg-color-lv3);
}

.ipsAreaBackground {
    background: var(--bg-color-lv2);
}

.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"], .ipsApp .ipsToolbox input, .ipsApp .ipsToolbox select, .ipsApp .ipsToolbox textarea {
    background-color: var(--bg-color-lv3);
}

.ipsApp .ipsButton_veryLight {
    background: var(--bg-color-lv3);
}

p > a {
	color: var(--bg-link-active) !important;
}

p {
	background: var(--transparent) !important;
	color: var(--text-color-lv2) !important;
}

img {
	background: var(--transparent) !important;
}

abbr {
	background: var(--transparent) !important;
	color: var(--text-color-lv2) !important;
}

#elProfileInfoColumn {
    background: var(--bg-color-lv3);
}

html .ipsApp .ipsBox .ipsAreaBackground .ipsBox {
    background-color: var(--bg-color-lv2);
}

.ipsStreamItem.ipsStreamItem_actionBlock {
    background: var(--bg-color-lv2);
}

.ipsApp .ipsButton_light {
    background: var(--bg-color-lv2);
}

.ipsApp .ipsButton_light {
    color: var(--text-color-lv2);
}
.ipsApp .ipsButton_veryLight {
    background: var(--bg-color-lv2) !important;
    color: var(--text-color);
}
.cke_widget_drag_handler_container {
	background: url('https://i.imgur.com/eMCxBjM.png') !important;
}

}
