css:
@-moz-document domain(en.wikipedia.org)
{

	*
	{
		line-height: 110% !important;
	}

	#content
	{
		background:#FFF;
		border:none;
		-moz-border-radius:0 !important;
		height:100%;
	}
	
	body
	{
		background:#FFF;
		font-family: "Times New Roman",times,serif !important;
		font-size: 1em !important;
		text-align: justify;
	}
	
	h1.firstHeading
	{
		color:#000;
	}
	
	#column-content,#content
	{
		margin:0 !important;
	}
	
	a
	{
		text-decoration:none !important;
	}
	
	a:visited,a:active
	{
		color:#002bb8;
	}

	a.new,a.new:visited,a.new:active
	{
		color:black !important;
	}

	a.external
	{
		background:none !important;
		padding-right: 0px !important;
	}

	#anon-banner,#catlinks,#centralNotice,#coordinates,#editpage-copywarn,#editpage-copywarn2,#editpage-specialchars,#f-copyright,#f-lastmod,#footer,#fundraising,#pt-logout,#siteNotice,#siteSub,#toc,.ambox,.dablink,.editsection,.Template-Fact,.portlet,.reference a,.toc,.usermessage,.infobox,.navbox,.reference,.references-small,.metadata,.articleFeedback
	{
		display:none !important;
	}

	a
	{
		color: black !important;
		font-weight: normal !important;
	}
}
