css:
@-moz-document domain("brick-hill.com") {
/* Brick Hub theme created by Leaf/Sigmap/Josu#9707/JR, original concept by SirStephen (I think???) */
    /* Shout out to Rhyhex for creating the orignal Brick Hub Theme, This part of the code is from him: */
    nav div.primary {
        background-image: url(https://cdn.discordapp.com/attachments/582605365145894922/621597089679278090/brickhubbanner.png);
        padding-top: 25px;
        overflow: hidden;
        background-repeat: no-repeat;
        width: auto!important;
        }
    /* If you see this, give Rhyhex some love on Discord: Derpeii#0011 */
/* Rhyhex's Original Theme: https://userstyles.org/styles/175254/brick-hub-theme */

    nav .info {
        background-color: transparent!important;
        bottom: 20px;
    }
    body {
        background-color: black;
        color: white;
    }
    .pages .page.active {
        color: white;
    }
    .friend-card:hover {
        background-color: #00000021;
    }
    footer,
    footer a,
    .card .item-card-details,
    .card .item-content {
        background-color: #1B1B1B;
        border-top: 1px solid #292929;
    }
    .status-time,
    span.bold.dark-gray-text.status-time.absolute.bottom {
        color: #6a6a6a;
    }
    .card .name {
        color: white;
    }
    nav div.primary,
    nav div.secondary,
    nav .info,
    .card .thumbnail.dark {
        background-color: #1B1B1B;
    }
    nav .username,
    .gray-text {
        color: #CCCCCC;
    }
    span#username,
    i.f-right.light-gray-text.far.fa-edit {
        color: #FF9900;
    }
    nav div.primary ul li a:hover,
    nav div.secondary ul li a:hover {
        border-color: #FF9900;
        color: white;
    }
    nav div.primary {
        color: #BBBBBB!important;
    }
    nav div.secondary {
        color: #fff;
        border-top: 1px solid #242424;
    }
    nav .info,
    .dark-gray-text {
        color: #fff;
    }
    input.rigid,
    textarea.rigid,
    .input-group.fill .input,
    .input-group.fill input,
    .input-group.fill textarea,
    .input-group.fill,
    input,
    textarea,
    .select.small-padding,
    select.small-padding,
    select.select.mb2 {
        background-color: #363636;
        color: white;
        border: 0.5px solid #555555;
    }
    .hover-card:hover {
        background-color: #1b1b1b;
    }
    /* Trading, etc */
    .trade-item-box {
        border: 1px solid #151515;
    }
    .trade-item img {
        border: 1px solid #1d1c1c00
    }
    .trade-item .serial {
        color: #ffffff;
    }
    .trade-item .trade-name {
        color: #c5c5c5
    }
    .trade-ev {
        color: #00ff16;
    }
    .item-card-container {
        background-color: #191919!important
    }
    .item-card-container:hover {
        position: absolute;
        text-align: left;
        background-color: #fff;
        border-radius: 3px;
        -webkit-box-shadow: 0 2px 5px rgba(0, 0, 0, .2)!important;
        box-shadow: 0 2px 5px rgba(0, 0, 0, .2)!important;
        padding: 5px;
        width: 100%;
        -webkit-box-sizing: border-box;
        box-sizing: border-box;
    }
    .trade-serial {
        background-color: #282828;
        color: #ffffff;
    }
    .item-card-image {
        border-color: transparent;
    }
    li.item-card-tile.small-tile.inline {
        background-color: #191919!important;
        border: 1.25px solid #191919!important;
    }
    .item-card-tile.selected .item-card-container {
        border: 2px solid #ffab00;
        padding: 3px;
        border-radius: 0px;
    }
    /* Buttons */
    .button.blue,
    button.blue,
    .button.red,
    button.red {
        color: #ACACAC;
        background-color: #2F2F2F;
        border-color: #2F2F2F;
        font-weight: bold;
    }
    .button.green,
    button.green,
    .nav-notif,
    .notif {
        color: #000;
        background-color: #FFA31A;
        border-color: #FFA31A;
        font-weight: bold;
    }
    .button,
    button {
        color: #303030;
        background-color: #2c2c2c47;
        border-color: #adadad00;
    }
    /* Section ends here */
    /*Content Card's start here */
    .card .content {
        background-color: #1B1B1B;
    }
    .card .top:not(.absolute).blue,
    .card .top:not(.absolute).green,
    .card .top:not(.absolute).orange,
    .card .top:not(.absolute),
    .card .top:not(.absolute).red {
        background-color: #1B1B1B;
        border-color: #242424;
        color: #FFA31A;
    }
    /* Section ends here */
    .profile-card:hover {
        background-color: #222222bf;
    }
    .black-text {
        color: #ffa500;
    }
    .line,
    hr {
        border-color: #1B1B1B;
    }
    .status-dot.online {
        background-color: #fc9700;
    }
    .fa,
    .fas {
        color: #000;
        border: 1px solid #f79000;
        border-radius: 2px;
        background-color: #f79000;
    }
    .input-group.fill .input-button {
        background-color: #f79000;
        color: #000;
        font-weight: bold;
        border-left: #f79000;
    }
    .tabs .tab {
        background-color: #171717!important;
        color: #b7b7b7!important;
        border: solid #171717!important;
        border-radius: 0px!important;
    }
    .tabs .tab.active {
        border: solid #141414!important;
        color: #b7b7b7!important;
        border-radius: 0px!important;
        background-color: #141414!important
    }
    .tabs .tab-body {
        border: 3px solid #101010!important;
        background-color: #131313!important;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/user/"), url-prefix("https://www.brick-hill.com/friends") {
/* User pages, etc */
    .toggle-user-desc:not(.open):after,
    .read-more-desc {
        background-color: #1B1b1b;
        color: #fc9700;
        margin-bottom: 2px
    }
    /* User pages, etc */
    .read-more-desc {
        color: #fc9700;
        font-weight: bold;
    }
    .button.red,
    button.red {
        color: #ACACAC;
        background-color: #2F2F2F;
        border: 1px solid #2F2F2F;
        font-weight: bold;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/forum/") {
/* Forums. Ah, yes. */
    .label.dark,
    .forum-bar a {
        color: white;
    }
    .board-info a,
    .stat .title {
        color: #c7c7c7;
    }
    .thread,
    .thread-row {
        border-bottom: transparent;
    }
    .forum-tag {
        background: #f79000;
        color: #000;
    }
    .forum-tag:after {
        border-right-color: #f79000;
    }
    .forum-bar .fa-angle-double-right {
        color: #f79000;
        background: transparent;
        border: transparent;
    }
    .darkest-gray-text {
        color: #cccccc;
    }
    .hover-card:hover {
        background-color: #0000001f;
    }
    .hover-card.viewed {
        background-color: #00000057;
    }
    .thread-label.blue {
        background-color: #f79000;
        color: #000;
    }
    .divide {
        background-color: transparent;
    }
    .forum-links a:hover {
        color: white;
    }
    a {
        color: white!important;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/client/") {
body,
    html {
        background-color: #000000 !important;
    }
    h5 {
        color: white!important;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/messages") {
.thread-info {
        color: #b5b5b5;
        background-color: #212121!important;
    }
    .thread-info.viewed {
        background-color: #191919!important;
    }
    .thread-info:hover {
        background-color: #292929!important;
    }
    .label.dark {
        color: white;
    }
    .board-info a,
    .stat .title {
        color: #ffffff;
    }
}
