css:
/* =============== DuckDuckGo_PureDark - v1.0 - 12-10-2019 =============== */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("duckduckgo.com")
{



#pg-index, #vertical_wrapper, .site-wrapper,
#search_form_homepage, #search_form,
.search--hover, .set-bookmarklet__detail, .no-results,
.vertical--map__sidebar__wrapper, .place-detail, .vertical--map__sidebar__header,
.metabar, .zci__main, .tile__body, .detail__pane, .btn--wire,
.header__button--menu:hover,
.nav-menu__list,
.js-search-button,
.js-acp-wrap,
#header_wrapper, #web_content_wrapper, .search-filters-wrap, .search-filters-wrap::before, .search-filters-wrap::after,
.module__content, .module__toggle--more, .module__toggle--less, .module--carousel__item, .module--carousel__right, .module--carousel__left,
#duckbar li a:hover,
.modal__box,
.frm__input, .modal__list__link,
#content, #pg-settings,
#zci-videos, .metabar__dropdowns-wrap::before, .metabar__dropdowns-wrap::after,
.badge-link
{
  background:url('data:image/png;base64,')!important;
}

a
{
    text-decoration: none !important;
}

/* =========== ACCUEIL / MAIN PAGE ================= */


.tag-home__item, .header--aside__item,
.onboarding-ed__arrow-teaser__alpinist
{
    display: none !important;
}
.onboarding-ed__arrow-teaser__arrow-wrapper
{
    background: #292B3A !important;
}
.onboarding-ed__arrow-teaser__arrow-wrapper:hover
{
    background: #303245 !important;
}
.onboarding-ed__arrow-teaser__arrow-wrapper img,
#logo_homepage_link:hover, .header__logo:hover
{
    opacity: 0.6;
}
#logo_homepage_link, .header__logo
{
    opacity: 0.4;
}



/* champ de recherche / search field */

#search_form_input_homepage, .js-vertical-map-search-input
{
    color: #5C6F84 !important;
}


/* suggestions */

.acp
{
    color: #6F6F6F !important;
}
.acp:hover
{
    background: #292B3A !important;
}
.search__autocomplete
{
    box-shadow: 1px 2px 2px 2px rgba(10, 10, 10, 0.2);
}
#search_form_input_clear, .js-detail-close
{
    opacity: 0.2;
}
#search_form_input_clear:hover, .js-detail-close:hover, .modal__close:hover
{
    color: orange !important;
    opacity: 0.5;
}

/* loupe / magnifying glass */
.js-search-button
{
    border: 1px solid #333333 !important;
    border-left: none !important;
    color: #333333  !important;
}
.js-search-button:hover
{    
    color: #545A54 !important;
}




/* ========== Menu droit / Right menu ========== */


.header__button--menu
{
    transform: scale(1.2);
    opacity: 0.5;
}
.header__button--menu:hover
{
    opacity: 0.8;
}
.nav-menu__list
{
    border-left: 1px solid #282939 !important;
}
.nav-menu__heading
{
    color: #6F6F6F !important;
}
.nav-menu__item a
{
    color: #939393 !important;
}
.nav-menu__item a:hover
{
    color: #CDCDCD !important;
}




/* ========== RESULTATS / RESULTS ========== */

/* hide badge link (uncomment)  
.badge-link
{
    display: none !important;
}*/

.badge-link__cookie-msg
{
    color: #5C6F84 !important;
}


.result__pagenum.result__pagenum--side
{
    color: orange !important;
}


#duckbar li a, .search-filters-wrap a, .metabar a
{
    color: #5C6F84 !important;
}
#duckbar li a:hover
{
    color: #B75B00 !important;
}
#duckbar li a.is-active
{
    color: #467D63 !important;
}
#duckbar li a
{
    border-bottom: none !important;
}

.dropdown__switch, .frm__switch__label
{
    background: #2F3040 !important;
}
.tile__content
{
    color: #888888 !important;
}


.module__header.module__header--link
{
    color: #3E72BF  !important;
    text-decoration: none !important;
}
.module__header.module__header--link:hover
{
    color: #51A4F7  !important;
}

.module--images__thumbnails__image
{
    opacity: 0.7;
}
.module--images__thumbnails__image:hover
{
    opacity: 0.9;
}

 
.js-images-show-more
{
    color: #396651 !important;
}
.js-images-show-more:hover
{
    color: #4D886B !important;
}


#did_you_mean
{
    color: #808299 !important;
}
#did_you_mean a
{
    color: #396651 !important;
}
#did_you_mean a:hover
{
    color: #4D886B !important;
}
.result--more__btn
{
    background: #262735 !important;
    color: #808299 !important;
    box-shadow: 1px 2px 0px 2px rgba(20,20,20,.5);
    border: 1px solid #2D2E40 !important;
}
.result--more__btn:hover
{
    background: #262735 !important;
    color: #396651 !important;
}


/* --------- carroussel --------- */

.js-carousel-module-title
{
    color: #808299 !important;
    text-decoration: none !important;
}
.js-carousel-module-title:hover
{
    color: #396651 !important;
}
.module--carousel__body a
{
    color: #6F6F6F !important;
}
.module--carousel__body a:hover
{
    color: #9B9B9B !important;
}



/* --------- Liens / Links --------- */

.result__a
{
    color: #3E72BF !important;
}
.result__a:hover
{
    color: #51A4F7 !important;
}
.result__a:visited
{
    color: #805555 !important;
}
.result__a:visited:hover
{
    color: #A37474 !important;
}

/* descriptif*/

.result__snippet, .module__text
{
  color: #6F6F6F !important;
}
.result__snippet b
{
  color: #7c7c7c !important;
}



/* adresse */
.result__extras__url a, .module__official-url
{
   color: #447B61 !important;
}
.result__extras__url a:hover
{
   color: #519173!important;
}



.js-search-input
{
    color: #7c7c7c !important;
}

.result__timestamp
{
   color: #888A9F !important; 
}


/* --------- cadre liens/links frame --------- */

.result.highlight, .result.highlight:hover
{    
    background: none !important;
    box-shadow: none !important;
    border: 1px solid transparent !important;    
}


/* --------- cadre wiki --------- */

.module__title__link
{
    color: #7D7D7D  !important;
}

.module__toggle--more, .module__toggle--less
{
    color: #404040 !important;
}
.module__toggle--more:hover, .module__toggle--less:hover
{
    color: #757575 !important;
}

.module__official-url:hover
{
   color: #658581 !important;
}
.module__more-at, .module__more-at-bottom
{
   color: #396651 !important;
}
.module__more-at:hover, .module__more-at-bottom:hover
{
   color: #4D886B !important;
}
.about-info-box__info-row .about-info-box__info-label
{
   color: #48646F!important;
}
.about-info-box__info-row span
{
   color: #606060!important;
}


/* code */

.prettyprint, .prettyprint span, .prettyprint span.com
{
    background: #242531 !important;
    color: #5C6F84 !important;
}
.prettyprint
{
    border: 1px solid #36384E !important;
    border-radius: 5px;
}
.prettyprint span
{
   color: #3AA4B6 !important;
}
.prettyprint span.lit
{
   color: #8784FF !important;
}



/* ========== VIDEOS ========== */

.tile__media
{
    opacity:0.6;
}
.tile__body a
{
    color: #6F6F6F !important;
}
.tile__body:hover a
{
    color: #3E72BF !important;
}

.tile__body a:visited
{
    color: #805555 !important;
}
.tile__body:hover a:visited
{
    color: #A37474 !important;
}

.tile__body:hover
{
    background: #242531 !important;
}
.tile__media:hover, .tile__body:hover.tile__media
{
    opacity: 0.9;
}
.tile-nav--sm--prev, .tile-nav--sm--next
{
    background: #242531 !important;
    color:#757575 !important;
}
.tile-nav--sm--prev:hover, .tile-nav--sm--next:hover
{
    background: #3E4055 !important;
    color:#969696 !important;
}
.overlay
{
    background: rgba(57,60,80,0.8) !important;
}
.btn--wire
{
    color: #3E72BF !important;
}
.btn--wire:hover
{
    color: #396651 !important;
}



/* ========== IMAGES ========== */

.tile--img
{
    opacity:0.9;
}

.c-detail__title
{
    color: #3E72BF  !important;
}
.c-detail__title a:hover
{
    color: #51A4F7  !important;
}
.c-detail__desc
{
    color: #404040  !important;
}
.c-detail__desc a:hover
{
    color: #396651  !important;
}
.c-detail__btn
{
    background: #282B39 !important;
    border: none !important;
    color: #808299  !important;
}
.c-detail__btn:hover
{
    background: #282B39 !important;
    border: none !important
}


.search-filters-wrap a:hover, .metabar a:hover
{
    color: #92A2B4 !important;
}
/* bouton fermeture */
.metabar__mode
{
    background: #282B39 !important;
}


/* --------- menus préférences --------- */

.settings-dropdown--header--label, .frm__label,
.modal__body, .set-theme
{
    opacity: 0.6;
}
.set-theme is-checked
{
    opacity: 0.9;
}
.modal__list li a:hover, .js-notification-text
{
    background: #292B3A !important;
}
.js-notification-text
{
    color: #A4A4A4 !important;
}




/* ========== MAPS ========== */

.no-results p, .no-results li
{
    color: #757575 !important;
}
.no-results p b
{
    color: #909090 !important;
}
.no-results a
{
    color: #396651 !important;
}
.no-results a:hover
{
    color: #4D886B !important;
}
.vertical--map__search-this-area
{
    color: #757575 !important;
    border: 1px solid transparent !important;
}
.vertical--map__search-this-area:hover
{
    color: #396651 !important;
    border: 1px solid #404040 !important;
}




/* ========== SETTINGS ========== */

.set-head__title
{
    color: #808299 !important;
}
.cloudsave, .set-bookmarklet__title, .set-reset__title, .frm__field option:hover
{
    background: #222331 !important;
}
.cloudsave
{
    border: 1px solid #303245 !important;
}
.btn
{
    background: #222331 !important;
}
.cloudsave, .cloudsave__actions span, .set-bookmarklet, .set-reset, .js-set-exit
{
    color: #808299 !important;
}
.cloudsave__whats-this
{
   color: #404040 !important; 
}
.cloudsave__whats-this:hover
{
   color: #757575 !important; 
}
.cloudsave__actions span:hover, .set-bookmarklet:hover, .set-reset:hover, .js-set-exit:hover
{
    color: #3E72BF !important;
    border: 1px solid #303245 !important;
}


.set-head__menu.btn-grp a
{
    background: #222331 !important;
    color: #7D7D7D  !important;
    border: none !important;
}
.set-head__menu.btn-grp a.is-active
{
    color: #467D63  !important;
    border: none !important;
}
.set-head__menu.btn-grp a:hover
{
    color: #969696  !important;
    border: none !important;
}
.js-set-exit
{
    background: #222331 !important;
}
.cloudsave__faq a, .frm--settings a
{
   color: #396651 !important; 
}
.cloudsave__faq a:hover, .frm--settings a:hover
{
   color: #467D63 !important; 
}
.frm--settings p
{
   color: #A4A4A4 !important; 
}
.set-bookmarklet__input, .set-bookmarklet__data, .frm__select__input
{
    background: #222331 !important;
    color: #7D7D7D  !important;
}
.frm__switch__label 
{
    color: #A45200  !important;
    border: 1px solid transparent !important;
}

.is-checked .frm__switch__label 
{
    color: #308594  !important;
    border: 1px solid #313346 !important;
}




/* --------- Envoyez vos remarques  / send your comments --------- */

.feedback-btn__send, .feedback-modal__input, .feedback-btn__icon
{
    background: #292B3A !important;
}
.feedback-modal__heading__prompt
{
    color: #808299 !important;
}
.feedback-modal__heading
{
   color: #467D63 !important; 
}


.js-feedback-dropdown
{
    color: #808299 !important;
    background: #292B3A !important;
}


.feedback-modal__submit
{
   color: #757575 !important; 
}
.feedback-btn a:hover, .feedback-modal__submit:hover
{
    color: #3E72BF !important;
}




/* ========== FOOTER ========== */

.footer
{
    display: none !important;
}

/* =================== END =================== */

}
