css:
@-moz-document domain("brick-hill.com") {
/* 
The goal of this theme is to
provide a true "Light Theme" UI,
or interface to the active user .
-----------------------------
Creator: Leaf
Date: 10.12.19
Discord: josu#9707
*/

.card .top:not(.absolute).red, .card .top:not(.absolute).green,.card .top:not(.absolute).red, .card .top:not(.absolute).blue, .card .top:not(.absolute).orange, .card .top:not(.absolute) {
    color: #7a7a7a;
    background-color: #ffffff;
    border-color: #efefef;
}

nav div.primary, nav div.secondary, nav .info {
    color: #7a7a7a;
    background-color: #fff;
}

footer {
    border-top: 1px solid #e7e7e7;
    background-color: #fff;
    color: #7a7a7a;
}

footer a {
    color: #7a7a7a;
}

input.rigid, textarea.rigid, .select, select {
    background-color: hsla(0,0%,100%,.9);
    border-color: #e7e7e7;
    color: #606162;
}

.card .thumbnail.dark {
    background-color: #ffffff;
}

.special-e-icon, .special-icon {
    top: -1px;
    right: -1px;
}

.status-dot {
    background-color: #c66;
}

.status-dot.online {
    background-color: #66cc66;
}

.trade-item img {
    border: unset;
}

li.item-card-tile.small-tile.inline {
    border: 1px solid #ededed!important;
}

.item-card-tile.selected .item-card-container {
    border: 1px solid #4cb04c;
}

a.button.bucks.small.flat span.bucks-icon.img-white, .button.purchase, button.purchase span.bucks-icon.img-white {
    filter: brightness(1) invert(0);
}

button.button.purchase.bits.flat.no-cap span.bits-icon.img-white {
    filter: brightness(.8);
}

.card .item-card-details, .card .item-content, .trade-item-box {
    border-color: #e7e7e7;
}

.line, hr {
    border: 0;
    height: 0;
    border-top: unset;
    border-bottom: 1px solid #eaeaea;
}

nav div.secondary, footer, footer a {
    border-top: 1px solid #efefef;
}

.img-white {
    filter: brightness(0) invert(0);
}

.nav-notif, .notif {
    background-color: #c60000;
}

.login-button {
    background-color: #5dda73;
    border: 1px solid #5dda73;
}

.register-button {
    background-color: #65c6d2;
    border: 1px solid #65c6d2;
}

.reigster-button:hover {
    background-color: #00bcd4;
    border: 1px solid #00bcd4
}

.index-top-bar {
    background-color: #ffffff;
    color: #000;
}

.login-button:hover {
   background-color: #10cb31;
   border: 1px solid #10cb31; 
}
        

.slider-button:hover {
    background-color: #00000008;
}
}
