css:
@-moz-document domain("www.nexusmods.com"), domain("help.nexusmods.com"), domain("wiki.nexusmods.com") {
/*
/*
/* Nexus Mods Dark Theme - KMV
/* https://userstyles.org/styles/176244
/*
/* Designed for Stylus:
/* https://add0n.com/stylus.html
/*
/* My other themes:
/* https://userstyles.org/users/370722
/* 
/* Contact:
/* https://www.reddit.com/r/KMV/
/*
/*
*/

::before,::after
{
    transition:.4s!important;
}

/*Text*/
body.scheme-theme-ReskinBlue ul.sublist li a,
a.mfp-prevent-close,
button.rj-search-category-dropdown-toggle,
form.rj-search .rj-search-input,
h1,
h2,
h3,
h4,
h5,
input.search,
li#footer-info-lastmod,
p,
#fullArticle div,
#fullArticle li,
#fullArticle strong,
.premium-banner > i,
.premium-banner > span,
#sidebar .nav-list .active a,
#sidebar .nav-list a:focus,
#sidebar .nav-list a:hover,
#sidebar form .search-query,
#wbbmodal .wbbm-title .wbbm-title-text,
.article-nav .article-label
{
    color: #ccc!important;
    transition:.4s;
}

p strong
{
    color: #ffbe00!important;
}

a p:hover
{
    color: #fff!important;
    transition:.4s;
}


code
{
    background-color: #000;
    color: #0f0!important;
}

.status-admin
{
    color: #ff6666!important;
}

/*List Options Hover*/
.rj-search-category-option:hover
{
    background-color: #ccc;
}

.article-nav ul li a .icon-arrow-thin,
.pagination ul li.next .icon-arrow-thin,
.pagination ul li.prev .icon-arrow-thin
{
    fill: #ccc;
}


/*Backgrounds*/
div.flex,
ul.tiles li.image-tile .tile-data
{
    background: transparent!important;
    background-color: transparent;
}

input,
#mod-editing-content .wysibb-text-editor,
.image_figure,
.tiles .tile-data
{
    background:#000!important;
    background-color: #000;
}

article,
body,
body .home-introm,
dd.clearfix,
div#mw-content-text.mw-content-ltr.mw-parser-output,
dt,
dt.clearfix,
dt.clearfix:hover,
dt.clearfix.accopen,
section,
section#rj-vortex,
ul.modtabs,
ul.modtabs li,
ul.modtabs li a.selected,
ul.rj-mobile-menu-subsections,
#mods-wrapper,
.add-process,
.agroup,
.bg-vortex-pattern/* Only 1 using "background" */
,
.comment-content,
.comment-kids,
.container,
.content,
.contentWrapper,
.cover-art .cover-art-slash,
.fcVazu.fcVazu,
.file-body,
.file-category,
.fore_div_mods,
.form-section-content,
.form-sub,
/*.home-intro/* <- Enable to get remove Frontpage Image,*/
.home-trending,
.image-filter-container,
.result.safe.inline-flex,
.logged-in,
.modimages,
.modpage .col-1-1,
.mw-parser-output > div:nth-child(1),
.open,
.overthrow,
.popup,
.qc-cmp-ui,
.rj-header-tray,
.rj-input-with-button,
.rj-mobile-menu-body,
.rj-mobile-menu-footer,
.rj-mobile-menu-header,
.rj-mobile-menu-section .section-content,
.rj-mobile-menu-sections,
.rj-mobile-menu-group-header,
.rj-mobile-menu-subsection,
.rj-mobile-search-categories,
.rj-nav-item.active,
.rj-vortex-button,
.select2-container,
.sideitems,
.side-tags,
.tab-block + .tab-block,
.tab-head,
.tabcontent,
.thumbgallery,
.title-left .wrap,
.video-ad,
.wrap.tab-block,
.wysibb
{
    background: #111 !important;
    background-color: #111 !important;
}

#wbbmodal .wbbm,
#wbbmodal .wbbm-bottom
{
    color:#ccc;
    background: #111 !important;
    background-color: #111 !important;
}



/*Slightly Lighter Headers and panels*/
#mw-head,
#mw-panel,
.article-actions,
.file-head,
.form-section header,
.readme-canvas,
.navbar-inner,
.mod-tile-right,
.mod-tile-left
{
    background: #181818!important;
    background-color: #181818!important;
}

pre,
table,
td,
th,
ul.action-log > li,
#all-time-distribution-chart,
#charity-chart,
#Notice_Box.boilerplate,
#payout-chart,
#searchBar .search-query,
#sidebar .nav-list li.active,
#yearly-distribution-chart,
.activity-desc,
.article-nav ul li a:hover,
a .category:hover,
.comments > ol > li ol > li .comment-head,
.ipsBlockquote,
.mw-normal-catlinks,
.mw-parser-output > p:nth-child(105) > span:nth-child(1),
.notification-load-more,
.notification-tile-in-page,
.notifications-header,
.notifications-list,
.result,
.rj-nav-item:hover,
.static .wrap:first-of-type,
.sublist,
.toc
{
    background-color: #222!important;
}

div.boilerplate:nth-child(98) > p:nth-child(3),
#pagehistory li.selected,
#serp-dd .result a:hover,
.mw-parser-output > p:nth-child(100) > span:nth-child(1),
.notification-tile-in-page:hover,
.page-selected.mfp-prevent-close
{
    background-color: #444!important;
}

/*Red Warning Backgrounds*/
.boilerplate.metadata
{
    background-color: #150000!important;
}

/*Slightly lighter Warning Backgrounds*/
.mw-parser-output > ul:nth-child(27) > li:nth-child(1) > span:nth-child(2)
{
    background-color: #ff000020!important;
}


/*Dropdowns*/
a.mw-helplink,
#sortBy,
#p-search form,
.rj-search-category-dropdown,
.search-button,
.select2-container
{
    filter: grayscale(1)invert(1)!important;
    z-index: 11;
}

/*Inverted Menus & Text*/
.oo-ui-capsuleMultiselectWidget-handle,
.oo-ui-clippableElement-clippable,
.oo-ui-menuOptionWidget,
.oo-ui-textInputWidget.oo-ui-widget-enabled input,
.oo-ui-textInputWidget.oo-ui-widget-enabled textarea,
.select2-search__field,
.wbb-select .val,
.wysibb-toolbar-btn.wbb-dropdown.wbb-cp,
.wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn span.btn-inner
{
    filter: grayscale(1)invert(1)!important;
}

/*Inverted List Text Inversion (Inverted text back to normal)*/
.rj-search-category-option:hover .select2-container--default .select2-results__option,
.select2-container--default .select2-results__option--highlighted[aria-selected]
{
    color: #000;
}


/*Buttons*/
.pagination ul > li > a,
.qc-cmp2-summary-buttons button
{
    background-color: #222!important;
    transition: .4s!important
}

.pagination ul > li > a:hover,
.qc-cmp2-summary-buttons button:hover
{
    background-color: #444!important;
    transition: .4s!important
}

/*Additional Transitions*/
.category
{
    transition:.4s!important;
}

/*Image Dimming*/
img,
a img,
img.game_cover,
.home-trending ul.tiles li.image-tile .tile-desc,
.rj-game-content-games.rj-grid-12 .rj-game
{
    filter:brightness(75%);
    transition:.4s;
}

a img:hover,
img.game_cover:hover,
ul.tiles li .single-image img:hover,
.home-trending ul.tiles li.image-tile .tile-desc:hover,
.rj-game-content-games.rj-grid-12 .rj-game:hover
{
    filter:brightness(100%);
    transition:.4s;
}



/*Tile Management*/
.fadeoff
{
    background: linear-gradient(to bottom,rgba(56, 56, 56, 0) 0,rgb(0, 0, 0) 100%);
}
ul.tiles.tile-list li.mod-tile .mod-tile-right,
ul.tiles li.image-tile .tile-desc,
.tile-desc
{
    background: linear-gradient(to bottom,rgba(56, 56, 56, 0) 0,rgb(0, 0, 0) 100%)!important;
}

.tiles .fadeoff
{
    background: transparent!important;
}

/*Optional - Ad-Removal*/

#ads,
.ads,
.sidebar-sticky,
.video-ad
{
    height: 0px;
    display: none!important;
}
}

@-moz-document domain("www.nexusmods.com") {
/*
/* 
/* Cursor - Skyrim - KMV
/* https://userstyles.org/styles/175086/
/*
*/
*
{
    cursor: url("data:image/x-icon;base64,"), default !important;
}
}

@-moz-document domain("help.nexusmods.com"), domain("wiki.nexusmods.com") {
/*
/* 
/* Cursor - Plain Black - KMV
/* https://userstyles.org/styles/175086/
/*
*/
a,
a *,
element,
html,
body,
div,
input,
select,
/*span,*/
svg
{
    cursor: url("data:image/x-icon;base64,"), default !important;
}

a:hover,
a:hover *,
h2 a:hover,
li a:hover,
button:hover,
th:hover,
use:hover,
.arrow:hover,
.arrow-upvote:hover,
.arrow-downvote:hover,
.css_button:hover,
.dismiss:hover,
.expando-button:hover,
.link:hover,
.no-select:hover,
.radio-response:hover,
.res-step:hover,
.selected:hover
{
    cursor: url("data:image/png;base64,"), default !important;
}

a:active,
a:active *,
h2 a:active,
li a:active,
button:active,
input:active,
th:active,
use:active,
.arrow:active,
.arrow-upvote:active,
.arrow-downvote:active,
.css_button:active,
.dismiss:active,
.expando-button:active,
.link:active,
.no-select:active,
.radio-response:active,
.res-step:active,
.selected:active
{
    cursor: url("data:image/png;base64,"), default !important;
}
}

@-moz-document domain("www.gstatic.com"), domain("www.google.com") {
/*
/* i really want this to be global */
/*
/*   reCAPTCHA Dark Theme by KMV, 2019 
/* https://userstyles.org/styles/175231/
/*
/* Themes by KMV:
/* https://userstyles.org/users/370722
/*
/* Contact - http://www.reddit.com/r/KMV/
/*
*/


/*--------------------------- Text ---------------------------*/

/*Headers*/

#cf-wrapper h1,
#cf-wrapper h2,
.cf-error-footer p,
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > h1:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(2) > h2:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > h2:nth-child(1),
h4#text,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div p.cap_mess.cap_text.text-center
{
    color:#fff!important;
}


/*Text*/

#cf-wrapper h2 + p,
#cf-wrapper p + p,
#checkbox-label.no-selection,
.cf-subheadline,
.prompt-text,
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(3),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(4),
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.cap_head.clearfix div.cap_side.pull-right p.cap_note.cap_text.count_down,
p.cap_mess:nth-child(5)
{
    color:#ccc!important;
}


/*Language Dropdown List Text*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li
{
    color: #aaa;
}
/*Language Dropdown Text - Active*/
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li.active
{
    color: #fff;
}


/*Links*/

#cf-wrapper a,
.challenge-modal a[class*="link"]
{
    color: #00bcb7;
    transition:.4s;
}
#cf-wrapper a:hover,
.challenge-modal a[class*="link"]:hover
{
    color: #00fff8 !important;
    transition:.4s;
}

/*------------------------ Backgrounds ------------------------*/

#cf-wrapper,
#cf-wrapper #cf-error-details,
#challenge-wrapper [class="prompt-block"],
.cf-highlight,
.challenge-modal [class*="container"],
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2),
body > div:nth-child(1) > div:nth-child(1),
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div
{
    background: #111 !important;
    background-color: #111 !important;
}

.challenge-modal [class*="header-title"]
{
    background: #000 !important;
}


/*------------- Image Backgrounds and Icons Tweak -------------*/

/*Main Page Right-hand side Imagery*/

#cf-wrapper #cf-error-details .cf-screenshot-container,
#Captcha-Container [class*="help-image"],
#Captcha-Container [class*="refresh-image"],
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(3) > div:nth-child(1)
{
    filter: invert(.95) grayscale(1);
    transition:.4s !important;
}

/*hCaptcha Images Background - "Movie-strips"*/

html body.no-selection div#Captcha-Container div#challenge-wrapper div.challenge div.challenge-view div.challenge-grid div.task-image
{
    background-color: #000!important;
}

/*hCaptcha Logo and Icons Tweak*/

.hcaptcha-logo, .icon-color, .icon-color:hover, .icon-grey, .icon-grey:hover
{
    transition:.4s !important;
}

/*"hCaptcha" Text Brightness Increase*/

#Captcha-Container [class*="text-img"]
{
    filter: brightness(4) !important;
}


/*hCaptcha Hovering*/

#Captcha-Container [class*="task-image"],
.rc-image-tile-target
{
    filter: brightness(.75)!important;
    transition:2s;
    transition-delay: 2s;
}
#Captcha-Container [class*="task-image"]:hover,
.rc-image-tile-target:hover
{
    filter: brightness(1)!important;
    transition:.4s;
}

/*--------------------- reCAPTCHA itself ---------------------*/

/*Backgrounds*/

.anchor,
#Captcha-Container,
.rc-anchor-light,
#rc-imageselect,
.rc-anchor-checkbox-label,
.recaptcha-checkbox-border,
.yt-uix-button,
.rc-imageselect-desc-wrapper,
.rc-doscaptcha-body-text,
.jfk-textinput,
.rc-anchor-invisible-text,
#cf-wrapper #cf-error-details .cf-highlight .cf-highlight-inverse,
html body.invisible_mode div.wraper div.middle,
html body.invisible_mode div.wraper div.bottom
{
    background: #111 !important;
    background-color: #111 !important;
    color: #ccc !important;
}


/*-------------------------- Borders --------------------------*/

/*Main Border*/
.anchor.normal.light,
.challenge-modal [class*="container"]
{
    border: 1px solid #444;
    border-radius: 3px;
}

/*Border over Footer*/
#cf-wrapper #cf-error-details .cf-error-footer
{
    border-top: 1px #444 solid;
}

/*hCaptcha Main Window Border*/

body > div:nth-child(3)
{
    border: 1px solid #444 !important;
    border-radius:6px;
}
#Captcha-Container
{
    border-radius:4px;
}

/*hCaptcha "Selected" Border*/
div.task-image > .border
{
    border: 3px solid #777 !important;
    width: 136px !important;
    height: 136px !important;
    transition:.4s;
}


/*-------------------------- Buttons --------------------------*/

/*Skip Button*/

.rc-button-default:focus
{
    background-color: #222 !important;
    transition: all 0.4s ease-in-out !important;
    box-shadow: 0 0 0 1px #555 !important;
}

/*New hCaptcha Skip-Button*/
.submit-background
{
    background-color: #222 !important;
    transition: all 0.4s ease-in-out !important;
    border: 1px solid #555;
}
.submit-background:hover
{
    background-color: #333 !important;
    transition: all 0.4s ease-in-out !important;
    border: 1px solid #666;
}


/*Submit, Verify % Try Again Button*/

input#hcaptcha_submit,
#recaptcha-demo-submit,
#recaptcha_submit,
input#recaptcha_submit,
.rc-button-default,
.rc-doscaptcha-header
{
    background-color: #222 !important;
    border: 1px solid #444 !important;
    border-radius: 4px;
    transition: all 0.4s ease-in-out !important;
    color: #fff !important;
}
#hcaptcha_submit:hover,
.rc-button-default:hover,
.rc-doscaptcha-header:hover,
.submit-background:hover
{
    background-color: #333 !important;
    transition: all 0.4s ease-in-out !important;
}

/*Aid buttons, Bottom*/

.rc-button
{
    background-color: transparent !important;
    filter: invert(100%) !important;
}

/*Language Dropdown - Button*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown button.dropdown-toggle,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content
{
    background: #111 !important;
}

/*Language Dropdown Hover*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown button.dropdown-toggle,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li:hover
{
    background: #333;
}


/*------------------------ Checkboxes ------------------------*/

/*Main Checkbox*/
html body.no-selection div#anchor.anchor.normal.light div.anchor-content div.checkbox-wrapper div.checkbox-container div#checkbox.checkbox
{
    width: 30px;
    height: 30px;
    border: 1px solid #555;
    border-radius: 4px;
    background-color: #222;
}

/* Image Select Checkbox*/

.rc-imageselect-checkbox,
.rc-imageselect-tileselected .rc-imageselect-checkbox,
.rc-imageselect-dynamic-selected .rc-imageselect-checkbox
{
    background-color: transparent !important;
    filter: grayscale(1) !important;
}
.rc-imageselect-incorrect-response,
.rc-imageselect-error-dynamic-more,
.rc-imageselect-error-select-more,
.rc-imageselect-error-select-something
{
    background-color: #222 !important;
    color: #fff !important;
    filter: grayscale(1) !important;
}


html body.invisible_mode form.form-group.form-captcha
{
    margin-bottom: 0px;
}
}
