css:
@-moz-document regexp("https:\\/\\/(www\\.)?wykop\\.pl(\\.[a-z]{2})?\\/(?!(ramka|feedback)).*") {
/*
* Name:			Ciemny wykop dla niezalogowanych
* Homepage:		https://userstyles.org/styles/176253/
* Author:		Atx
* Version:		v1.1
* Date: 		26.02.2021
*/

.type-light-caution, .type-light-warning {
    background-color: #333333 !important;
}

body, #site, tbody > tr:nth-child(2n), #loginField, #passwordField, #emailField, .comments-stream {
    background-color: #1C1C1C !important;
}

.r-block, .grid-right > .r-block, .grid-right > div:not(#rightFooterBox), .article, .rbl-block, .comments-stream > li, .media-content .description, tbody > tr:nth-child(2n):hover, .dropdown > div, #nav .nav li.active a, .diggbox .hot, .rdiggbox .hot, #popup-modal {
    background-color: #2C2C2C !important;
}

thead > tr > th {
    background: #1C1C1C !important/*wykop cos nie ogarnia tabeli*/
}

.list > tbody > tr > th {
    background: #3C3C3C !important/*wykop cos nie ogarnia tabeli*/
}

.tag.create:hover, .showSpoiler, .show-more, ul.related li > div span.data > span.cover, .button, button, input.button, input[type="submit"], .drop-cal > div ul, .summary, input[type="text"].create:hover, input[type="text"].create:focus, .tagChartTooTip, .newregister .form-marked, .newregister-switch-wrapper > div:last-child, tbody > tr:nth-child(2n+1):not(.infopanel), .form-big > div:first-child, .newregister-drop li.mark-bg.text-center, #nav,  #footer {
    background-color: #3C3C3C !important;
}

.r-block, .grid-right > .r-block, .grid-right > div:not(#rightFooterBox) {
    background-color: #232323 !important;
}

.button.submit, button.submit, input.button.submit, input[type="submit"] {
    background-color: #777 !important;
}

.diggbox span:first-child {
    background-image: url('https://wykop.pl/static/wykoppl7/s/14e7270353720baa3755fea29fa453c8.png') !important;
}

.drop-cal > div ul li a:hover, tbody > tr:nth-child(2n + 1):not(.infopanel):hover, .unmarked-fb-button:hover, .unmarked-fb-button:focus, .unmarked-button:hover, .unmarked-button:focus {
    background-color: #4C4C4C !important;
}

.w-item.mini span.image {
    background-color: #5C5C5C !important;
}

.mark-bg {
    background: #444 !important;
}

.article > div p.text a:hover, .nav > ul li a:hover, .nav .info, .lcontrast .actions a.affect, .lcontrast:hover .actions a.affect, .lcontrast:hover a.affect, .dark, body a#register.link:hover, .showlinks:hover a, body a:hover, a.button:hover, .slide.title > div > a, .number-small, .fa.fa-caret-left, .r-block.search li a.active, .r-block.search li .active i.fa-check-square, .author.ellipsis > a[href="https://www.wykop.pl/ludzie/wykop/"] > b, .newregister .register-switch, #nav .nav li a, .lcontrast:hover a.affect, body a:hover, #popup-modal h5, fieldset .row.check-and-label label {
    color: #FAFAFA !important;
}

.article > div p.text a, .nav > ul li a, .entry.iC, .text, .space.information.bdivider, a.button, ul.sub-menu.inline-list.clear-top li, #owncontent > p, .description > .fright.small, label.inline, .vC[data-vc="0"], .r-block.search li a, h3, h4, h6, blockquote p.description, .media-header p.description, .tagChartTooTip > div, .newregister input[type="email"], .newregister input[type="text"], .newregister input[type="password"], .rbl-block.space > .static-text-wraper > .static-content-wraper, #advancedSearchDetails > ul > li, #advancedSearchDetails > p, .grid.m-reset-float, .profilesRankTable.list, .newregister-drop, .drop-cal > div ul li a {
    color: #CCC !important;
}

#footer h4, #footer a {
    color: #BBB !important;
}

ul.related li > div span.data > span em.logoreklama, .article h2 a:visited, a.showSpoiler, a.show-more, .sub-menu.inline-list > ::after, .lcontrast .affect, blockquote p.description, .media-header p.description, .light, .usercard span.info, .article.preview h2 a + sup {
    color: #777 !important;
}

body .dropdown li a:hover *, body .nav li a:hover, body #nav .nav li a:hover, .row.elements a.affect:hover, .row.elements a:hover {
    color: #999 !important;
}

.m-set-fullwidth.covid19b {
    color: #95A5A6 !important;
}

h4 {
    color: #788DA2 !important;
}

.type-warning.space {
    color: #0A0A0A !important;
}

.tag, .showTagSummary {
    color: #6CB0DD !important;
}

.type-light-caution, .type-light-warning {
    color: #FFFDCA;
}

.info.m-reset-float {
    color: #CACECF !important;
}

div.m-set-fullwidth {
    color: #B3B3B3 !important;
}

body a, .tag, input, label.inline {
    color: #CDD9E2 !important;
}

div.text > p a.showProfileSummary, div.text > p a[rel="nofollow"] {
    color: #6CB0DD !important;
}

.article > div p.text a:hover, .author.hlight, .tag.create:hover, h4, .tdivider, ul.related li > div div.content, .dropdown > div, .bdivider, .media-content .description, .button, button, input.button, .sub-menu, .drop-cal > div ul, #surveyBox ul li > div:last-child, #surveyBox fieldset, .summary, .touch-content .width-one-third, .comments-stream.touch-content, .twitter-cover, .w-item.mini span.image, .tagChartTooTip, .newregister-switch-wrapper > div:last-child, .newregister input[type="email"], .newregister input[type="text"], .newregister input[type="password"], .newregister .form-marked, .static-menu-wraper li, .static-text-wraper header, th, td, .form-big > div:first-child, .form-big, textarea, input[type="text"], input[type="password"], input[type="date"], input[type="number"], .apisandbox-menu input, .static-text h2 {
    border-color: #4C4C4C !important;
}

.with-legend legend, .with-legend, .author {
    border-color: #3C3C3C !important;
}

.comments-stream > li.hot {
    border-left: 3px solid #E74C3C !important;
}

.button.submit, button.submit, input.button.submit, input[type="submit"] {
    border-color: #444 !important;
}

.menu-list li, .r-block, .grid-right > .r-block, .grid-right > div:not(#rightFooterBox) {
    border-color: #292929 !important;
}

.rbl-block, .nav {
    border-bottom-color: #2C2C2C !important;
    border-left-color: #2C2C2C !important;
    border-right-color: #2C2C2C !important;
}

.comments-stream > li {
    border-color: #222222 !important;
}

.newregister .newregister-header a.active {
    border-color: #6C6C6C !important;
}

.newregister-drop li.mark-bg.text-center::after {
    border-bottom-color: #3C3C3C !important;
}

.nav li.active a {
    border-color: #FAFAFA !important;
}

.newregister .newregister-header a {
    border-color: #CCC !important;
}

body.screening .wrapper {
    max-width: 1180px !important;
}

.diggbox .hot, .rdiggbox .hot {
    background-position: 4px -132px !important;
}
}
