	src: url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Light.woff2) format("woff2"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Light.woff) format("woff"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Light.otf) format("opentype");
	font-display: swap
}
@font-face {
	font-weight: 400;
	font-family: spotify-circular-cyrillic;
	font-style: normal;
	src: url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Book.woff2) format("woff2"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Book.woff) format("woff"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Book.otf) format("opentype");
	font-display: swap
}
@font-face {
	font-weight: 600;
	font-family: spotify-circular-cyrillic;
	font-style: normal;
	src: url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Bold.woff2) format("woff2"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Bold.woff) format("woff"), url(https://open.scdn.co/fonts/CircularSpUICyrOnly-Bold.otf) format("opentype");
	font-display: swap
}
html {
	min-height: 100vh
}
body {
	overflow: auto;
	background: #111
}
a,
h1,
h2,
h3,
h4,
h5,
h6,
h7,
h8,
p {
	font-family: spotify-circular, spotify-circular-cyrillic, spotify-circular-arabic, spotify-circular-hebrew, Helvetica Neue, Helvetica, Arial, Hiragino Kaku Gothic Pro, Meiryo, MS Gothic, sans-serif!important
}
::-webkit-scrollbar,
div::-webkit-scrollbar {
	width: 8px
}
::-webkit-scrollbar-track {
	background: linear-gradient(360deg, #0a0a0a 91px, transparent 91px)
}
div::-webkit-scrollbar-track, ul::-webkit-scrollbar-track {
	background: transparent
}
::-webkit-scrollbar-thumb,
div::-webkit-scrollbar-thumb {
	background: #202020
}
::-webkit-scrollbar-thumb:hover,
div::-webkit-scrollbar-thumb:hover {
	background: #222
}
#dropdown-button-53 ul:last-child {
	border-bottom: 0
}
.loading {
	background: url(https://a-v2.sndcdn.com/assets/images/loader-db80c58.gif) center center no-repeat;
	filter: invert(1)
}
h2.selectionModule__titleText {
	color: #fff;
	font-weight: 600;
	font-size: 28px
}
.playableTile {
	width: 120px;
	padding-bottom: 60px
}
.userBadge__usernameLink {
	font-size: 14px
}
.userBadge__usernameLink,
a.playableTile__heading {
	color: #fff!important;
	font-weight: 400;
	text-align: center
}
.userBadge__usernameLink:hover {
	color: #fff;
	font-size: 14px;
	text-align: center
}
a.playableTile__heading:hover {
	color: #fff;
	text-align: center
}
.badgeList__item {
	width: 150px
}
.l-collection {
	margin-right: 15px
}
.playableTile__usernameHeading,
a.playableTile__usernameHeading {
	width: 100%;
	color: #fff;
	font-weight: 200;
	text-align: center;
	opacity: .6
}
.sc-classic #app .l-container,
.sc-classic #app header,
.header__inner {
	box-sizing: border-box;
	width: 230px
}
a.playableTile__usernameHeading:hover {
	color: #fff;
	text-decoration: underline;
	opacity: 1
}
.sc-border-light-bottom,
.sc-border-light-right,
.sc-border-light-top,
.selectionModule {
	border: 0
}
.image__lightOutline .image__full {
	box-shadow: 0 0 10px rgba(0, 0, 0, .3)
}
.listenArtworkWrapper__artwork .image__lightOutline .image__full {
	box-shadow: 0 0 15px 0 #000
}
.moreActions {
	padding: 5px 0;
	background: #0d0d0d;
	border: 1px solid rgba(0, 0, 0, .15);
	border-radius: .25rem
}
.moreActions .moreActions__button.sc-button-medium {
	color: #b3b3b3;
	font-size: 14px;
	background: 0 0;
	border: 0;
	cursor: default
}
.sc-classic #app .l-container #content .soundBadge__additional .sc-button-small.sc-button-more:before,
.moreActions .moreActions__button.sc-button-medium:before {
	filter: brightness(3)
}
.moreActions .moreActions__button.sc-button-medium:hover {
	color: #fff;
	background: #333
}
.moreActions .moreActions__button.sc-button-medium:hover:before {
	filter: brightness(6)
}
.sc-classic #app {
	display: flex
}
.sc-classic #app header {
	background: 0
}
.header__inner {
	display: flex;
	flex-direction: column;
	height: 100%;
	background: #111
}
.header__inner .header__logo {
	width: 100%;
	background: 0
}
.sc-classic .show.peace-logo .header__logo {
	background: 0
}
.header__inner .header__logo .header__logoLink {
	width: 100%;
	margin-left: 0;
	background: no-repeat center/73px url(https://web.archive.org/web/20110615060435im_/http://a1.sndcdn.com/images/soundcloud-logo-sc.png?taylor62)
}
.header__inner .header__right {
	position: absolute;
	left: 10px;
	height: 90px;
	background: linear-gradient(30deg, #401702 -200%, transparent);
	border-radius: 5px;
	box-shadow: 2px 2px 2px #0d0d0d
}
.header__inner .header__right div.header__userNav {
	position: absolute;
	bottom: 0;
	width: 100%
}
.creatorsNavigation {
	margin: 15px
}
.header__soundInput .left {
	position: absolute;
	top: 40px;
	left: 110px;
	z-index: 1
}
.header__inner .header__right div.header__userNav .header__userNavUsernameButton .header__userNavUsername {
	display: block
}
.header__inner .header__right ul.header__navMenu {
	position: absolute;
	right: 0;
	bottom: 0
}
.header__navWrapper {
	width: 90%
}
.header__navWrapper .header__navMenu {
	display: flex;
	flex-direction: column;
	width: 100%
}
.header__navMenu > li > a.header__moreButton.selected,
.header__userNavButton.selected,
.header__userNavItem.selected {
	background: 0 0
}
.header__navWrapper .header__navMenu li a {
	box-sizing: border-box;
	width: 100%;
	height: 40px;
	margin: 10px;
	padding: 0 34px;
	color: #b3b3b3;
	font-weight: 100;
	font-size: 16px;
	line-height: 40px;
	letter-spacing: 1px;
	text-align: left;
	background: 0 0;
	border: 0
}
.header__navWrapper .header__navMenu li a.selected {
	background: linear-gradient(90deg, #401702 -400%, transparent);
	border-left: 4px solid #f50
}
.header__navWrapper .header__navMenu li a:hover {
	color: #fff
}
.sc-classic #app .l-container {
	width: 100%;
	margin: 0;
	padding: 0
}
.sc-classic #app .l-container.l-content {
	position: relative;
	left: 230px;
	width: calc(100% - 230px)
}
.sc-classic #app .l-container.l-content .l-fluid-fixed {
	min-height: 130vh;
	padding: 0 15px
}
.sc-classic #app .l-container.l-content .l-fluid-fixed .l-main {
	margin-right: 330px
}
.sc-classic #app .l-container.l-content .l-fluid-fixed .l-sidebar-right {
	right: 15px;
	transition: right .2s, background .2s
}
.header__inner .header__right div.header__userNav .header__userNavUsernameButton,
.header__navWrapper .header__navMenu li {
	width: 100%
}
@media (max-width: 1000px) {
	.sc-classic #app .l-container.l-content .l-fluid-fixed .l-main {
		margin-right: 50px
	}
	.sc-classic #app .l-container.l-content .l-fluid-fixed .l-sidebar-right {
		right: -260px!important;
		z-index: 9;
		padding: 0 15px;
		background: #181818
	}
	.sc-classic #app .l-container.l-content .l-fluid-fixed .l-sidebar-right:hover {
		right: 0!important
	}
}
.sc-classic #app .l-container #content {
	position: relative;
	box-sizing: border-box;
	padding-bottom: 90px;
	overflow: hidden
}
.sc-classic #app .l-container #content .l-about-rows,
.sc-classic #app .l-container #content .listenEngagement {
	margin-left: 15px;
	border: 0
}
.sc-classic #app .l-container #content .truncatedAudioInfo.m-collapsed.m-overflow .truncatedAudioInfo__wrapper {
	-webkit-mask-image: linear-gradient(to top, transparent 0, #000 40px)
}
.sc-classic #app .l-container #content .sc-button.sc-button-small:not(.sc-button-follow).sc-button-selected:before {
	filter: hue-rotate(360deg)
}
.sc-classic #app .l-container #content .sc-button.sc-button-small:not(.sc-button-follow).sc-button-repost:not(.sc-button-selected):before {
	background-image: url(data:image/svg+xml;base64,)
}
.sc-classic #app .l-container #content .truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after {
	background: linear-gradient(rgba(255, 255, 255, 0), #111)
}
.sc-classic #app .l-container #content .dropbar {
	width: calc(100% - 230px);
	transform: translate3d(15px, -120px, 0)
}
.sc-classic #app .l-container #content .dropbar .dropbar__content {
	box-sizing: border-box;
	width: 100%;
	background: linear-gradient(#000, 80%, transparent)
}
.sc-classic #app .l-container #content .statsOverview .dropbar .dropbar__content {
	height: 50px
}
.sc-classic #app .l-container #content .statsOverview .dropbar .dropbar__content .statsDropbarTabs__tab {
	padding: 10px 0
}
.sc-classic #app .l-container #content .dropbar .dropbar__content .userDropbar {
	padding: 14px 0
}
.sc-classic #app .l-container #content .dropbar.m-active {
	transform: translate3d(15px, -46px, 0)
}
.sc-classic #app .l-container #content .statsOverview .dropbar.m-active {
	transform: translate3d(-15px, -46px, 0)
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-edit:hover,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-message:hover,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-more:hover,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-share:hover {
	transform: scale(1.06)
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow:hover {
	box-shadow: inset 0 0 0 2px #ff3d00;
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow:not(.sc-button-selected):hover {
	background: #ff3d00
}
.sc-classic #app .l-container #content .l-user-hero {
	z-index: 9998;
	min-height: 405px
}
.sc-classic #app .l-container #content .l-user-hero:after {
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	z-index: -1;
	background: #111;
	content: ""
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader {
	min-height: 405px;
	margin: 0
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderInfo,
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeader__info {
	height: 100%
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderInfo__avatar.sc-media-image {
	position: absolute;
	top: 120px;
	left: 25px;
	filter: drop-shadow(5px 5px 3px #090909)
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderInfo__content {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	height: 100%
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderInfo__content h3 {
	color: #ccc;
	font-size: 18px;
	background: #111;
	margin-top: -13px
}
.profileHeaderInfo__userName.g-type-shrinkwrap-block.g-type-shrinkwrap-large-primary {
	margin-top: -100px;
	color: #fff;
	font-weight: 900;
	font-size: 72px;
	line-height: 82px;
	letter-spacing: -.005em;
	background: 0;
	margin-left: 18px
}
.sc-classic .g-tabs {
	border-bottom: none
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderInfo__content h4 {
	margin-top: -10px
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderBackground {
	min-height: 405px;
	background: #111
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderBackground .profileHeaderBackground__visual {
	position: relative;
	min-height: 405px;
	background-position: center;
	background-size: cover
}
.sc-classic #app .l-container #content .l-user-hero .profileHeader .profileHeaderBackground .profileHeaderBackground__visual:before {
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	background: linear-gradient(transparent -70%, #111);
	content: ""
}
.sc-classic #app .l-container #content .commentForm__wrapper {
	background: #0d0d0d;
	border: 0
}
.sc-classic #app .l-container #content .commentForm__wrapper input {
	color: #fff;
	background: #0d0d0d;
	border: 0
}
.sc-classic #app .l-container #content .l-vertical-bar {
	position: absolute;
	top: 325px;
	z-index: 9;
	width: 100%
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons {
	right: 25%;
	bottom: 95px;
	left: 25%;
	display: flex;
	justify-content: center;
	width: 50%;
	height: 40px
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-edit,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-message,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-more,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-share {
	width: 39px;
	height: 39px;
	overflow: hidden;
	text-indent: 100px;
	background: 0 0;
	border: 0;
	cursor: default;
	filter: brightness(10)
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-startstation {
	min-width: 130px;
	height: auto;
	margin: 0 15px 0 0;
	padding: 11px 44px;
	font-weight: 400;
	font-size: 11px;
	line-height: 1.6;
	letter-spacing: .16em;
	white-space: normal;
	text-align: center;
	text-transform: uppercase;
	vertical-align: middle;
	border: 0;
	cursor: default;
	will-change: transform
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-edit:before,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-message:before,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-more:before,
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-share:before {
	background-size: contain
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow {
	color: #fff;
	background: rgba(24, 24, 24, .7);
	border-radius: 500px;
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow:not(.sc-button-selected) {
	background: #f50;
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-follow.sc-button-selected {
	color: #f50
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-startstation {
	color: #fff;
	background: #f50;
	border-radius: 500px;
	transition-duration: .2s
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-startstation:hover {
	background: #ff3d00
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .userInfoBar__buttons .sc-button-startstation:before {
	left: 40px;
	background-position: center 3px;
	background-size: 16px 12px;
	filter: brightness(10)
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .profileTabs.g-tabs.g-tabs-medium {
	display: flex;
	justify-content: center;
	border: 0
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .profileTabs.g-tabs.g-tabs-medium .g-tabs-link {
	position: relative;
	display: inline-block;
	margin: 10px;
	padding: 5px 10px;
	color: #fff;
	font-weight: var(--glue-font-weight-bold);
	font-size: 12px;
	line-height: 16px;
	letter-spacing: .16em;
	text-transform: uppercase;
	text-decoration: none;
	border: 0;
	opacity: .6;
	-webkit-transition: opacity .2s linear;
	transition: opacity .2s linear
}
.sc-classic #app .l-container #content .l-vertical-bar .userInfoBar .profileTabs.g-tabs.g-tabs-medium .g-tabs-link.active:before {
	position: absolute;
	bottom: 0;
	left: 50%;
	display: block;
	width: 30px;
	height: 2px;
	margin-left: -15px;
	background: #f50;
	content: ""
}
.sc-classic #app .l-container #content .sound__soundActions {
	overflow: unset;
	background: 0 0
}
.sc-classic #app .l-container #content .l-inner-fullwidth {
