css:
input, textarea {
   background-color:#333333;
   border: 1px solid #F0FFF0;
   color:#F0FFF0;
}
select {
   background-color:#333333;
   border: 1px solid #F0FFF0;
   color:#F0FFF0;
}
body {
   color:#F0FFF0;
   background-color:#660000;
   background-image:url('https://i.imgur.com/ybX0DLb.jpg');
   background-repeat:no-repeat;
   background-origin:content;
}
a {
   color:#F0FFF0;
}
a:hover {
   color:#F0FFF0;
   background-color:#333333;
}
#header {
   background-color:#000000;
   border:solid #F0FFF0 1px;
   background-image:url('https://i.imgur.com/6vmZToB.png');
   background-position:center;
   background-repeat:no-repeat;
   background-origin:content;
   border-color: #9ecaed;
   box-shadow: 0 0 25px #9ecaed;
   border-radius: 15px 15px 15px 15px;
}
#main {
   border-left:solid #F0FFF0 1px;
   border-right:solid #F0FFF0 1px;
   background-image:url('https://i.imgur.com/OS1i9Rk.png');
   background-position:center;
   background-repeat:no-repeat;
   background-origin:content;
   border-color: #9ecaed;
   box-shadow: 0 0 45px #7375f0;
   border-radius: 0px 0px 0px 0px;
   font-family: helvetica; color: silver;
   font-size: 14px;
}
#side1 {
   border-left:solid #F0FFF0 2px;
   background-image:url('https://i.imgur.com/REiNQGd.png');
   background-position:center;
   background-repeat:no-repeat;
   background-origin:content-box;
   border-color: #9ecaed;
   box-shadow: 0 0 25px #9ecaed;
   font-family: helvetica;
   color: silver;
   border-radius: 15px 0px 0px 15px;
}
#side2 {
   border-right:solid #F0FFF0 2px;
   background-image:url('https://i.imgur.com/ynF2sB8.png');
   background-position:center;
   background-repeat:no-repeat;
   background-origin:content;
   border-color: #9ecaed;
   box-shadow: 0 0 35px #7375f0;
   font-family: helvetica;
   color: yellow;
   border-radius: 0px 15px 15px 0px;
}
#equipment {
   font-size:12px;
   color: #ffd369;
}
#equipment:h3 {
   margin-top: 0.5em;
   margin-bottom: 0;
   color:ffd369;
}
/* Can NOT FOR THE LIFE OF ME figure out a way for text to be more visible, but the pic is vital to the whole operation*/
 #equipstuff {
   font-family:"Times New Roman", Times, serif;
   text-align: left;
   font-size: 14px;
   font-style: underline;
   color:#ffd369;
}
#chat {
   background-color:#000000;
   border:solid #F0FFF0 1px;
   background-image:url('https://i.imgur.com/sJFIBxP.jpg');
   background-position:center;
   background-repeat:no-repeat;
   background-size:100%;
   background-origin:content;
   border-color: #dcddf5;
   box-shadow: 0 0 85px #dcddf5;
   border-radius: 15px 15px 15px 15px;
}
#chatwindow {
   background-color:#666666;
   color:#F0FFF0;
   border: 1px solid #F0FFF0;
   background-image:url('https://i.imgur.com/Cy0ufLc.png');
   background-repeat:no-repeat;
   background-size:100%;
   background-origin:content;
   border-color: #9ecaed;
   box-shadow: 0 0 25px #9ecaed;
   border-radius: 15px 15px 15px 15px;
}
#inputchat {
   background-color:#F0FFF0;
   color:#F0FFF0;
   background-image:url('https://i.imgur.com/KHIjOYk.png');
   background-repeat:no-repeat;
   background-origin:content;
}
#chatbutton {
   background-color:#F0FFF0;
   color:#F0FFF0;
   background-image:url('https://lyrania.co.uk/images/TinaGreen/main.jpg');
   background-repeat:no-repeat;
   background-size:100%;
   background-origin:content;
   border-radius: 15px 15px 15px 15px;
}
#bannerholder {
   border: 1px solid #33bbee;
   background-color:#33bbee;
   color:#33bbee;
   background-image:url('https://lyrania.co.uk/images/Aqua/bg.jpg');
   background-size:100%;
   background-origin:content;
   ;
}
#popupholder {
   border: 1px solid #33bbee;
   background-color:#33bbee;
   color:#33bbee;
   background-image:url('https://lyrania.co.uk/images/Aqua/bg.jpg');
   background-size:100%;
   background-origin:content;
}
.chatname {
   color:#b4b4bd;
}
.chatline {
   color:#FFF;
}
.chatlineTrade {
   color:#FFF
}
.admincolour {
   color:#FFFF33;
}
.panel {
   border-color: #34bbee;
}
.panel > .panel-heading {
   background-color: #34bbee;
   color: #fff;
/* i really want this to be global */
}
