css:
@-moz-document domain("members.superloop.com") {
.footer, .section, body {
    background-color: #4a4a4a;
}
.nav {
    background-color: #4a4a4a;
}
body {
    color: #fff;
}
.subtitle {
    color: #f5f4f5;
}
.box {
    /* background-color: #fff; */
    background-color: #4a4a4a;
}
.box {
    /* box-shadow: 0 2px 4px rgba(0,0,0,.1); */
    box-shadow: 0 2px 4px rgba(255, 255, 255, .1);
}
.message.is-success {
    background-color: #4a4a4a;
}
.message.is-success .message-body {
    color: #f5f5f5;
}
.message.is-info {
    background-color: #4a4a4a;
}
.message-body {
    color: #ffffff;
}
.table tr:hover {
    background-color:#4a4a4a;
    color: #f5f5f5;
}
.table tr {
    background-color:#4a4a4a;
    color: #f5f5f5;
}
.table.is-striped tbody tr:nth-child(2n) {
    background-color:#4a4a4a;
}
.table.is-striped tbody tr:nth-child(2n):hover, .tag {
    background-color:#4a4a4a;
}
.table {
    color: #f5f5f5;
}
.table th {
    color: #f5f5f5;
}
.title {
    color:#f5f5f5;
}
.nav-item a, a.nav-item {
    color:#9a9a9a;
}
.nav-item a, a.nav-item:hover {
    color:#9a9a9a;
}
.table thead td, .table thead th {
    border-width:0 0 2px;
    color:#9a9a9a;
}
.table tfoot td, .table tfoot th {
    border-width:2px 0 0;
    color:#9a9a9a;
}
.message-body {
    color:#f5f5f5;
}
.hero.is-superloop {
    background-color:#5a5a5a;
}
.label {
    color:#f5f5f5;
}
code {
    background-color:#3a3a3a;
}
strong {
    color:#f5f5f5;
}
.message.is-info .message-body {
    border-color:#3273dc;
    color:#f5f5f5;
}
}
