css:
@-moz-document domain(www.addic7ed.com) {

img[src*="logonew.png"] {
    filter: invert(60%);
}
html,body,table,#header
{
    background-color: #222;
}
.tabel, .tabel70, .tabel90, .tabel95 {
    background-color: #888;
}
.tabel td {
    background-color: #888;
}
.tabel td.tablecorner {
    background-color: #222;
}
.tablecorner {
    background-color: #222;
    padding: 0!important;
}
tr[bgcolor="#009BCA"],
td[bgcolor="#009BCA"],
th[bgcolor="#009BCA"]{
    background-color: #222;
}
.dropMenu {
    background-color: #222;
    border: 1px solid #222;
}
.topItem {
    color: darkred;
}

img[src*="bl.gif"] {
    background-color: #888;
    display: block;
    border-radius: 0 0 0 20px;
    width: 20px;
    box-sizing: border-box;
    padding-left: 20px;
    height: 20px;
}
img[src*="br.gif"] {
    background-color: #888;
    display: block;
    border-radius: 0 0 20px 0;
    width: 20px;
    box-sizing: border-box;
    padding-left: 20px;
    height: 20px;
}
img[src*="tl.gif"] {
    background-color: #888;
    display: block;
    border-radius: 20px 0 0 0;
    width: 20px;
    box-sizing: border-box;
    padding-left: 20px;
    height: 20px;
}
img[src*="tr.gif"] {
    background-color: #888;
    display: block;
    border-radius: 0 20px 0 0;
    width: 20px;
    box-sizing: border-box;
    padding-left: 20px;
    height: 20px;
}
img[src*="linea.jpg"] {
    width: 20px;
    height: 18px;
    box-sizing: border-box;
    padding-left: 20px;
}

font[color="red"],
.newsDate,
.newsDate b{
    color:darkred
}
font[color="#009966"]{
    color:darkgreen
}
font[color="#c5e9f6"]{
    color:#888
}
font[color="blue"]{
    color:#222
}
font[color="#3366FF"]{
    color:#170d99;
    font-weight: bold;
}
.newsDate img{
    vertical-align: middle;
    margin-right: 6px;
}

a{
    color: black;
    font-weight: 800;
    font-family: "Open Sans";
    font-size: 16px;
    font-variant: titling-caps
}

a.gris,a.gris:link,a.gris:visited{
    font-size: 11px;
    color: #222;
}
#header a {
    color: #ccc;
}
#header {
    color: #ccc;
}
#header hr{
    border-color: transparent;
}
#containernews td{
    vertical-align: middle;
}
#containernews a{
    font-size: 12px
}
#footermenu a{
    color: #888!important;
    font-size: 11px;
    font-weight: normal;
}

#form1,
#qsSeason a,
b
{
    color: #ccc;
}

#qsSeason {
    height: 40px;
    display: inline-block;
}

.language {
    background-color: #222;
    padding: 5px 15px;
}
.language img{
    vertical-align: middle;
    margin: 0 5px;
}
.language + td{
    padding-left: 10px;
    color: black!important
}
.language + td b{
    color: black!important
}
.NewsTitle {
    background-color: #222;
    padding: 5px 5px;
}
.NewsTitle img{
    vertical-align: middle;
    margin: 0 5px;
}
.NewsTitle + td{
    padding-left: 10px;
}
.NewsTitle + td img{
    vertical-align: middle;
    margin-right: 8px;
}
.titulo{
    color: black;
    font-size: 20px;
}
a.twitter-share-button,
a.twitter-share-button + a{
    font-size: 12px;
}

img[src*="hi.jpg"] {
    background: url("https://www.addic7ed.com/images/hi.PNG") no-repeat;
    background-size: 20px;
    background-position: 0 2px;
    display: inline-block;
    width: 24px;
    height: 24px;
    padding-left: 24px;
    box-sizing: border-box;
    margin-right: 5px;
}

#season table tbody tr.epeven:nth-child(odd) {
    background-color: #888;
}
#season table tbody tr.epeven:nth-child(even) {
    background-color: #aaa;
}
#langs table tbody tr:nth-child(odd) {
    background-color: #aaa;
}
#langs table tbody tr:nth-child(even) {
    background-color: #888;
}
#containernews {
    height: auto;
}

#comments * {
    font-size: 13px;
}
#comments .newsDate{
    font-size: 9px;
    padding-top: 8px;
}
#comments tr > td[width]:first-child{
    padding-top: 8px;
}
#newcomment {
    vertical-align: middle;
    background-color: #222;
    color: #ccc;
    border: 1px solid #444;
    padding: 4px 6px;
    margin: 12px 0;
    min-height: 100px
}

table[width="94%"]{
    max-width: 1140px;
}
th[height="58"]{
    height: auto;
}
th[width="14%"][height="37"]{
    width: 15%;
}
td[width="83%"][height="37"]{
    width: 85%;
}
table[width="94%"] > tbody > tr:nth-child(3) > td {
    width: 100%;
    max-width: 955px;
    position: absolute;
}
table[width="94%"] > tbody > tr:nth-child(3) > td #container {
    position: relative;
    width: 100%;
}
table[width="94%"] > tbody > tr:nth-child(3) > td .tabel70{
    width: 100%;
}

.coolBoton {
	box-shadow: 0px 1px 0px 0px #ffffff;
	background:linear-gradient(to bottom, #ededed 5%, #dfdfdf 100%);
	background-color:#ededed;
	border-radius:6px;
	border:1px solid #dcdcdc;
	display:inline-block;
	cursor:pointer;
	color:#333;
	font-family:Arial;
	font-size:15px;
	font-weight:bold;
	padding:6px 24px;
	text-decoration:none;
	text-shadow:0px -1px 0px #ffffff;
    margin: 15px ;
}
.coolBoton:hover {
	background:linear-gradient(to bottom, #dfdfdf 5%, #ededed 100%);
	background-color:#dfdfdf;
}
.coolBoton:active {
	position:relative;
	top:1px;
}
#trseqtop td:nth-child(1),
#trseqtop td:nth-child(2),
#trseqtop td:nth-child(3){
    width: 50px;
}
#trseqtop td:nth-child(4){
    width: 150px;
}
#trseqtop td:nth-child(5){
    width: 210px;
}
#trseqtop td:nth-child(6){
    width: 250px;
}
#trseqtop td:nth-last-child(1){
    width: auto;
}
#lista img{
    vertical-align: middle;
}
#lista td img{
    padding: 8px 0;
}
#lista .cursorEdit textarea{
    vertical-align: middle;
    background-color: #222;
    color: #ccc;
    border: 1px solid #444;
    padding: 0 4px;
    margin-left: -6px;
        
}
#lista .cursorEdit input[type="text"]{
    vertical-align: middle;
    background-color: #222;
    color: #ccc;
    border: none;
    width: 88px;
    text-align: center;
}
#lista .cursorEdit input[type="text"] ~ .coolBoton{
    font-size: 11px;
    padding:0px 10px;
}
#lista .cursorEdit .coolBoton{
    margin: 0 15px;
    vertical-align: middle;
    padding: 2px 12px;
}
#lista tr{
    min-height: 30px!important;
}
#lista td{
    vertical-align: middle;
    padding: 0;
}
#lista td a{
    font-size: 12px;
}

#lista .originalText td{
    background-color: #2a662e !important;
    color: #ddd;
}
#lista .quotedText td{
    background-color: #888 !important;
    color: #222;
    padding: 0 6px;
}
#lista .quotedText td img{
    padding: 0;
}
#lista .quotedText td a{
    font-weight: normal;
    text-decoration: none;
}
#lista .lockedText td{
    background-color:#8c0101 !important;
    color: #ccc;
}
#lista .cursorEdit{
    font-weight: 100!important;
    padding: 0 6px;
}
#lista .cursorEdit[style="font-weight: 900;"]{
    text-decoration: underline;
}
#lista a[href="noscript.php"]{
    box-shadow: 0px 1px 0px 0px #ffffff;
	background:linear-gradient(to bottom, #ededed 5%, #dfdfdf 100%);
	background-color:#ededed;
	border-radius:4px;
	border:1px solid #dcdcdc;
	display:inline-block;
	cursor:pointer;
	color:#333;
	font-family:Arial;
	font-size:12px;
	font-weight:bold;
	padding:2px 3px;
	text-decoration:none;
    vertical-align: middle;
}
#lista a[href="noscript.php"]:hover {
	background:linear-gradient(to bottom, #dfdfdf 5%, #ededed 100%);
	background-color:#dfdfdf;
}
  
}
